src/components/sections/Newsletter.tsx.content:
"use client";

import { useState } from "react";
import styles from "./Newsletter.module.css";
import { newsletterService } from "@/services/newsletter.service";
import type { ApiError } from "@/lib/api";

const preferenceOptions = [
  "Hair Styling Updates",
  "Makeup Tips & Trends",
  "Special Offers",
  "Events & Workshops",
  "New Services",
];

export default function Newsletter() {
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState("");
  const [isSuccess, setIsSuccess] = useState(false);

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setIsSubmitting(true);
    setMessage("");

    try {
      await newsletterService.subscribe({
        email,
        name,
      });

      setIsSuccess(true);
      setMessage("Successfully subscribed to our newsletter!");

      // Reset form
      setEmail("");
      setName("");
    } catch (error) {
      const apiError = error as ApiError;
      setIsSuccess(false);
      setMessage(apiError.message || "Failed to subscribe. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <section className={styles.newsletter}>
      <div className={styles.container}>
        <div className={styles.content}>
          <div className={styles.textContent}>
            <h2 className={styles.title}>Stay in the Loop</h2>
            <p className={styles.description}>
              Get the latest beauty tips, course updates, special offers, and
              exclusive content delivered straight to your inbox.
            </p>
            <ul className={styles.benefits}>
              <li>ðŸ’„ Weekly beauty tips and tutorials</li>
              <li>ðŸ“š Early access to new courses</li>
              <li>ðŸŽ¯ Exclusive member discounts</li>
              <li>âœ¨ Industry news and trends</li>
            </ul>
          </div>

          <div className={styles.formContainer}>
            <form onSubmit={handleSubmit} className={styles.form}>
              <div className={styles.inputGroup}>
                <input
                  type="text"
                  placeholder="Your name (optional)"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  className={styles.input}
                />
                <input
                  type="email"
                  placeholder="Enter your email address"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  className={styles.input}
                />
              </div>

              {message && (
                <div
                  className={`${styles.message} ${
                    isSuccess ? styles.success : styles.error
                  }`}
                >
                  {message}
                </div>
              )}

              <button
                type="submit"
                disabled={isSubmitting}
                className={styles.submitButton}
              >
                {isSubmitting ? "Subscribing..." : "Subscribe Now"}
              </button>

              <p className={styles.privacy}>
                We respect your privacy. Unsubscribe at any time.
              </p>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}

src/app/admin/settings/Settings.module.c.content:
.settingsPage {
  padding: 24px;
  max-width: 1200px;
  margin: 0 auto;
}

.header {
  margin-bottom: 32px;
}

.title {
  font-size: 2rem;
  font-weight: 700;
  color: #ffffff;
  margin-bottom: 8px;
}

.subtitle {
  color: rgba(255, 255, 255, 0.7);
  font-size: 1.1rem;
}

.message {
  padding: 16px 20px;
  border-radius: 12px;
  margin-bottom: 24px;
  font-weight: 500;
}

.message.success {
  background: rgba(34, 197, 94, 0.1);
  border: 1px solid rgba(34, 197, 94, 0.3);
  color: #22c55e;
}

.message.error {
  background: rgba(239, 68, 68, 0.1);
  border: 1px solid rgba(239, 68, 68, 0.3);
  color: #ef4444;
}

.settingsGrid {
  display: grid;
  gap: 24px;
  grid-template-columns: 1fr;
}

.settingCard {
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 16px;
  padding: 0;
  overflow: hidden;
}

.cardHeader {
  padding: 24px 24px 16px 24px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.cardTitle {
  font-size: 1.3rem;
  font-weight: 600;
  color: #ffffff;
  margin-bottom: 8px;
}

.cardDescription {
  color: rgba(255, 255, 255, 0.6);
  font-size: 0.95rem;
}

.cardContent {
  padding: 24px;
}

.toggleContainer {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.toggleLabel {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-weight: 500;
  color: #ffffff;
  cursor: pointer;
}

.toggle {
  position: relative;
  display: inline-block;
  width: 60px;
  height: 34px;
}

.toggle input {
  opacity: 0;
  width: 0;
  height: 0;
}

.toggleSlider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(255, 255, 255, 0.2);
  transition: 0.4s;
  border-radius: 34px;
}

.toggleSlider:before {
  position: absolute;
  content: "";
  height: 26px;
  width: 26px;
  left: 4px;
  bottom: 4px;
  background-color: white;
  transition: 0.4s;
  border-radius: 50%;
}

.toggle input:checked + .toggleSlider {
  background: linear-gradient(135deg, #D0B49F 0%, #B8A082 100%);
}

.toggle input:checked + .toggleSlider:before {
  transform: translateX(26px);
}

.toggleDescription {
  color: rgba(255, 255, 255, 0.6);
  font-size: 0.9rem;
  line-height: 1.4;
}

.formGroup {
  margin-bottom: 20px;
}

.formGroup label {
  display: block;
  margin-bottom: 8px;
  font-weight: 500;
  color: #ffffff;
}

.formGroup .input {
  width: 100%;
  padding: 12px 16px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 8px;
  background: rgba(255, 255, 255, 0.05);
  color: #ffffff;
  font-size: 1rem;
  transition: all 0.3s ease;
}

.formGroup .input:focus {
  outline: none;
  border-color: #D0B49F;
  background: rgba(255, 255, 255, 0.08);
  box-shadow: 0 0 0 3px rgba(208, 180, 159, 0.2);
}

.formGroup .input::placeholder {
  color: rgba(255, 255, 255, 0.4);
}

.loading,
.error {
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 400px;
  font-size: 1.1rem;
  color: rgba(255, 255, 255, 0.7);
}

.error {
  color: #ef4444;
}

@media (max-width: 768px) {
  .settingsPage {
    padding: 16px;
  }

  .title {
    font-size: 1.6rem;
  }

  .subtitle {
    font-size: 1rem;
  }

  .cardHeader,
  .cardContent {
    padding: 20px;
  }

  .toggleLabel {
    flex-direction: column;
    align-items: flex-start;
    gap: 12px;
  }
}

