src/app/cookie-policy/CookiePolicy.modul.content:
.cookiePage {
  padding: 120px 0 80px 0;
  min-height: 100vh;
  background: linear-gradient(135deg, #0f0f0f 0%, #1a1a1a 50%, #0f0f0f 100%);
}

.header {
  text-align: center;
  margin-bottom: 60px;
  padding-bottom: 40px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.title {
  font-size: clamp(2.5rem, 5vw, 3.5rem);
  font-weight: 800;
  color: #ffffff;
  margin-bottom: 16px;
  background: linear-gradient(135deg, #ffffff 0%, #D0B49F 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.lastUpdated {
  color: rgba(255, 255, 255, 0.6);
  font-size: 1rem;
  font-weight: 500;
}

.content {
  max-width: 900px;
  margin: 0 auto;
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  padding: 40px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
}

.section {
  margin-bottom: 40px;
}

.section:last-child {
  margin-bottom: 0;
}

.section h2 {
  font-size: 1.5rem;
  font-weight: 700;
  color: #D0B49F;
  margin-bottom: 16px;
  padding-bottom: 8px;
  border-bottom: 2px solid rgba(208, 180, 159, 0.3);
}

.section h3 {
  font-size: 1.2rem;
  font-weight: 600;
  color: #ffffff;
  margin: 24px 0 12px 0;
}

.section p {
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.7;
  margin-bottom: 16px;
  font-size: 1rem;
}

.section ul {
  margin: 16px 0;
  padding-left: 24px;
}

.section li {
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
  margin-bottom: 8px;
  font-size: 1rem;
}

.section li::marker {
  color: #D0B49F;
}

.link {
  color: #D0B49F;
  text-decoration: none;
  font-weight: 500;
  transition: all 0.3s ease;
  border-bottom: 1px solid transparent;
}

.link:hover {
  color: #E6D3B7;
  border-bottom-color: #E6D3B7;
}

.cookieType {
  margin: 32px 0;
  padding: 24px;
  background: rgba(255, 255, 255, 0.03);
  border: 1px solid rgba(255, 255, 255, 0.08);
  border-radius: 12px;
}

.cookieType h3 {
  color: #D0B49F;
  margin-top: 0;
  margin-bottom: 16px;
  font-size: 1.1rem;
}

.cookieTable {
  margin-top: 20px;
  border-radius: 8px;
  overflow: hidden;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.tableHeader {
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  background: rgba(208, 180, 159, 0.2);
  padding: 12px 16px;
  font-weight: 600;
  color: #ffffff;
  font-size: 0.9rem;
}

.tableRow {
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  padding: 12px 16px;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
  color: rgba(255, 255, 255, 0.8);
  font-size: 0.85rem;
}

.tableRow:nth-child(even) {
  background: rgba(255, 255, 255, 0.02);
}

.tableRow span:first-child {
  font-family: 'Courier New', monospace;
  color: #D0B49F;
  font-weight: 500;
}

.contactInfo {
  background: rgba(208, 180, 159, 0.1);
  border: 1px solid rgba(208, 180, 159, 0.2);
  border-radius: 12px;
  padding: 24px;
  margin-top: 20px;
}

.contactInfo p {
  margin-bottom: 8px;
  color: rgba(255, 255, 255, 0.9);
}

.contactInfo p:last-child {
  margin-bottom: 0;
}

/* Mobile Responsiveness */
@media (max-width: 768px) {
  .cookiePage {
    padding: 100px 0 60px 0;
  }

  .content {
    margin: 0 20px;
    padding: 30px 24px;
    border-radius: 16px;
  }

  .section h2 {
    font-size: 1.3rem;
  }

  .section h3 {
    font-size: 1.1rem;
  }

  .section p,
  .section li {
    font-size: 0.95rem;
  }

  .cookieType {
    padding: 20px;
  }

  .tableHeader,
  .tableRow {
    grid-template-columns: 1fr;
    gap: 8px;
    text-align: left;
  }

  .tableHeader {
    display: none; /* Hide header on mobile for better readability */
  }

  .tableRow {
    padding: 16px;
    border-radius: 8px;
    margin-bottom: 8px;
    background: rgba(255, 255, 255, 0.05) !important;
    border: 1px solid rgba(255, 255, 255, 0.1);
    display: block;
  }

  .tableRow span {
    display: block;
    margin-bottom: 4px;
  }

  .tableRow span:first-child::before {
    content: 'Cookie: ';
    color: rgba(255, 255, 255, 0.6);
    font-family: inherit;
    font-weight: normal;
  }

  .tableRow span:nth-child(2)::before {
    content: 'Purpose: ';
    color: rgba(255, 255, 255, 0.6);
  }

  .tableRow span:last-child::before {
    content: 'Duration: ';
    color: rgba(255, 255, 255, 0.6);
  }

  .contactInfo {
    padding: 20px;
  }
}

@media (max-width: 480px) {
  .content {
    margin: 0 16px;
    padding: 24px 20px;
  }

  .section ul {
    padding-left: 20px;
  }

  .cookieType {
    padding: 16px;
  }
}

src/components/ui/LoadingSpinner.module..content:
.loadingContainer {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 16px;
}

.loadingContainer.small {
  gap: 8px;
}

.loadingContainer.medium {
  gap: 12px;
}

.loadingContainer.large {
  gap: 20px;
}

/* Spinner Styles */
.spinner {
  position: relative;
  display: inline-block;
}

.spinner.small {
  width: 24px;
  height: 24px;
}

.spinner.medium {
  width: 40px;
  height: 40px;
}

.spinner.large {
  width: 60px;
  height: 60px;
}

.spinnerRing {
  position: absolute;
  border-radius: 50%;
  border: 2px solid transparent;
  animation: spin 1.5s linear infinite;
}

.spinner.small .spinnerRing {
  width: 24px;
  height: 24px;
  border-width: 2px;
}

.spinner.medium .spinnerRing {
  width: 40px;
  height: 40px;
  border-width: 3px;
}

.spinner.large .spinnerRing {
  width: 60px;
  height: 60px;
  border-width: 4px;
}

/* Color variants */
.spinner.primary .spinnerRing:nth-child(1) {
  border-top-color: #D0B49F;
  animation-delay: 0s;
}

.spinner.primary .spinnerRing:nth-child(2) {
  border-top-color: rgba(208, 180, 159, 0.6);
  animation-delay: -0.5s;
}

.spinner.primary .spinnerRing:nth-child(3) {
  border-top-color: rgba(208, 180, 159, 0.3);
  animation-delay: -1s;
}

.spinner.secondary .spinnerRing:nth-child(1) {
  border-top-color: #ffffff;
  animation-delay: 0s;
}

.spinner.secondary .spinnerRing:nth-child(2) {
  border-top-color: rgba(255, 255, 255, 0.6);
  animation-delay: -0.5s;
}

.spinner.secondary .spinnerRing:nth-child(3) {
  border-top-color: rgba(255, 255, 255, 0.3);
  animation-delay: -1s;
}

.spinner.white .spinnerRing:nth-child(1) {
  border-top-color: #ffffff;
  animation-delay: 0s;
}

.spinner.white .spinnerRing:nth-child(2) {
  border-top-color: rgba(255, 255, 255, 0.7);
  animation-delay: -0.5s;
}

.spinner.white .spinnerRing:nth-child(3) {
  border-top-color: rgba(255, 255, 255, 0.4);
  animation-delay: -1s;
}

/* Progress Bar Styles */
.progressContainer {
  width: 100%;
  max-width: 300px;
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.progressBar {
  width: 100%;
  height: 8px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 4px;
  overflow: hidden;
  position: relative;
}

.progressBar.large {
  height: 12px;
  border-radius: 6px;
}

.progressFill {
  height: 100%;
  background: linear-gradient(90deg, #D0B49F 0%, #E6D3B7 100%);
  border-radius: inherit;
  transition: width 0.3s ease;
  position: relative;
}

.progressFill::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
  animation: shimmer 2s infinite;
}

.progressBar.primary .progressFill {
  background: linear-gradient(90deg, #D0B49F 0%, #E6D3B7 100%);
}

.progressBar.secondary .progressFill {
  background: linear-gradient(90deg, #ffffff 0%, rgba(255, 255, 255, 0.8) 100%);
}

.progressBar.white .progressFill {
  background: #ffffff;
}

.progressText {
  font-size: 0.9rem;
  color: rgba(255, 255, 255, 0.8);
  text-align: center;
  font-weight: 500;
}

/* Loading Text */
.loadingText {
  color: rgba(255, 255, 255, 0.8);
  font-weight: 500;
  text-align: center;
}

.loadingContainer.small .loadingText {
  font-size: 0.8rem;
}

.loadingContainer.medium .loadingText {
  font-size: 0.9rem;
}

.loadingContainer.large .loadingText {
  font-size: 1.1rem;
}

/* Loading Overlay */
.loadingOverlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  backdrop-filter: blur(8px);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10000;
}

.overlayContent {
  background: rgba(20, 20, 20, 0.95);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  padding: 40px;
  text-align: center;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
}

/* Animations */
@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

@keyframes shimmer {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(100%);
  }
}

/* Pulse animation for loading states */
@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

.loadingContainer.pulse {
  animation: pulse 2s infinite;
}

src/header/navLinks/FlowingMenu.css.content:
.menu-wrap {
    width: 30vw;
    height: 100%;
    overflow: hidden;
}

.menu {
    display: flex;
    flex-direction: column;
    height: 100%;
    width: 100% ;
    margin: 0;
    padding: 0;
}

.menu__item {
    flex: 1;
    position: relative;
    overflow: hidden;
    text-align: center;
    width: 100%;
}

.menu__item-link {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
    position: relative;
    cursor: pointer;
    text-transform: uppercase;
    text-decoration: none;
    white-space: nowrap;
    font-weight: 600;
    color: #101010;
    font-size: 4vh;
}

.menu__item-link:hover {
    color: #060010;
}

.menu__item-link:focus:not(:focus-visible) {
    color: #101010;
}

.marquee {
    position: absolute;
    top: 0;
    left: 0;
    overflow: hidden;
    width: 300%;
    height: 100%;
    pointer-events: none;
    background: #101010;
    transform: translate3d(0, 101%, 0);
    transition: transform 0.6s ease-expo;
    
}

.marquee__inner-wrap {
    height: 100%;
    width: 100%;
    display: flex;
    transform: translateX(0);
}

.marquee__inner {
    display: flex;
    align-items: center;
    position: relative;
    height: 100%;
    width: 600%;
    will-change: transform;
    animation: marquee 15s linear infinite;
}

.marquee span {
    color: #f1f1f1;
    white-space: nowrap;
    text-transform: uppercase;
    font-weight: 400;
    font-size: 4vh;
    line-height: 1.2;
    padding: 1vh 1vw 0;
}

.marquee__img {
    width: 200px;
    height: 7vh;
    margin: 2em 2vw;
    padding: 1em 0;
    border-radius: 50px;
    background-size: cover;
    background-position: 50% 50%;
}

.menu__item-link:hover+.marquee {
    transform: translate3d(0, 0%, 0);
}

@keyframes marquee {
    from {
        transform: translateX(0);
    }

    to {
        transform: translateX(-50%);
    }
}

src/header/homePage-header.module.css.content:
/* ==============================================
   CSS CUSTOM PROPERTIES (Variables)
   ============================================== */
:root {
    --color-primary: #ffffff;
    --color-dark: #000000;
    --transition: all 0.2s ease;
    --animation-duration: 0.4s;
    --border-radius: 5px;
    --shadow: 2px 0 8px rgba(0, 0, 0, 0.04);
}

/* ==============================================
   HEADER LAYOUT
   ============================================== */
.headerSpacer {
    width: 100%;
    height: 80px;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 35px 25px;
    color: var(--color-primary);
    position: fixed;
    z-index: 100;
    background: var(--color-dark);
}

.headerContainer {
    width: 75%;
    height: 65px;
    display: flex;
    align-items: center;
    justify-content: space-between;
}



/* Hide header when menu is open */
.headerHidden {
    display: none !important;
}

/* ==============================================
   HEADER ELEMENTS
   ============================================== */
.headerLogo_container img {
    height: 80px;
    width: 80px;
}

.headerLinks {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 50px;
}

/* Button Base Styles */
.headerCTA_button {
    padding: 5px 15px;
    border-radius: var(--border-radius);
    cursor: pointer;
    font-size: 2.2rem;
    font-weight: 500;
    transition: var(--transition);
    color: var(--color-primary);
    background-color: var(--color-dark);
    border: var(--color-primary) 2px solid;

}

.headerCTA_button:hover {
    color: var(--color-dark);
    background-color: var(--color-primary);
    border: var(--color-dark) 2px solid;
}

.headerMenu_button {
    padding: 7px 12px;
    background: none;
    border: none;
    cursor: pointer;
    color: var(--color-dark);
    background-color: var(--color-primary);
    font-size: 2rem;
    transition: var(--transition);
    border-radius: var(--border-radius);
    border: var(--color-dark) 2px solid;
}

.headerMenu_button:hover {
    color: var(--color-primary);
    background-color: var(--color-dark);
    border: var(--color-primary) 2px solid;
}

/* ==============================================
   NAVIGATION POPUP
   ============================================== */
.headerNav_popup {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    display: flex;
    z-index: 100;
    overflow: hidden;
}

.headerNav_popup_left {
    width: 70vw;
    height: 100vh;
    background: var(--color-dark);
    box-shadow: var(--shadow);
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
}

.headerNav_popup_right {
    width: 30vw;
    height: 100vh;
    background: var(--color-primary);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    position: relative;
}

/* ==============================================
   POPUP CONTENT
   ============================================== */
.headerNav_popup_logo_container {
    position: absolute;
    top: 20px;
    left: 40px;
    z-index: 101;
}

.headerNav_popup_logo_container img {
    height: 80px;
    width: 80px;
}

.headerNav_popup_close_container {
    position: absolute;
    top: 30px;
    right: 30px;
    z-index: 101;
}

.headerNav_popup_close_button {
    background: none;
    border: none;
    font-size: 2.5rem;
    color: var(--color-dark);
    cursor: pointer;
    padding: 10px 20px;
    border-radius: 50px;
    font-weight: 600;
    transition: var(--transition);
}

.headerNav_popup_close_button:hover {
    background: var(--color-dark);
    color: var(--color-primary);
}

.headerNav_popup_left_content {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

.headerNav_popup_left_content_top {
    display: flex;
    align-items: center;
    justify-content: center;
}

.headerNav_popup_left_content_top p {
    font-size: 40rem;
    font-weight: 500;
    color: #Fff;
    opacity: 1;
    letter-spacing: -15px;
}

.headerNav_popup_left_content_top_p_firstLetter {
    display: inline-block;
    transform: scaleY(1.5) translateY(6.5%);
    transform-origin: bottom;

}

.headerNav_popup_left_content_top h1 {
    font-size: 15rem;
    font-weight: 200;
    color: #fff;
    letter-spacing: -10px;
    position: absolute;
    top: 425px;
    left: 825px;
}

/* ==============================================
   ANIMATIONS
   ============================================== */
@keyframes slideInLeft {
    from {
        transform: translateX(-100%);
        opacity: 0;
    }

    to {
        transform: translateX(0);
        opacity: 1;
    }
}

@keyframes slideInRight {
    from {
        transform: translateX(100%);
        opacity: 0;
    }

    to {
        transform: translateX(0);
        opacity: 1;
    }
}

@keyframes slideOutLeft {
    from {
        transform: translateX(0);
        opacity: 1;
    }

    to {
        transform: translateX(-100%);
        opacity: 0;
    }
}

@keyframes slideOutRight {
    from {
        transform: translateX(0);
        opacity: 1;
    }

    to {
        transform: translateX(100%);
        opacity: 0;
    }
}

.slideInLeft {
    animation: slideInLeft var(--animation-duration) forwards;
}

.slideOutLeft {
    animation: slideOutLeft var(--animation-duration) forwards;
}

.slideInRight {
    animation: slideInRight var(--animation-duration) forwards;
}

.slideOutRight {
    animation: slideOutRight var(--animation-duration) forwards;
}

/* ==============================================
   RESPONSIVE DESIGN
   ============================================== */
@media (max-width: 1200px) {
    .headerName {
        font-size: 2.5rem;
    }

    .headerCTA_button {
        font-size: 1.8rem;
        padding: 8px 16px;
    }

    .headerMenu_button {
        font-size: 2.5rem;
    }
}

@media (max-width: 2560px) {
    .headerNav_popup_left_content_top p {
        font-size: 28rem;
        margin: 250px 0 -20px 25px;
    }
}

@media (max-width: 900px) {
    .headerContainer {
        width: 95%;
    }

    .headerCTA_button.headerCTA_header {
        display: none !important;
    }

    .headerNav_popup {
        flex-direction: column;
    }

    .headerNav_popup_left {
        display: none !important;
    }

    .headerNav_popup_right {
        width: 100vw;
        height: 100vh;
    }

    .headerNav_popup_close_container {
        top: 24px;
        right: 24px;
    }

    .headerNav_popup_close_button {
        font-size: 2rem;
        padding: 8px 16px;
        border-radius: 30px;
    }
}

@media (max-width: 768px) {
    .headerContainer {
        width: 100%;
    }
}

@media (max-width: 600px) {
    .headerNav_popup_close_button {
        font-size: 1.5rem;
        padding: 6px 12px;
        border-radius: 20px;
    }
}

src/styles/globals.css.content:
/* Import Google Fonts */
@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:ital,wght@0,400;0,500;0,600;0,700;0,800;0,900;1,400;1,500;1,600;1,700;1,800;1,900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@100;200;300;400;500;600;700;800;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Cormorant+Garamond:ital,wght@0,300;0,400;0,500;0,600;0,700;1,300;1,400;1,500;1,600;1,700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Montserrat:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap');

/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    font-size: 16px;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
    background-color: #000000;
    color: #ffffff;
    line-height: 1.6;
    overflow-x: hidden;
    opacity: 1;
    visibility: visible;
}

.page-content {
    opacity: 1;
    visibility: visible;
    min-height: 100vh;
}

/* CSS Variables for Design System */
:root {
    /* Black & White Color Palette - Bright and Clean */
    --color-dark-charcoal: #000000;
    /* Pure black background */
    --color-warm-taupe: #D0B49F;
    /* Warm taupe accent (project spec) */
    --color-soft-beige: #E4D4C8;
    /* Soft beige accent (project spec) */
    --color-light-gray: #ffffff;
    /* Pure white for maximum contrast */

    /* Base Colors - Black & White theme */
    --color-primary-bg: #000000;
    --color-secondary-bg: #111111;
    --color-tertiary-bg: #1a1a1a;
    --color-quaternary-bg: #2a2a2a;
    --color-card-bg: #000000;
    --color-card-border: #333333;

    /* Header specific variables - Black & White */
    --color-primary: #ffffff;
    --color-dark: #000000;
    --transition: all 0.2s ease;
    --animation-duration: 0.4s;
    --border-radius: 5px;
    --shadow: 2px 0 8px rgba(255, 255, 255, 0.1);

    /* Accent Colors - Warm taupe with white */
    --color-accent-primary: #D0B49F;
    --color-accent-secondary: #E4D4C8;
    --color-accent-tertiary: #ffffff;
    --color-accent-hover: #DBC4A7;

    /* Text Colors - White text on black backgrounds */
    --color-text-primary: #ffffff;
    --color-text-secondary: #e0e0e0;
    --color-text-tertiary: #D0B49F;
    --color-text-muted: #cccccc;
    --color-text-accent: var(--color-accent-primary);

    /* Interactive Elements - White and taupe borders */
    --color-border: #333333;
    --color-border-hover: #555555;
    --color-border-active: var(--color-accent-primary);
    --color-border-accent: #ffffff;

    /* Gradients - Flat colors */
    --gradient-primary: var(--color-primary-bg);
    --gradient-accent: var(--color-accent-primary);

    /* Flat Colors - No shadows */
    --shadow-accent-sm: none;
    --shadow-accent-md: none;
    --shadow-accent-lg: none;
    --shadow-accent-xl: none;

    /* Font Families */
    --font-heading: 'Playfair Display', Georgia, serif;
    /* Elegant serif for headings */
    --font-subheading: 'Cormorant Garamond', Georgia, serif;
    /* Classic serif for subheadings */
    --font-body: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
    /* Clean sans-serif for body */
    --font-accent: 'Montserrat', Helvetica, Arial, sans-serif;
    /* Modern sans-serif for buttons/labels */

    /* Spacing */
    --spacing-xs: 0.25rem;
    --spacing-sm: 0.5rem;
    --spacing-md: 1rem;
    --spacing-lg: 1.5rem;
    --spacing-xl: 2rem;
    --spacing-2xl: 3rem;
    --spacing-3xl: 4rem;

    /* Typography */
    --font-size-xs: 0.75rem;
    --font-size-sm: 0.875rem;
    --font-size-base: 1rem;
    --font-size-lg: 1.125rem;
    --font-size-xl: 1.25rem;
    --font-size-2xl: 1.5rem;
    --font-size-3xl: 1.875rem;
    --font-size-4xl: 2.25rem;
    --font-size-5xl: 3rem;

    /* Enhanced Shadows - None */
    --shadow-sm: none;
    --shadow-md: none;
    --shadow-lg: none;
    --shadow-xl: none;

    /* Enhanced Transitions */
    --transition-fast: 0.15s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-normal: 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-slow: 0.5s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-bounce: 0.5s cubic-bezier(0.68, -0.55, 0.265, 1.55);

    /* Enhanced Border Radius */
    --radius-sm: 0.25rem;
    --radius-md: 0.5rem;
    --radius-lg: 0.75rem;
    --radius-xl: 1rem;
    --radius-2xl: 1.5rem;
    --radius-full: 9999px;

    /* Animation Durations */
    --duration-fast: 200ms;
    --duration-normal: 300ms;
    --duration-slow: 500ms;
    --duration-slower: 800ms;
}

/* Typography Classes */
.font-heading {
    font-family: var(--font-heading);
}

.font-subheading {
    font-family: var(--font-subheading);
}

.font-body {
    font-family: var(--font-body);
}

.font-accent {
    font-family: var(--font-accent);
}

.text-xs {
    font-size: var(--font-size-xs);
}

.text-sm {
    font-size: var(--font-size-sm);
}

.text-base {
    font-size: var(--font-size-base);
}

.text-lg {
    font-size: var(--font-size-lg);
}

.text-xl {
    font-size: var(--font-size-xl);
}

.text-2xl {
    font-size: var(--font-size-2xl);
}

.text-3xl {
    font-size: var(--font-size-3xl);
}

.text-4xl {
    font-size: var(--font-size-4xl);
}

.text-5xl {
    font-size: var(--font-size-5xl);
}

.text-primary {
    color: var(--color-text-primary);
}

.text-secondary {
    color: var(--color-text-secondary);
}

.text-tertiary {
    color: var(--color-text-tertiary);
}

.text-muted {
    color: var(--color-text-muted);
}

.font-light {
    font-weight: 300;
}

.font-normal {
    font-weight: 400;
}

.font-medium {
    font-weight: 500;
}

.font-semibold {
    font-weight: 600;
}

.font-bold {
    font-weight: 700;
}

/* Layout Classes */
.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 var(--spacing-md);
}

.section {
    padding: var(--spacing-3xl) 0;
}

.section-sm {
    padding: var(--spacing-2xl) 0;
}

/* Flexbox Utilities */
.flex {
    display: flex;
}

.flex-col {
    flex-direction: column;
}

.flex-row {
    flex-direction: row;
}

.items-center {
    align-items: center;
}

.items-start {
    align-items: flex-start;
}

.items-end {
    align-items: flex-end;
}

.justify-center {
    justify-content: center;
}

.justify-between {
    justify-content: space-between;
}

.justify-start {
    justify-content: flex-start;
}

.justify-end {
    justify-content: flex-end;
}

.gap-xs {
    gap: var(--spacing-xs);
}

.gap-sm {
    gap: var(--spacing-sm);
}

.gap-md {
    gap: var(--spacing-md);
}

.gap-lg {
    gap: var(--spacing-lg);
}

.gap-xl {
    gap: var(--spacing-xl);
}

/* Grid Utilities */
.grid {
    display: grid;
}

.grid-cols-1 {
    grid-template-columns: repeat(1, 1fr);
}

.grid-cols-2 {
    grid-template-columns: repeat(2, 1fr);
}

.grid-cols-3 {
    grid-template-columns: repeat(3, 1fr);
}

.grid-cols-4 {
    grid-template-columns: repeat(4, 1fr);
}

/* Enhanced Button Styles with Accent Colors */
.btn {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    padding: var(--spacing-sm) var(--spacing-lg);
    font-size: var(--font-size-base);
    font-weight: 500;
    text-decoration: none;
    border-radius: var(--radius-md);
    transition: all var(--transition-normal);
    cursor: pointer;
    border: 2px solid transparent;
    min-height: 44px;
    position: relative;
    overflow: hidden;
}

.btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: rgba(255, 255, 255, 0.1);
    opacity: 0;
    transition: all var(--transition-normal);
    z-index: -1;
}

.btn:hover::before {
    left: 0;
    opacity: 1;
}

.btn-primary {
    background: var(--color-accent-primary);
    color: #000000;
    border-color: rgba(255, 255, 255, 0.3);
    box-shadow: none;
}

.btn-primary:hover {
    background: var(--color-accent-hover);
    border-color: rgba(255, 255, 255, 0.5);
    transform: translateY(-3px) scale(1.02);
    box-shadow: none;
}

.btn-secondary {
    background: rgba(255, 255, 255, 0.1);
    color: #ffffff;
    border-color: rgba(255, 255, 255, 0.3);
    box-shadow: none;
}

.btn-secondary:hover {
    background: var(--color-accent-primary);
    color: #000000;
    border-color: rgba(255, 255, 255, 0.5);
    transform: translateY(-3px) scale(1.02);
    box-shadow: none;
}

.btn-accent {
    background: transparent;
    color: var(--color-text-primary);
    border-color: var(--color-text-primary);
    position: relative;
}

.btn-accent::after {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: inherit;
    padding: 2px;
    background: var(--gradient-accent);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask-composite: exclude;
    opacity: 0;
    transition: opacity var(--transition-normal);
}

.btn-accent:hover {
    color: var(--color-accent-primary);
    transform: translateY(-3px) scale(1.02);
}

.btn-accent:hover::after {
    opacity: 1;
}

.btn-lg {
    padding: var(--spacing-md) var(--spacing-xl);
    font-size: var(--font-size-lg);
    min-height: 52px;
}

.btn-sm {
    padding: var(--spacing-xs) var(--spacing-md);
    font-size: var(--font-size-sm);
    min-height: 36px;
}

/* Enhanced Card Styles with Animations */
.card {
    background: var(--color-card-bg);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    padding: var(--spacing-xl);
    transition: all var(--transition-normal);
    position: relative;
    overflow: hidden;
    cursor: pointer;
    box-shadow: none;
}

.card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: transparent;
    opacity: 0;
    transition: opacity var(--transition-normal);
    pointer-events: none;
}

.card:hover {
    border-color: rgba(255, 255, 255, 0.2);
    border-radius: 25px;
    transform: translateY(-8px) scale(1.02);
    box-shadow: none;
}

.card:hover::before {
    opacity: 0;
}

.card-interactive {
    background: var(--color-card-bg);
    border: 2px solid rgba(255, 255, 255, 0.1);
    background-clip: padding-box;
    border-radius: 20px;
    box-shadow: none;
}

.card-interactive::after {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: inherit;
    padding: 2px;
    background: var(--gradient-accent);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask-composite: exclude;
    opacity: 0;
    transition: all var(--transition-bounce);
}

.card-interactive:hover {
    transform: translateY(-12px) scale(1.05);
    border-radius: 25px;
    border-color: rgba(255, 255, 255, 0.2);
    box-shadow: none;
}

.card-interactive:hover::after {
    opacity: 1;
}

/* Input Styles */
.input {
    width: 100%;
    padding: var(--spacing-sm) var(--spacing-md);
    background: var(--color-tertiary-bg);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    color: var(--color-text-primary);
    font-size: var(--font-size-base);
    transition: all var(--transition-normal);
    box-shadow: none;
}

.input:focus {
    outline: none;
    border-color: rgba(255, 255, 255, 0.3);
    border-width: 2px;
    box-shadow: none;
}

.input::placeholder {
    color: var(--color-text-muted);
}



/* Loading Animation */
@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(60px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes fadeInLeft {
    from {
        opacity: 0;
        transform: translateX(-60px);
    }

    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes fadeInRight {
    from {
        opacity: 0;
        transform: translateX(60px);
    }

    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes scaleIn {
    from {
        opacity: 0;
        transform: scale(0.8);
    }

    to {
        opacity: 1;
        transform: scale(1);
    }
}

@keyframes slideInDown {
    from {
        opacity: 0;
        transform: translateY(-100%);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Removed glow pulse animation to eliminate yellow glow effect */

@keyframes borderFlow {
    0% {
        border-image-source: linear-gradient(90deg, var(--color-accent-primary) 0%, transparent 50%, transparent 100%);
    }

    50% {
        border-image-source: linear-gradient(90deg, transparent 0%, var(--color-accent-primary) 50%, transparent 100%);
    }

    100% {
        border-image-source: linear-gradient(90deg, transparent 0%, transparent 50%, var(--color-accent-primary) 100%);
    }
}

/* Animation Classes */
.fade-in {
    animation: fadeIn 0.6s ease-out;
}

.fade-in-up {
    animation: fadeInUp 0.8s ease-out;
}

.fade-in-left {
    animation: fadeInLeft 0.8s ease-out;
}

.fade-in-right {
    animation: fadeInRight 0.8s ease-out;
}

.scale-in {
    animation: scaleIn 0.6s ease-out;
}

.slide-in-down {
    animation: slideInDown 0.8s ease-out;
}

/* Removed glow pulse class */

/* Hover Effect Classes */
.hover-lift {
    transition: all var(--transition-normal);
}

.hover-lift:hover {
    transform: translateY(-8px);
}

.hover-glow {
    transition: all var(--transition-normal);
}

.hover-glow:hover {
    border-color: var(--color-accent-primary);
}

.hover-scale {
    transition: all var(--transition-bounce);
}

.hover-scale:hover {
    transform: scale(1.05);
}

.hover-border-flow {
    border: 2px solid transparent;
    border-image: linear-gradient(90deg, var(--color-accent-primary) 0%, transparent 50%, transparent 100%) 1;
    transition: all var(--transition-normal);
}

.hover-border-flow:hover {
    animation: borderFlow 1.5s ease-in-out infinite;
    border-radius: var(--radius-xl);
}

/* Interactive Elements */
.interactive-box {
    position: relative;
    padding: var(--spacing-xl);
    border: 2px solid rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    background: linear-gradient(145deg, var(--color-card-bg) 0%, var(--color-tertiary-bg) 100%);
    cursor: pointer;
    transition: all var(--transition-normal);
    overflow: hidden;
    box-shadow: var(--shadow-md);
}

.interactive-box::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: transparent;
    opacity: 0;
    transition: all var(--transition-slow);
    transform: scale(0);
}

.interactive-box:hover {
    border-color: rgba(255, 255, 255, 0.2);
    border-radius: 25px;
    transform: translateY(-5px) scale(1.02);
    box-shadow: var(--shadow-lg);
}

.interactive-box:hover::before {
    opacity: 0;
    transform: scale(0);
}

/* Claymorphism Effect - No glow */
.glass {
    background: linear-gradient(145deg, rgba(0, 0, 0, 0.8) 0%, rgba(26, 26, 26, 0.6) 100%);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    box-shadow: var(--shadow-md);
}



/* Enhanced Gradient Text - Black & White theme */
.gradient-text {
    background: linear-gradient(135deg, #ffffff 0%, #e0e0e0 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.gradient-text-accent {
    background: var(--gradient-accent);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

/* Responsive Design */
@media (max-width: 768px) {
    .container {
        padding: 0 var(--spacing-sm);
    }

    .section {
        padding: var(--spacing-2xl) 0;
    }

    .grid-cols-2 {
        grid-template-columns: 1fr;
    }

    .grid-cols-3 {
        grid-template-columns: 1fr;
    }

    .grid-cols-4 {
        grid-template-columns: repeat(2, 1fr);
    }

    .text-5xl {
        font-size: var(--font-size-4xl);
    }

    .text-4xl {
        font-size: var(--font-size-3xl);
    }

    .text-3xl {
        font-size: var(--font-size-2xl);
    }
}

@media (max-width: 480px) {
    .grid-cols-4 {
        grid-template-columns: 1fr;
    }

    .btn-lg {
        padding: var(--spacing-sm) var(--spacing-lg);
        font-size: var(--font-size-base);
    }
}

/* Accessibility */
@media (prefers-reduced-motion: reduce) {
    * {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

/* Focus Styles for Keyboard Navigation - Removed */
*:focus {
    outline: none;
}

.btn:focus {
    outline: none;
}

/* Remove all focus outlines and borders */
button:focus,
input:focus,
a:focus,
*:focus {
    outline: none !important;
    box-shadow: none !important;
}

/* Remove active state borders */
button:active,
input:active,
a:active,
*:active {
    outline: none !important;
    box-shadow: none !important;
}

/* Remove webkit focus rings */
button:focus-visible,
input:focus-visible,
a:focus-visible,
*:focus-visible {
    outline: none !important;
}

/* Remove tap highlights on mobile */
* {
    -webkit-tap-highlight-color: transparent;
    -webkit-focus-ring-color: transparent;
    -webkit-appearance: none;
}

/* Ensure buttons don't retain focus styles */
button,
.btn,
[role="button"] {
    -webkit-user-select: none;
    -moz-user-select: none;
    user-select: none;
}

button::-moz-focus-inner,
input::-moz-focus-inner {
    border: 0;
    padding: 0;
}

/* Custom Scrollbar - Dark theme */
::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-track {
    background: var(--color-secondary-bg);
}

::-webkit-scrollbar-thumb {
    background: var(--color-accent-primary);
    border-radius: var(--radius-full);
}

::-webkit-scrollbar-thumb:hover {
    background: var(--color-accent-hover);
}

src/app/admin/certificates/Certificates..content:
.certificatesPage {
    padding: 24px;
    max-width: 1400px;
    margin: 0 auto;
    background: transparent;
}

.loading {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 60vh;
    font-size: 1.2rem;
    color: #D0B49F;
    font-weight: 600;
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 32px;
    padding: 24px 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.title {
    font-size: 2.5rem;
    font-weight: 800;
    color: #ffffff;
    background: linear-gradient(135deg, #ffffff 0%, #D0B49F 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.headerActions {
    display: flex;
    gap: 16px;
    align-items: center;
}

.headerActions .btn {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 12px 24px;
    border-radius: 12px;
    font-weight: 600;
    transition: all 0.3s ease;
}

.headerActions .btn i {
    font-size: 1.1rem;
}

.filters {
    display: flex;
    gap: 20px;
    margin-bottom: 32px;
    padding: 20px;
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    border-radius: 16px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.searchBox {
    flex: 1;
    max-width: 400px;
}

.searchBox .input {
    width: 100%;
    padding: 12px 16px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 12px;
    background: rgba(255, 255, 255, 0.08);
    color: #ffffff;
    font-size: 1rem;
    transition: all 0.3s ease;
}

.searchBox .input:focus {
    outline: none;
    border-color: #D0B49F;
    background: rgba(255, 255, 255, 0.12);
    box-shadow: 0 0 0 3px rgba(208, 180, 159, 0.2);
}

.statusFilter {
    padding: 12px 16px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 12px;
    background: rgba(255, 255, 255, 0.08);
    color: #ffffff;
    font-size: 1rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    min-width: 150px;
}

.statusFilter:focus {
    outline: none;
    border-color: #D0B49F;
    background: rgba(255, 255, 255, 0.12);
}

.certificatesList {
    display: grid;
    gap: 20px;
}

.certificateCard {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 16px;
    padding: 24px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.certificateCard::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, rgba(208, 180, 159, 0.5), transparent);
}

.certificateCard:hover {
    transform: translateY(-4px);
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.3);
    border-color: rgba(208, 180, 159, 0.3);
}

.certificateInfo {
    flex: 1;
}

.studentName {
    font-size: 1.3rem;
    font-weight: 700;
    color: #ffffff;
    margin-bottom: 8px;
    display: block;
}

.courseInfo,
.completionInfo,
.generatedInfo {
    color: rgba(255, 255, 255, 0.7);
    margin-bottom: 6px;
    font-size: 0.95rem;
}

.courseInfo strong,
.completionInfo strong,
.generatedInfo strong {
    color: #D0B49F;
    font-weight: 600;
}

.status {
    display: inline-block;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    margin-top: 8px;
}

.status.issued {
    background: rgba(34, 197, 94, 0.2);
    color: #22c55e;
    border: 1px solid rgba(34, 197, 94, 0.3);
}

.status.pending {
    background: rgba(251, 191, 36, 0.2);
    color: #fbbf24;
    border: 1px solid rgba(251, 191, 36, 0.3);
}

.status.revoked {
    background: rgba(239, 68, 68, 0.2);
    color: #ef4444;
    border: 1px solid rgba(239, 68, 68, 0.3);
}

.actions {
    display: flex;
    gap: 12px;
    align-items: center;
}

.actions .btn {
    padding: 8px 16px;
    border-radius: 8px;
    font-size: 0.9rem;
    font-weight: 600;
    transition: all 0.3s ease;
}

.noCertificates {
    text-align: center;
    padding: 60px 20px;
    color: rgba(255, 255, 255, 0.6);
    font-size: 1.1rem;
}

/* Modal Styles */
.modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    backdrop-filter: blur(10px);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 9999;
    padding: 20px;
}

.modalContent {
    background: rgba(20, 20, 20, 0.98);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.15);
    border-radius: 20px;
    max-width: 600px;
    width: 100%;
    max-height: 90vh;
    overflow-y: auto;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
}

.modalHeader {
    padding: 24px 24px 16px 24px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.modalHeader h2 {
    font-size: 1.5rem;
    font-weight: 700;
    color: #ffffff;
    margin: 0;
}

.closeBtn {
    background: rgba(255, 255, 255, 0.1);
    border: none;
    color: #ffffff;
    font-size: 1.5rem;
    cursor: pointer;
    width: 32px;
    height: 32px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
}

.closeBtn:hover {
    background: rgba(239, 68, 68, 0.2);
    color: #ef4444;
}

.modalBody {
    padding: 24px;
}

.formGroup {
    margin-bottom: 20px;
}

.formGroup label {
    display: block;
    margin-bottom: 8px;
    font-weight: 600;
    color: #ffffff;
}

.formGroup .input,
.formGroup select {
    width: 100%;
    padding: 12px 16px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 12px;
    background: rgba(255, 255, 255, 0.08);
    color: #ffffff;
    font-size: 1rem;
    transition: all 0.3s ease;
}

.formGroup .input:focus,
.formGroup select:focus {
    outline: none;
    border-color: #D0B49F;
    background: rgba(255, 255, 255, 0.12);
    box-shadow: 0 0 0 3px rgba(208, 180, 159, 0.2);
}

.studentPreview {
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    padding: 20px;
    margin-top: 16px;
}

.studentPreview h4 {
    color: #D0B49F;
    font-size: 1.1rem;
    font-weight: 600;
    margin-bottom: 12px;
}

.previewInfo p {
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 8px;
    font-size: 0.95rem;
}

.previewInfo strong {
    color: #ffffff;
    font-weight: 600;
}

.fileNote {
    color: rgba(255, 255, 255, 0.6);
    font-size: 0.85rem;
    margin-top: 6px;
    display: block;
}

.filePreview {
    background: rgba(208, 180, 159, 0.1);
    border: 1px solid rgba(208, 180, 159, 0.2);
    border-radius: 12px;
    padding: 20px;
    margin-top: 16px;
}

.filePreview h4 {
    color: #D0B49F;
    font-size: 1.1rem;
    font-weight: 600;
    margin-bottom: 12px;
}

.fileInfo p {
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 8px;
    font-size: 0.95rem;
}

.fileInfo strong {
    color: #D0B49F;
    font-weight: 600;
}

.formGroup input[type="file"] {
    padding: 16px;
    border: 2px dashed rgba(208, 180, 159, 0.3);
    border-radius: 12px;
    background: rgba(255, 255, 255, 0.02);
    color: #ffffff;
    font-size: 1rem;
    transition: all 0.3s ease;
    cursor: pointer;
}

.formGroup input[type="file"]:hover {
    border-color: rgba(208, 180, 159, 0.5);
    background: rgba(255, 255, 255, 0.05);
}

.formGroup input[type="file"]:focus {
    outline: none;
    border-color: #D0B49F;
    background: rgba(255, 255, 255, 0.08);
    box-shadow: 0 0 0 3px rgba(208, 180, 159, 0.2);
}

/* Enhanced File Upload Area */
.fileUploadArea {
    position: relative;
    width: 100%;
}

.fileInput {
    position: absolute;
    opacity: 0;
    width: 100%;
    height: 100%;
    cursor: pointer;
    z-index: 2;
}

.fileUploadLabel {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 40px 20px;
    border: 2px dashed rgba(208, 180, 159, 0.3);
    border-radius: 16px;
    background: rgba(255, 255, 255, 0.02);
    cursor: pointer;
    transition: all 0.3s ease;
    min-height: 120px;
}

.fileUploadLabel:hover {
    border-color: rgba(208, 180, 159, 0.5);
    background: rgba(255, 255, 255, 0.05);
    transform: translateY(-2px);
}

.uploadIcon {
    font-size: 3rem;
    color: #D0B49F;
    margin-bottom: 16px;
    transition: all 0.3s ease;
}

.fileUploadLabel:hover .uploadIcon {
    transform: scale(1.1);
    color: #E6D3B7;
}

.uploadText {
    text-align: center;
}

.uploadTitle {
    display: block;
    font-size: 1.1rem;
    font-weight: 600;
    color: #ffffff;
    margin-bottom: 4px;
}

.uploadSubtitle {
    display: block;
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.6);
}

/* When file is selected */
.fileUploadArea:has(.fileInput[value]) .fileUploadLabel {
    border-color: rgba(34, 197, 94, 0.5);
    background: rgba(34, 197, 94, 0.1);
}

.fileUploadArea:has(.fileInput[value]) .uploadIcon {
    color: #22c55e;
}

.fileUploadArea:has(.fileInput[value]) .uploadTitle {
    color: #22c55e;
}

/* Drag and drop states */
.fileUploadArea.dragOver .fileUploadLabel {
    border-color: #D0B49F;
    background: rgba(208, 180, 159, 0.1);
    transform: scale(1.02);
}

.fileUploadArea.dragOver .uploadIcon {
    color: #D0B49F;
    transform: scale(1.2);
}

.fileUploadArea.dragOver .uploadTitle {
    color: #D0B49F;
}

.formActions {
    padding: 16px 24px 24px 24px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    display: flex;
    gap: 12px;
    justify-content: flex-end;
}

.formActions .btn {
    padding: 12px 24px;
    border-radius: 12px;
    font-weight: 600;
    transition: all 0.3s ease;
}

/* Mobile Responsiveness */
@media (max-width: 768px) {
    .certificatesPage {
        padding: 16px;
    }

    .header {
        flex-direction: column;
        gap: 16px;
        align-items: flex-start;
    }

    .title {
        font-size: 2rem;
    }

    .filters {
        flex-direction: column;
        gap: 16px;
    }

    .searchBox {
        max-width: 100%;
    }

    .certificateCard {
        flex-direction: column;
        gap: 16px;
        align-items: flex-start;
        padding: 20px;
    }

    .actions {
        width: 100%;
        justify-content: flex-start;
    }

    .modalContent {
        margin: 10px;
        max-width: calc(100% - 20px);
    }

    .formActions {
        flex-direction: column;
    }

    .formActions .btn {
        width: 100%;
    }
}

.uploadMessage {
  padding: 16px 20px;
  border-radius: 12px;
  margin-bottom: 24px;
  font-weight: 500;
  animation: slideIn 0.3s ease;
}

.uploadMessage.success {
  background: rgba(34, 197, 94, 0.1);
  border: 1px solid rgba(34, 197, 94, 0.3);
  color: #22c55e;
}

.uploadMessage.error {
  background: rgba(239, 68, 68, 0.1);
  border: 1px solid rgba(239, 68, 68, 0.3);
  color: #ef4444;
}

@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateY(-10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

src/app/admin/courses/page.tsx.content:
"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import AdminLayout from "../../../components/layout/AdminLayout";
import styles from "./Courses.module.css";

interface Course {
  id: string;
  title: string;
  description: string;
  duration: string;
  price: number;
  maxStudents: number;
  currentStudents: number;
  status: "active" | "draft" | "archived";
  instructor: string;
  modules: number;
}

export default function CoursesPage() {
  const [courses, setCourses] = useState<Course[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState<Course | null>(null);
  const [newCourse, setNewCourse] = useState({
    title: "",
    description: "",
    duration: "",
    price: "",
    maxStudents: "",
    instructor: "",
  });
  const [editCourse, setEditCourse] = useState({
    title: "",
    description: "",
    duration: "",
    price: "",
    maxStudents: "",
    instructor: "",
  });
  const router = useRouter();

  useEffect(() => {
    const adminSession = localStorage.getItem("admin_session");
    if (!adminSession) {
      router.push("/admin/login");
      return;
    }
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await fetch("/api/admin/courses");
      const data = await response.json();
      if (response.ok) {
        setCourses(data.courses);
      }
    } catch (error) {
      console.error("Failed to fetch courses:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddCourse = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await fetch("/api/admin/courses", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...newCourse,
          price: parseFloat(newCourse.price),
          maxStudents: parseInt(newCourse.maxStudents),
        }),
      });

      if (response.ok) {
        setShowAddForm(false);
        setNewCourse({
          title: "",
          description: "",
          duration: "",
          price: "",
          maxStudents: "",
          instructor: "",
        });
        fetchCourses();
        alert("Course created successfully!");
      } else {
        const data = await response.json();
        alert(data.error || "Failed to create course");
      }
    } catch (error) {
      alert("Error creating course");
    }
  };

  const handleEditCourse = (course: Course) => {
    setEditingCourse(course);
    setEditCourse({
      title: course.title,
      description: course.description,
      duration: course.duration,
      price: course.price.toString(),
      maxStudents: course.maxStudents.toString(),
      instructor: course.instructor,
    });
    setShowEditForm(true);
  };

  const handleUpdateCourse = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingCourse) return;

    try {
      const response = await fetch(`/api/admin/courses/${editingCourse.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...editCourse,
          price: parseFloat(editCourse.price),
          maxStudents: parseInt(editCourse.maxStudents),
        }),
      });

      if (response.ok) {
        setShowEditForm(false);
        setEditingCourse(null);
        setEditCourse({
          title: "",
          description: "",
          duration: "",
          price: "",
          maxStudents: "",
          instructor: "",
        });
        fetchCourses();
        alert("Course updated successfully!");
      } else {
        const data = await response.json();
        alert(data.error || "Failed to update course");
      }
    } catch (error) {
      alert("Error updating course");
    }
  };

  const handleDeleteCourse = async (courseId: string) => {
    if (confirm("Are you sure you want to delete this course?")) {
      try {
        const response = await fetch(`/api/admin/courses/${courseId}`, {
          method: "DELETE",
        });
        if (response.ok) {
          fetchCourses();
          alert("Course deleted successfully!");
        }
      } catch (error) {
        alert("Error deleting course");
      }
    }
  };

  const toggleCourseStatus = async (
    courseId: string,
    currentStatus: string
  ) => {
    const newStatus = currentStatus === "active" ? "draft" : "active";
    try {
      const response = await fetch(`/api/admin/courses/${courseId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: newStatus }),
      });
      if (response.ok) {
        fetchCourses();
      }
    } catch (error) {
      alert("Error updating course status");
    }
  };

  if (isLoading) {
    return (
      <AdminLayout>
        <div className={styles.loading}>Loading courses...</div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className={styles.coursesPage}>
        <div className={styles.header}>
          <h1 className={styles.title}>Courses Management</h1>
          <button
            onClick={() => setShowAddForm(true)}
            className="btn btn-primary"
          >
            Create New Course
          </button>
        </div>

        {/* Courses Grid */}
        <div className={styles.coursesGrid}>
          {courses.map((course) => (
            <div key={course.id} className={`card ${styles.courseCard}`}>
              <div className={styles.courseHeader}>
                <h3 className={styles.courseTitle}>{course.title}</h3>
                <span className={`${styles.status} ${styles[course.status]}`}>
                  {course.status}
                </span>
              </div>

              <p className={styles.courseDescription}>{course.description}</p>

              <div className={styles.courseDetails}>
                <div className={styles.detailItem}>
                  <span className={styles.detailLabel}>Duration:</span>
                  <span className={styles.detailValue}>{course.duration}</span>
                </div>
                <div className={styles.detailItem}>
                  <span className={styles.detailLabel}>Price:</span>
                  <span className={styles.detailValue}>${course.price}</span>
                </div>
                <div className={styles.detailItem}>
                  <span className={styles.detailLabel}>Students:</span>
                  <span className={styles.detailValue}>
                    {course.currentStudents}/{course.maxStudents}
                  </span>
                </div>
                <div className={styles.detailItem}>
                  <span className={styles.detailLabel}>Instructor:</span>
                  <span className={styles.detailValue}>
                    {course.instructor}
                  </span>
                </div>
                <div className={styles.detailItem}>
                  <span className={styles.detailLabel}>Modules:</span>
                  <span className={styles.detailValue}>{course.modules}</span>
                </div>
              </div>

              <div className={styles.courseActions}>
                <button
                  className={`btn btn-secondary btn-sm`}
                  onClick={() => handleEditCourse(course)}
                >
                  Edit
                </button>
                <button
                  className={`btn ${
                    course.status === "active" ? "btn-secondary" : "btn-primary"
                  } btn-sm`}
                  onClick={() => toggleCourseStatus(course.id, course.status)}
                >
                  {course.status === "active" ? "Deactivate" : "Activate"}
                </button>
                <button
                  className={`btn ${styles.deleteBtn} btn-sm`}
                  onClick={() => handleDeleteCourse(course.id)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Add Course Modal */}
        {showAddForm && (
          <div className={styles.modal}>
            <div className={styles.modalContent}>
              <div className={styles.modalHeader}>
                <h2>Create New Course</h2>
                <button
                  onClick={() => setShowAddForm(false)}
                  className={styles.closeBtn}
                >
                  Ã—
                </button>
              </div>

              <form onSubmit={handleAddCourse} className={styles.form}>
                <div className={styles.formGroup}>
                  <label>Course Title</label>
                  <input
                    type="text"
                    value={newCourse.title}
