        font-size: 1.4rem;
    }

    .headerScrolled .headerMenu_button {
        width: 40px;
        height: 40px;
        font-size: 1.25rem;
    }
}

/* Tablet (768px - 991px) */
@media (max-width: 991px) and (min-width: 769px) {
    .headerSpacer {
        height: 70px;
        padding: 15px 20px;
    }

    .headerScrolled {
        height: 50px;
