src/app/admin/services/page.tsx.content:
"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import AdminLayout from "../../../components/layout/AdminLayout";
import styles from "./Services.module.css";

interface Service {
  id: string;
  name: string;
  description: string;
  duration: string;
  price: number;
  category: string;
  status: "active" | "inactive";
}

export default function ServicesPage() {
  const [services, setServices] = useState<Service[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(false);
  const [editingService, setEditingService] = useState<Service | null>(null);
  const [newService, setNewService] = useState({
    name: "",
    description: "",
    duration: "",
    price: "",
    category: "",
  });
  const [editService, setEditService] = useState({
    name: "",
    description: "",
    duration: "",
    price: "",
    category: "",
  });
  const router = useRouter();

  useEffect(() => {
    const adminSession = localStorage.getItem("admin_session");
    if (!adminSession) {
      router.push("/admin/login");
      return;
    }
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const response = await fetch("/api/admin/services");
      const data = await response.json();
      if (response.ok) {
        setServices(data.services);
      }
    } catch (error) {
      console.error("Failed to fetch services:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddService = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await fetch("/api/admin/services", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...newService,
          price: parseFloat(newService.price),
        }),
      });

      if (response.ok) {
        setShowAddForm(false);
        setNewService({
          name: "",
          description: "",
          duration: "",
          price: "",
          category: "",
        });
        fetchServices();
        alert("Service added successfully!");
      }
    } catch (error) {
      alert("Error adding service");
    }
  };

  const handleEditService = (service: Service) => {
    setEditingService(service);
    setEditService({
      name: service.name,
      description: service.description,
      duration: service.duration,
      price: service.price.toString(),
      category: service.category,
    });
    setShowEditForm(true);
  };

  const handleUpdateService = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingService) return;

    try {
      const response = await fetch(`/api/admin/services/${editingService.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...editService,
          price: parseFloat(editService.price),
        }),
      });

      if (response.ok) {
        setShowEditForm(false);
        setEditingService(null);
        setEditService({
          name: "",
          description: "",
          duration: "",
          price: "",
          category: "",
        });
        fetchServices();
        alert("Service updated successfully!");
      } else {
        const data = await response.json();
        alert(data.error || "Failed to update service");
      }
    } catch (error) {
      alert("Error updating service");
    }
  };

  const toggleServiceStatus = async (
    serviceId: string,
    currentStatus: string
  ) => {
    const newStatus = currentStatus === "active" ? "inactive" : "active";
    try {
      const response = await fetch(`/api/admin/services/${serviceId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: newStatus }),
      });
      if (response.ok) {
        fetchServices();
      }
    } catch (error) {
      alert("Error updating service status");
    }
  };

  if (isLoading) {
    return (
      <AdminLayout>
        <div className={styles.loading}>Loading services...</div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className={styles.servicesPage}>
        <div className={styles.header}>
          <h1 className={styles.title}>Services Management</h1>
          <button
            onClick={() => setShowAddForm(true)}
            className="btn btn-primary"
          >
            Add New Service
          </button>
        </div>

        <div className={styles.servicesGrid}>
          {services.map((service) => (
            <div key={service.id} className={`card ${styles.serviceCard}`}>
              <div className={styles.serviceHeader}>
                <h3 className={styles.serviceName}>{service.name}</h3>
                <span className={`${styles.status} ${styles[service.status]}`}>
                  {service.status}
                </span>
              </div>
              <p className={styles.serviceDescription}>{service.description}</p>
              <div className={styles.serviceDetails}>
                <div className={styles.detailItem}>
                  <span>Duration: {service.duration}</span>
                </div>
                <div className={styles.detailItem}>
                  <span>Price: ${service.price}</span>
                </div>
                <div className={styles.detailItem}>
                  <span>Category: {service.category}</span>
                </div>
              </div>
              <div className={styles.serviceActions}>
                <button
                  className="btn btn-secondary btn-sm"
                  onClick={() => handleEditService(service)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-primary btn-sm"
                  onClick={() =>
                    toggleServiceStatus(service.id, service.status)
                  }
                >
                  {service.status === "active" ? "Deactivate" : "Activate"}
                </button>
              </div>
            </div>
          ))}
        </div>

        {showAddForm && (
          <div className={styles.modal}>
            <div className={styles.modalContent}>
              <div className={styles.modalHeader}>
                <h2>Add New Service</h2>
                <button
                  onClick={() => setShowAddForm(false)}
                  className={styles.closeBtn}
                >
                  Ã—
                </button>
              </div>
              <form onSubmit={handleAddService} className={styles.form}>
                <div className={styles.formGroup}>
                  <label>Service Name</label>
                  <input
                    type="text"
                    value={newService.name}
                    onChange={(e) =>
                      setNewService({ ...newService, name: e.target.value })
                    }
                    required
                    className="input"
                  />
                </div>
                <div className={styles.formGroup}>
                  <label>Description</label>
                  <textarea
                    value={newService.description}
                    onChange={(e) =>
                      setNewService({
                        ...newService,
                        description: e.target.value,
                      })
                    }
                    required
                    className="input"
                    rows={3}
                  />
                </div>
                <div className={styles.formRow}>
                  <div className={styles.formGroup}>
                    <label>Duration</label>
                    <input
                      type="text"
                      value={newService.duration}
                      onChange={(e) =>
                        setNewService({
                          ...newService,
                          duration: e.target.value,
                        })
                      }
                      required
                      className="input"
                      placeholder="e.g., 60 minutes"
                    />
                  </div>
                  <div className={styles.formGroup}>
                    <label>Price ($)</label>
                    <input
                      type="number"
                      value={newService.price}
                      onChange={(e) =>
                        setNewService({ ...newService, price: e.target.value })
                      }
                      required
                      className="input"
                      min="0"
                      step="0.01"
                    />
                  </div>
                </div>
                <div className={styles.formGroup}>
                  <label>Category</label>
                  <div className={styles.categoryInputGroup}>
                    <select
                      value={newService.category}
                      onChange={(e) =>
                        setNewService({ ...newService, category: e.target.value })
                      }
                      required
                      className="input"
                    >
                      <option value="">Select Category</option>
                      <option value="HAIR">Hair</option>
                      <option value="MAKEUP">Makeup</option>
                      <option value="NAILS">Nails</option>
                      <option value="SKIN">Skin Care</option>
                      <option value="MASSAGE">Massage</option>
                      <option value="SPA">Spa</option>
                      <option value="BROWS">Brows</option>
                      <option value="WAXING">Waxing</option>
                      <option value="TANNING">Tanning</option>
                      <option value="custom">+ Add New Category</option>
                    </select>
                    {newService.category === 'custom' && (
                      <input
                        type="text"
                        placeholder="Enter new category name"
                        onChange={(e) => {
                          const customCategory = e.target.value.toUpperCase().replace(/\s+/g, '_');
                          setNewService({ ...newService, category: customCategory });
                        }}
                        className="input"
                        style={{ marginTop: '8px' }}
                      />
                    )}
                  </div>
                </div>
                <div className={styles.formActions}>
                  <button
                    type="button"
                    onClick={() => setShowAddForm(false)}
                    className="btn btn-secondary"
                  >
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Add Service
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Edit Service Modal */}
        {showEditForm && editingService && (
          <div className={styles.modal}>
            <div className={styles.modalContent}>
              <div className={styles.modalHeader}>
                <h2>Edit Service</h2>
                <button
                  onClick={() => {
                    setShowEditForm(false);
                    setEditingService(null);
                  }}
                  className={styles.closeBtn}
                >
                  Ã—
                </button>
              </div>
              <form onSubmit={handleUpdateService} className={styles.form}>
                <div className={styles.formGroup}>
                  <label>Service Name</label>
                  <input
                    type="text"
                    value={editService.name}
                    onChange={(e) =>
                      setEditService({ ...editService, name: e.target.value })
                    }
                    required
                    className="input"
                  />
                </div>
                <div className={styles.formGroup}>
                  <label>Description</label>
                  <textarea
                    value={editService.description}
                    onChange={(e) =>
                      setEditService({
                        ...editService,
                        description: e.target.value,
                      })
                    }
                    required
                    className="input"
                    rows={3}
                  />
                </div>
                <div className={styles.formRow}>
                  <div className={styles.formGroup}>
                    <label>Duration</label>
                    <input
                      type="text"
                      value={editService.duration}
                      onChange={(e) =>
                        setEditService({
                          ...editService,
                          duration: e.target.value,
                        })
                      }
                      required
                      className="input"
                      placeholder="e.g., 60 minutes"
                    />
                  </div>
                  <div className={styles.formGroup}>
                    <label>Price ($)</label>
                    <input
                      type="number"
                      value={editService.price}
                      onChange={(e) =>
                        setEditService({
                          ...editService,
                          price: e.target.value,
                        })
                      }
                      required
                      className="input"
                      min="0"
                      step="0.01"
                    />
                  </div>
                </div>
                <div className={styles.formGroup}>
                  <label>Category</label>
                  <select
                    value={editService.category}
                    onChange={(e) =>
                      setEditService({
                        ...editService,
                        category: e.target.value,
                      })
                    }
                    required
                    className="input"
                  >
                    <option value="">Select Category</option>
                    <option value="Hair">Hair</option>
                    <option value="Makeup">Makeup</option>
                    <option value="Nails">Nails</option>
                    <option value="Skin Care">Skin Care</option>
                    <option value="Bridal">Bridal</option>
                  </select>
                </div>
                <div className={styles.formActions}>
                  <button
                    type="button"
                    onClick={() => {
                      setShowEditForm(false);
                      setEditingService(null);
                    }}
                    className="btn btn-secondary"
                  >
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Update Service
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </AdminLayout>
  );
}

src/app/admin/students/Students.module.c.content:
.studentsPage {
    padding: var(--spacing-xl);
}

.loading {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 50vh;
    font-size: var(--font-size-lg);
    color: var(--color-text-secondary);
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--spacing-2xl);
}

.title {
    font-size: var(--font-size-3xl);
    font-weight: 700;
    color: var(--color-text-primary);
}

.filters {
    display: flex;
    gap: var(--spacing-lg);
    margin-bottom: var(--spacing-xl);
    align-items: center;
}

.searchBox {
    flex: 1;
    max-width: 400px;
}

.statusFilter {
    padding: var(--spacing-sm) var(--spacing-md);
    border: 1px solid var(--color-border);
    border-radius: var(--radius-md);
    background: var(--color-tertiary-bg);
    color: var(--color-text-primary);
    font-size: var(--font-size-base);
}

.tableContainer {
    background: var(--color-card-bg);
    border-radius: var(--radius-lg);
    border: 1px solid var(--color-card-border);
    overflow: hidden;
}

.studentsTable {
    width: 100%;
    border-collapse: collapse;
}

.studentsTable th {
    background: var(--color-tertiary-bg);
    padding: var(--spacing-lg);
    text-align: left;
    font-weight: 600;
    color: var(--color-text-primary);
    border-bottom: 1px solid var(--color-border);
}

.studentsTable td {
    padding: var(--spacing-lg);
    border-bottom: 1px solid var(--color-border);
    color: var(--color-text-secondary);
}

.studentsTable tr:hover {
    background: var(--color-tertiary-bg);
}

.progressBar {
    position: relative;
    width: 100px;
    height: 20px;
    background: var(--color-border);
    border-radius: var(--radius-full);
    overflow: hidden;
}

.progressFill {
    height: 100%;
    background: linear-gradient(90deg, var(--color-text-primary) 0%, var(--color-text-secondary) 100%);
    border-radius: var(--radius-full);
    transition: width var(--transition-normal);
}

.progressText {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-size: var(--font-size-xs);
    font-weight: 600;
    color: var(--color-primary-bg);
    text-shadow: 1px 1px 1px rgba(0, 0, 0, 0.5);
}

.status {
    padding: var(--spacing-xs) var(--spacing-sm);
    border-radius: var(--radius-md);
    font-size: var(--font-size-sm);
    font-weight: 500;
    text-transform: uppercase;
}

.status.active {
    background: rgba(34, 197, 94, 0.2);
    color: #22c55e;
    border: 1px solid #22c55e;
}

.status.completed {
    background: rgba(59, 130, 246, 0.2);
    color: #3b82f6;
    border: 1px solid #3b82f6;
}

.status.paused {
    background: rgba(251, 191, 36, 0.2);
    color: #fbbf24;
    border: 1px solid #fbbf24;
}

.actions {
    display: flex;
    gap: var(--spacing-sm);
}

.actionBtn {
    padding: var(--spacing-xs) var(--spacing-sm);
    border: none;
    border-radius: var(--radius-sm);
    cursor: pointer;
    font-size: var(--font-size-sm);
    font-weight: 500;
    transition: all var(--transition-normal);
}

.editBtn {
    background: rgba(59, 130, 246, 0.2);
    color: #3b82f6;
    border: 1px solid #3b82f6;
}

.editBtn:hover {
    background: #3b82f6;
    color: white;
}

.deleteBtn {
    background: rgba(239, 68, 68, 0.2);
    color: #ef4444;
    border: 1px solid #ef4444;
}

.deleteBtn:hover {
    background: #ef4444;
    color: white;
}

/* Modal Styles */
.modal {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
}

.modalContent {
    background: var(--color-card-bg);
    border-radius: var(--radius-lg);
    border: 1px solid var(--color-card-border);
    width: 100%;
    max-width: 500px;
    max-height: 90vh;
    overflow-y: auto;
}

.modalHeader {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: var(--spacing-xl);
    border-bottom: 1px solid var(--color-border);
}

.modalHeader h2 {
    font-size: var(--font-size-xl);
    font-weight: 600;
    color: var(--color-text-primary);
    margin: 0;
}

.closeBtn {
    background: none;
    border: none;
    font-size: var(--font-size-2xl);
    color: var(--color-text-secondary);
    cursor: pointer;
    padding: 0;
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: var(--radius-sm);
    transition: all var(--transition-normal);
}

.closeBtn:hover {
    background: var(--color-tertiary-bg);
    color: var(--color-text-primary);
}

.form {
    padding: var(--spacing-xl);
}

.formGroup {
    margin-bottom: var(--spacing-lg);
}

.formGroup label {
    display: block;
    margin-bottom: var(--spacing-sm);
    font-weight: 500;
    color: var(--color-text-primary);
}

.formActions {
    display: flex;
    gap: var(--spacing-md);
    justify-content: flex-end;
    margin-top: var(--spacing-xl);
    padding-top: var(--spacing-lg);
    border-top: 1px solid var(--color-border);
}

/* Responsive Design */
@media (max-width: 1024px) {
    .studentsTable {
        font-size: var(--font-size-sm);
    }

    .studentsTable th,
    .studentsTable td {
        padding: var(--spacing-md);
    }
}

@media (max-width: 768px) {
    .studentsPage {
        padding: var(--spacing-md);
    }

    .header {
        flex-direction: column;
        gap: var(--spacing-lg);
        align-items: stretch;
    }

    .filters {
        flex-direction: column;
        gap: var(--spacing-md);
    }

    .searchBox {
        max-width: none;
    }

    .tableContainer {
        overflow-x: auto;
    }

    .studentsTable {
        min-width: 800px;
    }

    .modalContent {
        margin: var(--spacing-md);
        max-width: none;
    }
}

@media (max-width: 480px) {
    .form {
        padding: var(--spacing-lg);
    }

    .formActions {
        flex-direction: column;
    }

    .modalHeader {
        padding: var(--spacing-lg);
    }
}

