src/app/reviews/page.tsx.content:
"use client";

import { useState, useEffect } from "react";
import Layout from "../../components/layout/Layout";
import AnimatedSection from "../../components/ui/animated-components";
import styles from "./Reviews.module.css";

interface Review {
  id: string;
  name: string;
  rating: number;
  comment: string;
  service: string;
  date: string;
  imageUrl?: string;
}

export default function ReviewsPage() {
  const [reviews, setReviews] = useState<Review[]>([]);
  const [averageRating, setAverageRating] = useState(0);
  const [totalReviews, setTotalReviews] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [filter, setFilter] = useState("all");

  useEffect(() => {
    fetchReviews();
  }, []);

  const fetchReviews = async () => {
    try {
      // Simulate API call with sample data
      const sampleReviews: Review[] = [
        {
          id: "1",
          name: "Sarah Johnson",
          rating: 5,
          comment:
            "Amazing experience! The hair styling course exceeded my expectations. The instructors are highly professional and knowledgeable.",
          service: "Hair Styling Course",
          date: "2024-01-15",
          imageUrl: "/images/reviews/sarah.jpg",
        },
        {
          id: "2",
          name: "Emily Chen",
          rating: 5,
          comment:
            "Best salon in town! Got a wonderful haircut and the makeup services are top-notch. Highly recommend!",
          service: "Haircut & Makeup",
          date: "2024-01-10",
        },
        {
          id: "3",
          name: "Jessica Miller",
          rating: 4,
          comment:
            "Great learning environment. The makeup artistry course helped me develop professional skills.",
          service: "Makeup Artistry Course",
          date: "2024-01-08",
        },
        {
          id: "4",
          name: "Amanda Rodriguez",
          rating: 5,
          comment:
            "Fantastic nail art course! Learned so many techniques and the certification helped me start my career.",
          service: "Nail Art Course",
          date: "2024-01-05",
        },
        {
          id: "5",
          name: "Maria Garcia",
          rating: 5,
          comment:
            "Professional staff and excellent facilities. The bridal makeup package was perfect for my wedding!",
          service: "Bridal Makeup",
          date: "2024-01-03",
        },
        {
          id: "6",
          name: "Lisa Thompson",
          rating: 4,
          comment:
            "Good overall experience. The eyebrow threading service was precise and the staff was friendly.",
          service: "Eyebrow Threading",
          date: "2024-01-01",
        },
      ];

      setReviews(sampleReviews);
      const avg =
        sampleReviews.reduce((sum, review) => sum + review.rating, 0) /
        sampleReviews.length;
      setAverageRating(avg);
      setTotalReviews(sampleReviews.length);
    } catch (error) {
      console.error("Failed to fetch reviews:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const filteredReviews =
    filter === "all"
      ? reviews
      : reviews.filter((review) =>
          review.service.toLowerCase().includes(filter.toLowerCase())
        );

  const renderStars = (rating: number) => {
    return [...Array(5)].map((_, index) => (
      <span
        key={index}
        className={`${styles.star} ${index < rating ? styles.filled : ""}`}
      >
        â˜…
      </span>
    ));
  };

  if (isLoading) {
    return (
      <Layout>
        <div className={styles.loading}>Loading reviews...</div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className={styles.reviewsPage}>
        {/* Hero Section */}
        <AnimatedSection className={styles.hero} animationType="fade-up">
          <div className="container">
            <h1 className={styles.title}>Client Reviews</h1>
            <p className={styles.subtitle}>
              See what our clients say about their experience at The Fringe
            </p>

            <div className={styles.stats}>
              <div className={styles.statItem}>
                <div className={styles.statNumber}>
                  {averageRating.toFixed(1)}
                </div>
                <div className={styles.statStars}>
                  {renderStars(Math.round(averageRating))}
                </div>
                <div className={styles.statLabel}>Average Rating</div>
              </div>
              <div className={styles.statItem}>
                <div className={styles.statNumber}>{totalReviews}</div>
                <div className={styles.statLabel}>Total Reviews</div>
              </div>
            </div>
          </div>
        </AnimatedSection>

        {/* Filter Section */}
        <AnimatedSection className={styles.filters} animationType="fade-up">
          <div className="container">
            <div className={styles.filterButtons}>
              <button
                className={`${styles.filterBtn} ${
                  filter === "all" ? styles.active : ""
                }`}
                onClick={() => setFilter("all")}
              >
                All Reviews
              </button>
              <button
                className={`${styles.filterBtn} ${
                  filter === "course" ? styles.active : ""
                }`}
                onClick={() => setFilter("course")}
              >
                Courses
              </button>
              <button
                className={`${styles.filterBtn} ${
                  filter === "service" ? styles.active : ""
                }`}
                onClick={() => setFilter("service")}
              >
                Services
              </button>
            </div>
          </div>
        </AnimatedSection>

        {/* Reviews Grid */}
        <AnimatedSection
          className={styles.reviewsSection}
          animationType="fade-up"
        >
          <div className="container">
            <div className={styles.reviewsGrid}>
              {filteredReviews.map((review, index) => (
                <div key={review.id} className={`card ${styles.reviewCard}`}>
                  <div className={styles.reviewHeader}>
                    <div className={styles.reviewerInfo}>
                      <div className={styles.reviewerAvatar}>
                        {review.imageUrl ? (
                          <img src={review.imageUrl} alt={review.name} />
                        ) : (
                          <div className={styles.avatarPlaceholder}>
                            {review.name
                              .split(" ")
                              .map((n) => n[0])
                              .join("")}
                          </div>
                        )}
                      </div>
                      <div className={styles.reviewerDetails}>
                        <h4 className={styles.reviewerName}>{review.name}</h4>
                        <p className={styles.reviewService}>{review.service}</p>
                      </div>
                    </div>
                    <div className={styles.reviewRating}>
                      {renderStars(review.rating)}
                    </div>
                  </div>

                  <div className={styles.reviewContent}>
                    <p className={styles.reviewComment}>{review.comment}</p>
                  </div>

                  <div className={styles.reviewFooter}>
                    <span className={styles.reviewDate}>
                      {new Date(review.date).toLocaleDateString("en-US", {
                        year: "numeric",
                        month: "long",
                        day: "numeric",
                      })}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </AnimatedSection>

        {/* Call to Action */}
        <div style={{ marginBottom: "150px" }}>
          <AnimatedSection className={styles.cta} animationType="scale">
            <div className="container">
              <div className={styles.ctaContent}>
                <h2>Share Your Experience</h2>
                <p>We'd love to hear about your experience with us!</p>
                <a href="/contact" className="btn btn-primary btn-lg">
                  Leave a Review
                </a>
              </div>
            </div>
          </AnimatedSection>
        </div>
      </div>
    </Layout>
  );
}

src/app/admin/students/page.tsx.content:
"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import AdminLayout from "../../../components/layout/AdminLayout";
import styles from "./Students.module.css";

interface Student {
  id: string;
  name: string;
  email: string;
  course: string;
  enrollmentDate: string;
  progress: number;
  status: "active" | "completed" | "paused";
  certificateUrl?: string;
  invitedBy?: string;
  certificateAssignedBy?: string;
}

export default function StudentsPage() {
  const [students, setStudents] = useState<Student[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(false);
  const [showCertificateModal, setShowCertificateModal] = useState(false);
  const [editingStudent, setEditingStudent] = useState<Student | null>(null);
  const [certificateStudent, setCertificateStudent] = useState<Student | null>(
    null
  );
  const [searchTerm, setSearchTerm] = useState("");
  const [filterStatus, setFilterStatus] = useState("all");
  const [newStudent, setNewStudent] = useState({
    name: "",
    email: "",
    course: "",
    accessExpiryDate: "",
  });
  const [editStudent, setEditStudent] = useState({
    name: "",
    email: "",
    course: "",
    status: "active",
  });
  const [certificateFile, setCertificateFile] = useState<File | null>(null);
  const router = useRouter();

  useEffect(() => {
    const adminSession = localStorage.getItem("admin_session");
    if (!adminSession) {
      router.push("/admin/login");
      return;
    }
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await fetch("/api/admin/students");
      const data = await response.json();
      if (response.ok) {
        setStudents(data.students);
      }
    } catch (error) {
      console.error("Failed to fetch students:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddStudent = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await fetch("/api/admin/students", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newStudent),
      });

      if (response.ok) {
        setShowAddForm(false);
        setNewStudent({ name: "", email: "", course: "", accessExpiryDate: "" });
        fetchStudents();
        alert("Student added successfully!");
      } else {
        const data = await response.json();
        alert(data.error || "Failed to add student");
      }
    } catch (error) {
      alert("Error adding student");
    }
  };

  const handleDeleteStudent = async (studentId: string) => {
    if (confirm("Are you sure you want to delete this student?")) {
      try {
        const response = await fetch(`/api/admin/students/${studentId}`, {
          method: "DELETE",
        });
        if (response.ok) {
          fetchStudents();
          alert("Student deleted successfully!");
        }
      } catch (error) {
        alert("Error deleting student");
      }
    }
  };

  const handleEditStudent = (student: Student) => {
    setEditingStudent(student);
    setEditStudent({
      name: student.name,
      email: student.email,
      course: student.course,
      status: student.status,
    });
    setShowEditForm(true);
  };

  const handleUpdateStudent = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingStudent) return;

    try {
      const response = await fetch(`/api/admin/students/${editingStudent.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(editStudent),
      });

      if (response.ok) {
        setShowEditForm(false);
        setEditingStudent(null);
        setEditStudent({
          name: "",
          email: "",
          course: "",
          status: "active",
        });
        fetchStudents();
        alert("Student updated successfully!");
      } else {
        const data = await response.json();
        alert(data.error || "Failed to update student");
      }
    } catch (error) {
      alert("Error updating student");
    }
  };

  const handleUploadCertificate = (student: Student) => {
    setCertificateStudent(student);
    setShowCertificateModal(true);
  };

  const handleCertificateUpload = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!certificateStudent || !certificateFile) return;

    const formData = new FormData();
    formData.append("certificate", certificateFile);
    formData.append("studentId", certificateStudent.id);

    try {
      const response = await fetch("/api/admin/students/upload-certificate", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        setShowCertificateModal(false);
        setCertificateStudent(null);
        setCertificateFile(null);
        fetchStudents();
        alert("Certificate uploaded successfully!");
      } else {
        const data = await response.json();
        alert(data.error || "Failed to upload certificate");
      }
    } catch (error) {
      alert("Error uploading certificate");
    }
  };

  const handleSendInvite = async (studentId: string) => {
    try {
      const response = await fetch(`/api/admin/students/${studentId}/invite`, {
        method: "POST",
      });

      if (response.ok) {
        alert("Invitation sent successfully!");
      } else {
        const data = await response.json();
        alert(data.error || "Failed to send invitation");
      }
    } catch (error) {
      alert("Error sending invitation");
    }
  };

  const filteredStudents = students.filter((student) => {
    const matchesSearch =
      student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      student.email.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter =
      filterStatus === "all" || student.status === filterStatus;
    return matchesSearch && matchesFilter;
  });

  if (isLoading) {
    return (
      <AdminLayout>
        <div className={styles.loading}>Loading students...</div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className={styles.studentsPage}>
        <div className={styles.header}>
          <h1 className={styles.title}>Students Management</h1>
          <button
            onClick={() => setShowAddForm(true)}
            className="btn btn-primary"
          >
            Add New Student
          </button>
        </div>

        {/* Filters */}
        <div className={styles.filters}>
          <div className={styles.searchBox}>
            <input
              type="text"
              placeholder="Search students..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="input"
            />
          </div>
          <select
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
            className={styles.statusFilter}
          >
            <option value="all">All Status</option>
            <option value="active">Active</option>
            <option value="completed">Completed</option>
            <option value="paused">Paused</option>
          </select>
        </div>

        {/* Students Table */}
        <div className={styles.tableContainer}>
          <table className={styles.studentsTable}>
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Course</th>
                <th>Enrollment Date</th>
                <th>Progress</th>
                <th>Status</th>
                <th>Certificate</th>
                <th>Invited By</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredStudents.map((student) => (
                <tr key={student.id}>
                  <td>{student.name}</td>
                  <td>{student.email}</td>
                  <td>{student.course}</td>
                  <td>
                    {new Date(student.enrollmentDate).toLocaleDateString()}
                  </td>
                  <td>
                    <div className={styles.progressBar}>
                      <div
                        className={styles.progressFill}
                        style={{ width: `${student.progress}%` }}
                      ></div>
                      <span className={styles.progressText}>
                        {student.progress}%
                      </span>
                    </div>
                  </td>
                  <td>
                    <span
                      className={`${styles.status} ${styles[student.status]}`}
                    >
                      {student.status}
                    </span>
                  </td>
                  <td>
                    {student.certificateUrl ? (
                      <span className={styles.certificateStatus}>
                        âœ… Assigned
                        <br />
                        <small>by {student.certificateAssignedBy}</small>
                      </span>
                    ) : (
                      <span className={styles.certificateStatus}>
                        âŒ Not Assigned
                      </span>
                    )}
                  </td>
                  <td>
                    <span className={styles.invitedBy}>
                      {student.invitedBy || "N/A"}
                    </span>
                  </td>
                  <td>
                    <div className={styles.actions}>
                      <button
                        className={`${styles.actionBtn} ${styles.editBtn}`}
                        onClick={() => handleEditStudent(student)}
                        title="Edit Student"
                      >
                        âœï¸
                      </button>
                      <button
                        className={`${styles.actionBtn} ${styles.certificateBtn}`}
                        onClick={() => handleUploadCertificate(student)}
                        title="Upload Certificate"
                      >
                        ðŸ†
                      </button>
                      <button
                        className={`${styles.actionBtn} ${styles.inviteBtn}`}
                        onClick={() => handleSendInvite(student.id)}
                        title="Send Invite"
                      >
                        ðŸ“§
                      </button>
                      <button
                        className={`${styles.actionBtn} ${styles.deleteBtn}`}
                        onClick={() => handleDeleteStudent(student.id)}
                        title="Delete Student"
                      >
                        ðŸ—‘ï¸
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Add Student Modal */}
        {showAddForm && (
          <div className={styles.modal}>
            <div className={styles.modalContent}>
              <div className={styles.modalHeader}>
                <h2>Add New Student</h2>
                <button
                  onClick={() => setShowAddForm(false)}
                  className={styles.closeBtn}
                >
                  Ã—
                </button>
              </div>

              <form onSubmit={handleAddStudent} className={styles.form}>
                <div className={styles.formGroup}>
                  <label>Student Name</label>
                  <input
                    type="text"
                    value={newStudent.name}
                    onChange={(e) =>
                      setNewStudent({ ...newStudent, name: e.target.value })
                    }
                    required
                    className="input"
                    placeholder="Enter student name"
                  />
                </div>

                <div className={styles.formGroup}>
                  <label>Email Address</label>
                  <input
                    type="email"
                    value={newStudent.email}
                    onChange={(e) =>
                      setNewStudent({ ...newStudent, email: e.target.value })
                    }
                    required
                    className="input"
                    placeholder="Enter email address"
                  />
                </div>

                <div className={styles.formGroup}>
                  <label>Course</label>
                  <select
                    value={newStudent.course}
                    onChange={(e) =>
                      setNewStudent({ ...newStudent, course: e.target.value })
                    }
                    required
                    className="input"
                  >
                    <option value="">Select Course</option>
                    <option value="Hair Styling">Hair Styling</option>
                    <option value="Makeup Artistry">Makeup Artistry</option>
                    <option value="Nail Art">Nail Art</option>
                    <option value="Skin Care">Skin Care</option>
                  </select>
                </div>

                <div className={styles.formGroup}>
                  <label>Access Expiry Date</label>
                  <input
                    type="date"
                    value={newStudent.accessExpiryDate}
                    onChange={(e) =>
                      setNewStudent({ ...newStudent, accessExpiryDate: e.target.value })
                    }
                    required
                    className="input"
                    min={new Date().toISOString().split('T')[0]}
                  />
                  <small className={styles.fieldNote}>
                    Student will receive an email to set their password and will have access until this date.
                  </small>
                </div>

                <div className={styles.formActions}>
                  <button
                    type="button"
                    onClick={() => setShowAddForm(false)}
                    className="btn btn-secondary"
                  >
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Add Student
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Edit Student Modal */}
        {showEditForm && editingStudent && (
          <div className={styles.modal}>
            <div className={styles.modalContent}>
              <div className={styles.modalHeader}>
                <h2>Edit Student</h2>
                <button
                  onClick={() => {
                    setShowEditForm(false);
                    setEditingStudent(null);
                  }}
                  className={styles.closeBtn}
                >
                  Ã—
                </button>
              </div>

              <form onSubmit={handleUpdateStudent} className={styles.form}>
                <div className={styles.formGroup}>
                  <label>Student Name</label>
                  <input
                    type="text"
                    value={editStudent.name}
                    onChange={(e) =>
                      setEditStudent({ ...editStudent, name: e.target.value })
                    }
                    required
                    className="input"
                    placeholder="Enter student name"
                  />
                </div>

                <div className={styles.formGroup}>
                  <label>Email Address</label>
                  <input
                    type="email"
                    value={editStudent.email}
                    onChange={(e) =>
                      setEditStudent({ ...editStudent, email: e.target.value })
                    }
                    required
                    className="input"
                    placeholder="Enter email address"
                  />
                </div>

                <div className={styles.formGroup}>
                  <label>Course</label>
                  <select
                    value={editStudent.course}
                    onChange={(e) =>
                      setEditStudent({ ...editStudent, course: e.target.value })
                    }
                    required
                    className="input"
                  >
                    <option value="">Select Course</option>
                    <option value="Hair Styling">Hair Styling</option>
                    <option value="Makeup Artistry">Makeup Artistry</option>
                    <option value="Nail Art">Nail Art</option>
                    <option value="Skin Care">Skin Care</option>
                  </select>
                </div>

                <div className={styles.formGroup}>
                  <label>Status</label>
                  <select
                    value={editStudent.status}
                    onChange={(e) =>
                      setEditStudent({ ...editStudent, status: e.target.value })
                    }
                    required
                    className="input"
                  >
                    <option value="active">Active</option>
                    <option value="completed">Completed</option>
                    <option value="paused">Paused</option>
                  </select>
                </div>

                <div className={styles.formActions}>
                  <button
                    type="button"
                    onClick={() => {
                      setShowEditForm(false);
                      setEditingStudent(null);
                    }}
                    className="btn btn-secondary"
                  >
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Update Student
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Certificate Upload Modal */}
        {showCertificateModal && certificateStudent && (
          <div className={styles.modal}>
            <div className={styles.modalContent}>
              <div className={styles.modalHeader}>
                <h2>Upload Certificate for {certificateStudent.name}</h2>
                <button
                  onClick={() => {
                    setShowCertificateModal(false);
                    setCertificateStudent(null);
                    setCertificateFile(null);
                  }}
                  className={styles.closeBtn}
                >
                  Ã—
                </button>
              </div>

              <form onSubmit={handleCertificateUpload} className={styles.form}>
                <div className={styles.formGroup}>
                  <label>Certificate File</label>
                  <input
                    type="file"
                    accept=".pdf,.jpg,.jpeg,.png"
                    onChange={(e) =>
                      setCertificateFile(e.target.files?.[0] || null)
                    }
                    required
                    className="input"
                  />
                  <small className={styles.fileNote}>
                    Supported formats: PDF, JPG, JPEG, PNG (Max: 5MB)
                  </small>
                </div>

                <div className={styles.studentInfo}>
                  <p>
                    <strong>Student:</strong> {certificateStudent.name}
                  </p>
                  <p>
                    <strong>Course:</strong> {certificateStudent.course}
                  </p>
                  <p>
                    <strong>Progress:</strong> {certificateStudent.progress}%
                  </p>
                </div>

                <div className={styles.formActions}>
                  <button
                    type="button"
                    onClick={() => {
                      setShowCertificateModal(false);
                      setCertificateStudent(null);
                      setCertificateFile(null);
                    }}
                    className="btn btn-secondary"
                  >
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Upload Certificate
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </AdminLayout>
  );
}

src/components/ui/textLoop/textLoop.modu.content:
.scrollContainer {
    width: 100%;
    overflow: hidden;
    position: relative;
    background: #ffffff;
    padding: 15px 0;
    opacity: 0;
    transform: translateY(30px);
    transition: all 1s ease-out;
}

.scrollContainer.visible {
    opacity: 1;
    transform: translateY(0);
}

.scrollTrack {
    display: flex;
    width: max-content;
    animation: scroll 40s linear infinite;
    will-change: transform;
}

.word {
    flex-shrink: 0;
    margin: 0 30px;
    font-size: 1rem;
    font-weight: 700;
    color: #000;
    background-clip: text;
    text-transform: uppercase;
    letter-spacing: 1px;
    position: relative;
    transition: all 0.3s ease;
    cursor: default;
    user-select: none;
}

.word:hover {
    transform: scale(1.05);
    filter: drop-shadow(0 0 20px rgba(208, 180, 159, 0.4));
}


@keyframes scroll {
    0% {
        transform: translateX(0);
    }

    100% {
        transform: translateX(-25%);
    }
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Responsive Design */

/* Large Desktop (1200px+) */
@media (min-width: 1200px) {
    .word {
        font-size: 2rem;
        margin: 0 40px;
    }
}

/* Medium Desktop (992px - 1199px) */
@media (min-width: 992px) and (max-width: 1199px) {
    .word {
        font-size: 1.4rem;
        margin: 0 35px;
    }

    .gradientLeft,
    .gradientRight {
        width: 150px;
    }
}

/* Tablet (768px - 991px) */
@media (min-width: 768px) and (max-width: 991px) {
    .scrollContainer {
        padding: 15px 0;
    }

    .word {
        font-size: 1.3rem;
        margin: 0 30px;
        letter-spacing: 1px;
    }

    .gradientLeft,
    .gradientRight {
        width: 120px;
    }
}

/* Mobile Landscape (481px - 767px) */
@media (min-width: 481px) and (max-width: 767px) {
    .scrollContainer {
        padding: 12px 0;
    }

    .word {
        font-size: 1.2rem;
        margin: 0 25px;
        letter-spacing: 1px;
    }

    .gradientLeft,
    .gradientRight {
        width: 80px;
    }
}

/* Mobile Portrait (320px - 480px) */
@media (min-width: 320px) and (max-width: 480px) {
    .scrollContainer {
        padding: 10px 0;
    }

    .word {
        font-size: 1.1rem;
        margin: 0 20px;
        letter-spacing: 0.5px;
    }

    .gradientLeft,
    .gradientRight {
        width: 60px;
    }
}

/* Extra Small Mobile (â‰¤320px) */
@media (max-width: 320px) {
    .scrollContainer {
        padding: 8px 0;
    }

    .word {
        font-size: 1rem;
        margin: 0 15px;
        letter-spacing: 0px;
    }

    .gradientLeft,
    .gradientRight {
        width: 50px;
    }
}

/* Reduce motion for accessibility */
@media (prefers-reduced-motion: reduce) {
    .scrollTrack {
        animation-duration: 60s;
    }

    .word {
        transition: none;
    }

    .word:hover {
        transform: none;
        filter: none;
    }

    .sectionHeader {
        animation: none;
        opacity: 1;
    }

    .scrollContainer {
        transition: none;
        opacity: 1;
        transform: none;
    }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
    .word {
        background: #000000;
        -webkit-background-clip: text;
        background-clip: text;
    }
}

