    padding-bottom: var(--spacing-sm);
}

.courseInfo {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-lg);
}

.infoItem {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: var(--spacing-md) 0;
    border-bottom: 1px solid var(--color-border);
}

.infoItem:last-child {
