}

.infoLabel {
    font-weight: 600;
    color: var(--color-text-secondary);
    flex: 1;
}

.infoValue {
    color: var(--color-text-primary);
    font-weight: 500;
    flex: 2;
    text-align: right;
}

.progressContainer {
    display: flex;
    align-items: center;
    gap: var(--spacing-md);
    flex: 2;
    justify-content: flex-end;
}

.progressBar {
    width: 120px;
    height: 8px;
    background-color: var(--color-border);
    border-radius: var(--radius-full);
    overflow: hidden;
}

.progressFill {
    height: 100%;
    background-color: var(--color-accent-primary);
    transition: width 0.3s ease;
}

.progressText {
    font-weight: 600;
    color: var(--color-text-primary);
    min-width: 40px;
}

.certificateInfo {
    display: flex;
    align-items: center;
    gap: var(--spacing-lg);
    padding: var(--spacing-lg);
    border-radius: var(--radius-lg);
}

.certificateAvailable {
    display: flex;
    align-items: center;
    gap: var(--spacing-lg);
    background-color: rgba(34, 197, 94, 0.1);
    padding: var(--spacing-lg);
    border-radius: var(--radius-lg);
    width: 100%;
}

.certificateNotAvailable {
    display: flex;
    align-items: center;
    gap: var(--spacing-lg);
    background-color: var(--color-tertiary-bg);
    padding: var(--spacing-lg);
    border-radius: var(--radius-lg);
    width: 100%;
}

.certificateIcon {
    font-size: 2.5rem;
}

.certificateText {
    font-size: var(--font-size-lg);
    font-weight: 600;
    color: var(--color-text-primary);
    margin: 0 0 var(--spacing-sm) 0;
}

.certificateSubtext {
    font-size: var(--font-size-base);
    color: var(--color-text-secondary);
    margin: 0;
}

/* Modal Styles */
.modal {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
}

.modalContent {
    background-color: var(--color-card-bg);
    border-radius: var(--radius-lg);
    padding: var(--spacing-2xl);
    width: 90%;
    max-width: 500px;
    max-height: 90vh;
    overflow-y: auto;
}

.modalHeader {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--spacing-xl);
    padding-bottom: var(--spacing-md);
    border-bottom: 1px solid var(--color-border);
}

.modalHeader h2 {
    margin: 0;
    color: var(--color-text-primary);
    font-size: var(--font-size-xl);
}

.closeBtn {
    background: none;
    border: none;
    font-size: var(--font-size-2xl);
    color: var(--color-text-secondary);
    cursor: pointer;
    padding: var(--spacing-xs);
    line-height: 1;
}

.closeBtn:hover {
    color: var(--color-text-primary);
}

.form {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-lg);
}

.formGroup {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-sm);
}

.formGroup label {
    font-weight: 600;
    color: var(--color-text-primary);
    font-size: var(--font-size-base);
}

.formActions {
    display: flex;
    gap: var(--spacing-md);
    justify-content: flex-end;
    margin-top: var(--spacing-lg);
    padding-top: var(--spacing-lg);
    border-top: 1px solid var(--color-border);
}

/* Mobile Responsive */
@media (max-width: 768px) {
    .header {
        flex-direction: column;
        gap: var(--spacing-md);
        align-items: stretch;
    }

    .title {
        font-size: var(--font-size-2xl);
        text-align: center;
    }

    .profileHeader {
        flex-direction: column;
        text-align: center;
    }

    .avatar {
        width: 100px;
        height: 100px;
        font-size: var(--font-size-3xl);
    }

    .infoItem {
        flex-direction: column;
        align-items: flex-start;
        gap: var(--spacing-xs);
    }

    .infoValue,
    .progressContainer {
        justify-content: flex-start;
    }

    .certificateAvailable,
    .certificateNotAvailable {
        flex-direction: column;
        text-align: center;
    }

    .modalContent {
        margin: var(--spacing-md);
        width: calc(100% - 2 * var(--spacing-md));
    }

    .formActions {
        flex-direction: column;
    }
}

@media (max-width: 480px) {
    .progressContainer {
        flex-direction: column;
        align-items: flex-start;
        gap: var(--spacing-sm);
    }

    .progressBar {
        width: 100%;
    }
}

