"use client";

import { useState, useEffect } from "react";
import Layout from "../../components/layout/Layout";
import styles from "./Courses.module.css";
import { coursesService } from "@/services/courses.service";
import type { Course } from "@/services/courses.service";
import { useAuth } from "@/hooks/useAuth";

export default function CoursesPage() {
  const [selectedCategories, setSelectedCategories] = useState<string[]>([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCourse, setSelectedCourse] = useState<Course | null>(null);
  const [courses, setCourses] = useState<Course[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const { user, isAuthenticated } = useAuth();

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const data = await coursesService.getAllCourses();
        setCourses(data);
      } catch (error) {
        setError("Failed to load courses. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, []);

  const courseCategoriesFromData = Array.from(
    new Set(courses.map((c) => c.level.toLowerCase()))
  ).map((lvl) => ({ id: lvl, name: lvl[0].toUpperCase() + lvl.slice(1) }));
  const [dropdownOpen, setDropdownOpen] = useState(false);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const dropdown = document.querySelector('.course-filter-dropdown');
      if (dropdown && !dropdown.contains(event.target as Node)) {
        setDropdownOpen(false);
      }
    };

    if (dropdownOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [dropdownOpen]);

  const handleEnrollment = async (courseId: string) => {
    if (!isAuthenticated) {
      window.location.href = "/login?redirect=/courses";
      return;
    }

    try {
      await coursesService.enrollInCourse({
        courseId,
        userId: user!.id,
      });
      window.location.href = `/student/my-course/${courseId}`;
    } catch (error) {
      setError("Failed to enroll in the course. Please try again.");
    }
  };

  const filteredCourses = courses.filter((course) => {
    const matchesCategory =
      selectedCategories.length === 0 ||
      selectedCategories.includes("ALL") ||
      selectedCategories.includes(course.level.toLowerCase());

    const matchesSearch =
      course.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      course.description.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesCategory && matchesSearch;
  });

  if (loading) {
    return (
      <Layout>
        <div className={styles.loadingContainer}>
          <div className={styles.loader}>Loading...</div>
        </div>
      </Layout>
    );
  }

  if (error) {
    return (
      <Layout>
        <div className={styles.errorContainer}>
          <p className={styles.error}>{error}</p>
          <button
            onClick={() => window.location.reload()}
            className={styles.retryButton}
          >
            Try Again
          </button>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className={styles.coursesPage}>
        <div className={styles.container}>
          <div className={styles.hero}>
            <h1 className={styles.pageTitle}>Professional Beauty Courses</h1>
            <p className={styles.pageSubtitle}>
              Launch your beauty career with our comprehensive training programs
            </p>
          </div>

          <div className={styles.filters}>
            <div className={styles.searchContainer}>
              <input
                type="text"
                placeholder="Search courses..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className={styles.searchInput}
              />
            </div>
            <div className={styles.categoryFilters}>
              <div className={`${styles.filterDropdown} ${dropdownOpen ? styles.open : ""} course-filter-dropdown`}>
                <button
                  className={`btn ${styles.filterButton}`}
                  onClick={() => setDropdownOpen((s) => !s)}
                  aria-haspopup="menu"
                  aria-expanded={dropdownOpen}
                >
                  {selectedCategories.length === 0
                    ? "Filter levels"
                    : `${selectedCategories.length} selected`}
                </button>

                {dropdownOpen && (
                  <div className={styles.dropdownMenu} role="menu">
                    <div className={styles.dropdownHeader}>
                      <button
                        className="btn btn-secondary"
                        onClick={() => setSelectedCategories(["ALL"])}
                      >
                        All
                      </button>
                      <button
                        className="btn btn-secondary"
                        onClick={() => setSelectedCategories([])}
                      >
                        Clear
                      </button>
                    </div>

                    <ul className={styles.dropdownList}>
                      {courseCategoriesFromData.map((c) => (
                        <li key={c.id}>
                          <label>
                            <input
                              type="checkbox"
                              checked={
                                selectedCategories.includes("ALL") ||
                                selectedCategories.includes(c.id)
                              }
                              onChange={(e) => {
                                const checked = e.target.checked;
                                setSelectedCategories((prev) => {
                                  if (checked) {
                                    return Array.from(new Set([...prev, c.id]));
                                  }
                                  return prev.filter(
                                    (x) => x !== c.id && x !== "ALL"
                                  );
                                });
                              }}
                            />{" "}
                            {c.name}
                          </label>
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </div>
          </div>

          <div className={styles.coursesGrid}>
            {filteredCourses.map((course) => (
              <div key={course.id} className={styles.courseCard}>
                <div className={styles.courseImage}>
                  <img src={course.image} alt={course.title} />
                  <div className={styles.courseBadge}>{course.level}</div>
                </div>
                <div className={styles.courseContent}>
                  <h3 className={styles.courseTitle}>{course.title}</h3>
                  <p className={styles.courseDescription}>
                    {course.description}
                  </p>

                  <div className={styles.courseStats}>
                    <span>ðŸ“… {course.duration}</span>
                    <span>ðŸ“š {course.topics.length} modules</span>
                    <span>ðŸ‘¨â€ðŸ« {course.instructor}</span>
                  </div>

                  <div className={styles.courseFooter}>
                    <div className={styles.price}>${course.price}</div>
                    <div className={styles.courseActions}>
                      <button
                        className="btn btn-secondary btn-sm"
                        onClick={() => setSelectedCourse(course)}
                      >
                        Learn More
                      </button>
                      <button
                        className="btn btn-primary btn-sm"
                        onClick={() => handleEnrollment(course.id)}
                        disabled={course.enrollmentStatus !== "open"}
                      >
                        {course.enrollmentStatus === "open"
                          ? "Enroll Now"
                          : "Enrollment Closed"}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {selectedCourse && (
          <div className={styles.modal} onClick={() => setSelectedCourse(null)}>
            <div
              className={styles.modalContent}
              onClick={(e) => e.stopPropagation()}
            >
              <button
                className={styles.closeButton}
                onClick={() => setSelectedCourse(null)}
              >
                Ã—
              </button>

              <div className={styles.modalHeader}>
                <img src={selectedCourse.image} alt={selectedCourse.title} />
                <div className={styles.modalTitle}>
                  <h2>{selectedCourse.title}</h2>
                  <p>{selectedCourse.description}</p>
                </div>
              </div>

              <div className={styles.modalBody}>
                <div className={styles.modalSection}>
                  <h3>What You'll Learn</h3>
                  <ul>
                    {selectedCourse.topics.map((topic, index) => (
                      <li key={index}>{topic}</li>
                    ))}
                  </ul>
                </div>

                <div className={styles.courseDetails}>
                  <div className={styles.detailItem}>
                    <strong>Duration:</strong> {selectedCourse.duration}
                  </div>
                  <div className={styles.detailItem}>
                    <strong>Level:</strong> {selectedCourse.level}
                  </div>
                  <div className={styles.detailItem}>
                    <strong>Instructor:</strong> {selectedCourse.instructor}
                  </div>
                  <div className={styles.detailItem}>
                    <strong>Status:</strong> {selectedCourse.enrollmentStatus}
                  </div>
                </div>
              </div>

              <div className={styles.modalFooter}>
                <button
                  className="btn btn-primary btn-lg"
                  onClick={() => handleEnrollment(selectedCourse.id)}
                  disabled={selectedCourse.enrollmentStatus !== "open"}
                >
                  Enroll Now - ${selectedCourse.price}
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
}

src/app/student/my-course/page.tsx.content:
"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import StudentLayout from "../../../components/layout/StudentLayout";
import VideoPlayer from "../../../components/ui/VideoPlayer";
import styles from "./MyCourse.module.css";

export default function MyCourse() {
  const [course, setCourse] = useState<any>(null);
  const [progress, setProgress] = useState<any>(null);
  const [currentModule, setCurrentModule] = useState<any>(null);
  const [isLoading, setIsLoading] = useState(true);
  const router = useRouter();

  useEffect(() => {
    const studentSession = localStorage.getItem("student_session");
    if (!studentSession) {
      router.push("/student/login");
      return;
    }
    fetchCourseData();
  }, []);

  const fetchCourseData = async () => {
    try {
      const response = await fetch("/api/student/dashboard");
      const data = await response.json();
      if (response.ok) {
        setCourse(data.course);
        setProgress(data.progress);
        if (data.course?.modules?.length > 0) {
          setCurrentModule(data.course.modules[0]);
        }
      }
    } catch (error) {
      console.error("Failed to fetch course data:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleModuleSelect = (module: any) => {
    setCurrentModule(module);
  };

  const handleVideoProgress = async (
    moduleId: string,
    progress: number,
    watchTime: number
  ) => {
    try {
      await fetch("/api/student/progress", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ moduleId, progress, watchTime }),
      });
    } catch (error) {
      console.error("Failed to update progress:", error);
    }
  };

  if (isLoading) {
    return (
      <StudentLayout>
        <div className={styles.loading}>Loading your course...</div>
      </StudentLayout>
    );
  }

  if (!course) {
    return (
      <StudentLayout>
        <div className={styles.noCourse}>
          <h2>No Course Assigned</h2>
          <p>Please contact your instructor to get enrolled in a course.</p>
        </div>
      </StudentLayout>
    );
  }

  return (
    <StudentLayout>
      <div className={styles.myCoursePage}>
        <div className={styles.courseHeader}>
          <h1 className={styles.courseTitle}>{course.title}</h1>
          <p className={styles.courseDescription}>{course.description}</p>
          <div className={styles.progressOverview}>
            <div className={styles.progressBar}>
              <div
                className={styles.progressFill}
                style={{ width: `${progress?.progressPercent || 0}%` }}
              ></div>
            </div>
            <span className={styles.progressText}>
              {Math.round(progress?.progressPercent || 0)}% Complete
            </span>
          </div>
        </div>

        <div className={styles.courseContent}>
          <div className={styles.modulesList}>
            <h2>Course Modules</h2>
            {course.modules?.map((module: any, index: number) => (
              <div
                key={module.id}
                className={`${styles.moduleItem} ${
                  currentModule?.id === module.id ? styles.active : ""
                } ${
                  progress?.completedModules?.includes(module.id)
                    ? styles.completed
                    : ""
                }`}
                onClick={() => handleModuleSelect(module)}
              >
                <div className={styles.moduleNumber}>{index + 1}</div>
                <div className={styles.moduleContent}>
                  <h3>{module.title}</h3>
                  <p>
                    {module.duration
                      ? `${module.duration} min`
                      : "Duration varies"}
                  </p>
                </div>
                <div className={styles.moduleStatus}>
                  {progress?.completedModules?.includes(module.id) ? "âœ“" : "â—‹"}
                </div>
              </div>
            ))}
          </div>

          <div className={styles.videoSection}>
            {currentModule ? (
              <>
                <h2>{currentModule.title}</h2>
                {currentModule.description && (
                  <p className={styles.moduleDescription}>
                    {currentModule.description}
                  </p>
                )}

                {currentModule.videoUrl ? (
                  <VideoPlayer
                    src={currentModule.videoUrl}
                    onProgress={(progress, watchTime) =>
                      handleVideoProgress(currentModule.id, progress, watchTime)
                    }
                    startTime={
                      progress?.moduleProgress?.[currentModule.id]
                        ?.lastPosition || 0
                    }
                  />
                ) : (
                  <div className={styles.textContent}>
                    <h3>Module Content</h3>
                    <div className={styles.contentText}>
                      {currentModule.content ||
                        "No content available for this module."}
                    </div>
                  </div>
                )}
              </>
            ) : (
              <div className={styles.selectModule}>
                <h3>Select a Module</h3>
                <p>Choose a module from the list to start learning.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </StudentLayout>
  );
}

src/components/layout/Footer.module.css.content:
.footer {
    background-color: var(--color-secondary-bg);
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    padding: var(--spacing-3xl) 0 var(--spacing-lg);
    position: relative;
    max-height: none;
    height: auto;
    overflow-y: visible;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    box-shadow: 0 -8px 32px rgba(0, 0, 0, 0.3);
}

.footerVisible {
    transform: translateY(0);
}

.footerHidden {
    transform: translateY(100%);
}

.footerContent {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: var(--spacing-2xl);
}

.section {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-md);
}

.sectionTitle {
    font-size: var(--font-size-4xl);
    font-weight: 600;
    color: var(--color-text-primary);
    margin-bottom: var(--spacing-xs);
}

.description {
    color: var(--color-text-secondary);
    line-height: 1.6;
    font-size: var(--font-size-base);
    padding-left: 10px;
}

.socialLinks {
    display: flex;
    gap: var(--spacing-lg);
    margin-top: var(--spacing-sm);
    
}

.socialLink {
    color: var(--color-text-secondary);
    text-decoration: none;
    padding: var(--spacing-sm);
    border-radius: var(--radius-md);
    transition: all var(--transition-normal);
    font-size: var(--font-size-3xl);
}

.socialLink:hover {
    color: var(--color-text-primary);
    transform: translateY(-2px);
}

.linkList {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-sm);
}

.footerLink {
    color: var(--color-text-secondary);
    text-decoration: none;
    font-size: var(--font-size-base);
    transition: all var(--transition-normal);
    padding-left: 10px;
}

.footerLink:hover {
    color: var(--color-text-primary);
    padding-left: var(--spacing-sm);
}

.contactInfo {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-sm);
    padding-left: 10px;
}

.contactItem {
    color: var(--color-text-secondary);
    font-size: var(--font-size-base);
    line-height: 1.5;
}

.contactItem strong {
    color: var(--color-text-primary);
}

.newsletter {
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: var(--spacing-xl);
    padding: var(--spacing-xl);
    background: var(--color-tertiary-bg);
    border-radius: 20px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    margin: var(--spacing-xl) 0;
}

.newsletterContent {
    flex: 1;
}

.newsletterTitle {
    font-size: var(--font-size-xl);
    font-weight: 600;
    color: var(--color-text-primary);
    margin-bottom: var(--spacing-xs);
}

.newsletterDescription {
    color: var(--color-text-secondary);
    font-size: var(--font-size-sm);
}

.newsletterForm {
    display: flex;
    gap: var(--spacing-md);
    align-items: center;
    flex-shrink: 0;
}

.newsletterInput {
    min-width: 280px;
}

.bottomBar {
    border-top: 1px solid var(--color-border);
    padding: var(--spacing-lg) 0;
}

.bottomContent {
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: var(--spacing-lg);
}

.copyright {
    color: var(--color-text-muted);
    font-size: var(--font-size-sm);
}

.legalLinks {
    display: flex;
    gap: var(--spacing-lg);
}

.legalLink {
    color: var(--color-text-muted);
    text-decoration: none;
    font-size: var(--font-size-sm);
    transition: color var(--transition-normal);
}

.legalLink:hover {
    color: var(--color-text-primary);
}

/* Large Fringe Text */
.fringeText {
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    font-size: 20rem;
    font-weight: 900;
    color: rgba(255, 255, 255, 0.04);
    white-space: nowrap;
    pointer-events: none;
    user-select: none;
    z-index: -1;
    height: 90%;
    overflow: hidden;
}

/* Mobile Responsive */
@media (max-width: 768px) {
    .footer {
        max-height: 85vh;
        padding: var(--spacing-2xl) 0 var(--spacing-md);
    }

    .fringeText {
        display: none;
    }

    .footerContent {
        grid-template-columns: 1fr;
        gap: var(--spacing-xl);
    }

    .newsletter {
        flex-direction: column;
        align-items: stretch;
        text-align: center;
        gap: var(--spacing-lg);
    }

    .newsletterForm {
        flex-direction: column;
        gap: var(--spacing-md);
    }

    .newsletterInput {
        min-width: 100%;
    }

    .bottomContent {
        flex-direction: column;
        text-align: center;
        gap: var(--spacing-md);
    }

    .legalLinks {
        justify-content: center;
        flex-wrap: wrap;
    }
}

@media (max-width: 480px) {
    .footer {
        padding: var(--spacing-xl) 0 var(--spacing-sm);
        max-height: 80vh;
    }

    .fringeText {
        font-size: 6rem;
    }

    .socialLinks {
        justify-content: center;
    }

    .legalLinks {
        gap: var(--spacing-md);
    }
}

@media (max-width: 320px) {
    .fringeText {
        font-size: 4rem;
    }
}

