"use client";
import { useState, useEffect } from "react";
import styles from "./Header.module.css";
import Image from "next/image";
import Link from "next/link";
import { usePathname } from "next/navigation";
import "remixicon/fonts/remixicon.css";
import FlowingMenu from "../navigation/FlowingMenu";

export default function Header() {
  const [navOpen, setNavOpen] = useState(false);
  const [isClosing, setIsClosing] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const pathname = usePathname();

  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Prevent body scroll when menu is open
  useEffect(() => {
    if (navOpen) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "unset";
    }

    return () => {
      document.body.style.overflow = "unset";
    };
  }, [navOpen]);

  const handleClose = () => {
    setIsClosing(true);
    setTimeout(() => {
      setNavOpen(false);
      setIsClosing(false);
    }, 400); // Match animation duration
  };

  // Improved navigation items with better organization
  const navigationItems = [
    {
      link: "/",
      text: "Home",
      image: "https://picsum.photos/600/400?random=1",
    },
    {
      link: "/about",
      text: "About Us",
      image: "https://picsum.photos/600/400?random=2",
    },
    {
      link: "/services",
      text: "Services",
      image: "https://picsum.photos/600/400?random=3",
    },
    {
      link: "/courses",
      text: "Courses",
      image: "https://picsum.photos/600/400?random=4",
    },
    {
      link: "/contact",
      text: "Contact Us",
      image: "https://picsum.photos/600/400?random=5",
    },
  ];

  // Check if current page should show different header style
  const isHomePage = pathname === "/";
  const isAuthPage = pathname.includes("/login");

  return (
    <>
      <div
        className={`${styles.headerSpacer} ${
          scrolled ? styles.headerScrolled : ""
        } ${isAuthPage ? styles.headerAuth : ""}`}
      >
        <div className={styles.headerContainer}>
          <Link href="/" className={styles.headerLogo_container}>
            <Image
              src="/logoDark-0.svg"
              alt="The Fringe Logo"
              width={80}
              height={80}
              priority
              className={styles.headerLogo}
            />
          </Link>

          <nav className={styles.headerNav} aria-label="Main navigation">
            <div className={styles.headerLinks}>
              <Link href="/book-now">
                <button
                  className={`${styles.headerCTA_button} ${styles.headerCTA_header}`}
                  aria-label="Book appointment"
                >
                  Book Now
                </button>
              </Link>
              <button
                className={styles.headerMenu_button}
                onClick={() => setNavOpen(true)}
                aria-label="Open navigation menu"
                aria-expanded={navOpen}
              >
                <i className="ri-menu-4-fill" aria-hidden="true"></i>
              </button>
            </div>
          </nav>
        </div>
      </div>

      {/* Navigation Pop Up Menu */}
      {navOpen && (
        <div className={styles.headerNav_popup}>
          {/* Navigation Pop Up Menu Left */}
          <div
            className={
              isClosing
                ? `${styles.headerNav_popup_left} ${styles.slideOutLeft}`
                : `${styles.headerNav_popup_left} ${styles.slideInLeft}`
            }
          >
            <div className={styles.headerNav_popup_left_content}>
              <div className={styles.headerNav_popup_left_content_top}>
                <h1>The</h1>
                <br />
                <p>
                  Fringe
                </p>
              </div>
            </div>
          </div>
          {/* Navigation Pop Up Menu Right */}
          <div
            className={
              isClosing
                ? `${styles.headerNav_popup_right} ${styles.slideOutRight}`
                : `${styles.headerNav_popup_right} ${styles.slideInRight}`
            }
          >
            {/* Navigation Pop Up Menu Right Div's Top Left Corner Logo */}
            <div className={styles.headerNav_popup_logo_container}>
              <Link href="/">
                <Image
                  src="/logo.svg"
                  alt="The Fringe Logo"
                  width={100}
                  height={100}
                />
              </Link>
            </div>
            {/* Navigation Pop Up Menu Close Button */}
            <div className={styles.headerNav_popup_close_container}>
              <button
                className={styles.headerNav_popup_close_button}
                onClick={handleClose}
                aria-label="Close navigation"
              >
                Close
              </button>
            </div>

            {/* Navigation Pop Up Menu Links */}
            <div className={styles.headerNav_popup_menu_container}>
              <div
                style={{ height: "600px", width: "100%", position: "relative" }}
              >
                <FlowingMenu items={navigationItems} />
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

src/services/settings.service.ts.content:
import { fetchApi } from '@/lib/api';

export interface SiteSettings {
  id: string;
  showPrices: boolean;
  siteName: string;
  contactEmail: string;
  contactPhone: string;
  address: string;
  socialMedia: {
    facebook?: string;
    instagram?: string;
    twitter?: string;
  };
  updatedAt: string;
}

// Mock settings for fallback
const mockSettings: SiteSettings = {
  id: '1',
  showPrices: true,
  siteName: 'The Fringe Beauty Academy',
  contactEmail: 'info@thefringe.com',
  contactPhone: '+1 (555) 123-4567',
  address: '123 Beauty Street, City, State 12345',
  socialMedia: {
    facebook: 'https://facebook.com/thefringe',
    instagram: 'https://instagram.com/thefringe',
    twitter: 'https://twitter.com/thefringe'
  },
  updatedAt: new Date().toISOString()
};

export const settingsService = {
  async getSettings(): Promise<SiteSettings> {
    try {
      return await fetchApi<SiteSettings>('/settings');
    } catch (error) {
      console.log('Falling back to mock settings', error);
      return mockSettings;
    }
  },

  async updateSettings(settings: Partial<SiteSettings>): Promise<SiteSettings> {
    try {
      return await fetchApi<SiteSettings>('/settings', {
        method: 'PUT',
        body: JSON.stringify(settings),
      });
    } catch (error) {
      console.log('Simulating settings update with mock data', error);
      return {
        ...mockSettings,
        ...settings,
        updatedAt: new Date().toISOString()
      };
    }
  },

  async togglePriceVisibility(): Promise<SiteSettings> {
    try {
      const currentSettings = await this.getSettings();
      return await this.updateSettings({
        showPrices: !currentSettings.showPrices
      });
    } catch (error) {
      console.log('Error toggling price visibility', error);
      throw error;
    }
  }
};

src/components/sections/Hero.module.css.content:
.hero {
    position: relative;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
    padding-top: 80px;
    margin-bottom: 0;
    box-sizing: border-box;
    font-family: var(--font-body);
    /* Account for fixed header */
}

.videoBackground {
    opacity: 0.4;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -2;
    background: var(--color-primary-bg) url('/video-poster.jpg') center/cover no-repeat;
    /* Fallback background with poster image */
}

.video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    opacity: 1;
    transition: opacity 1s ease-in-out;
}

.video.videoLoaded {
    opacity: 1;
}

.videoOverlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg,
            rgba(0, 0, 0, 0.4) 0%,
            rgba(0, 0, 0, 0.2) 50%,
            rgba(0, 0, 0, 0.3) 100%);
    z-index: -1;
}

.heroContent {
    position: relative;
    z-index: 1;
    text-align: center;
    max-width: 800px;
    padding: var(--spacing-2xl) 0 0 0;
}

.contentWrapper {
    animation: fadeIn 1s ease-out;
}

.heroTitle {
    font-size: var(--font-size-5xl);
    font-weight: 700;
    font-family: var(--font-heading);
    line-height: 1.2;
    margin-bottom: var(--spacing-xl);
    color: var(--color-text-primary);
    letter-spacing: -0.02em;
}

.heroDescription {
    font-size: var(--font-size-xl);
    font-family: var(--font-subheading);
    color: var(--color-text-secondary);
    line-height: 1.6;
    margin-bottom: var(--spacing-2xl);
    font-weight: 400;
    letter-spacing: 0.01em;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
    max-width: 600px;
}

.ctaContainer {
    display: flex;
    justify-content: center;
    gap: var(--spacing-lg);
    margin-bottom: var(--spacing-2xl);
}

.scrollIndicator {
    display: none;
}

.scrollIndicator:hover {
    animation-play-state: paused;
    transform: translateX(-50%) scale(1.05) translateY(-3px);
}

.scrollIndicator:hover .scrollText {
    color: var(--color-text-primary);
}

.scrollIndicator:hover .scrollMouse {
    border-color: var(--color-text-primary);
}

.scrollIndicator:hover .scrollWheel {
    background-color: var(--color-accent-primary);
}

.scrollMouse {
    width: 28px;
    height: 45px;
    border: 2px solid var(--color-text-primary);
    border-radius: 14px;
    position: relative;
    transition: all 0.3s ease;
}

.scrollWheel {
    width: 4px;
    height: 10px;
    background-color: var(--color-accent-primary);
    border-radius: 2px;
    position: absolute;
    top: 8px;
    left: 50%;
    transform: translateX(-50%);
    animation: scrollWheel 2s infinite;
    transition: all 0.3s ease;
}

.scrollText {
    font-size: var(--font-size-xs);
    font-family: var(--font-accent);
    color: var(--color-text-primary);
    text-transform: uppercase;
    letter-spacing: 1px;
    font-weight: 600;
    transition: all 0.3s ease;
}

/* Special Offer Modal */
.offerModal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 2000;
    animation: fadeIn 0.3s ease-out;
}

.offerContent {
    background: var(--color-card-bg);
    border: 1px solid var(--color-border);
    border-radius: var(--radius-xl);
    padding: var(--spacing-2xl);
    max-width: 500px;
    margin: var(--spacing-lg);
    text-align: center;
    position: relative;
    animation: slideUp 0.3s ease-out;
}

.closeButton {
    position: absolute;
    top: var(--spacing-md);
    right: var(--spacing-md);
    background: none;
    border: none;
    color: var(--color-text-primary);
    font-size: var(--font-size-2xl);
    cursor: pointer;
    width: 32px;
    height: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: var(--radius-md);
    transition: all var(--transition-normal);
}

.closeButton:hover {
    background-color: var(--color-tertiary-bg);
}

.offerTitle {
    font-size: var(--font-size-2xl);
    font-weight: 700;
    margin-bottom: var(--spacing-md);
    color: var(--color-text-primary);
}

.offerDescription {
    font-size: var(--font-size-lg);
    color: var(--color-text-secondary);
    margin-bottom: var(--spacing-xl);
    line-height: 1.6;
}

.offerActions {
    display: flex;
    gap: var(--spacing-md);
    justify-content: center;
}

.offerToggle {
    position: fixed;
    bottom: var(--spacing-xl);
    right: var(--spacing-xl);
    background: #D0B49F;
    color: #000000;
    border: 2px solid rgba(255, 255, 255, 0.2);
    width: 60px;
    height: 60px;
    border-radius: 20px;
    font-size: var(--font-size-xl);
    cursor: pointer;
    transition: all var(--transition-normal);
    z-index: 1500;
    display: flex;
    align-items: center;
    justify-content: center;
}

.offerToggle:hover {
    transform: scale(1.1) translateY(-2px);
    background: #DBC4A7;
    border-color: rgba(255, 255, 255, 0.3);
}

/* Animations */
@keyframes bounce {

    0%,
    20%,
    50%,
    80%,
    100% {
        transform: translateX(-50%) translateY(0);
    }

    40% {
        transform: translateX(-50%) translateY(-10px);
    }

    60% {
        transform: translateX(-50%) translateY(-5px);
    }
}

@keyframes scrollWheel {
    0% {
        opacity: 1;
        top: 6px;
    }

    50% {
        opacity: 0.5;
        top: 12px;
    }

    100% {
        opacity: 0;
        top: 18px;
    }
}

@keyframes fadeIn {
    from {
        opacity: 0;
    }

    to {
        opacity: 1;
    }
}

@keyframes slideUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* ==============================================
   RESPONSIVE DESIGN
   ============================================== */

/* Extra Large Desktop (â‰¥1440px) */
@media (min-width: 1440px) {
    .hero {
        padding-top: 85px;
    }

    .heroTitle {
        font-size: 4.5rem;
        line-height: 1.1;
        margin-bottom: 2rem;
    }

    .heroDescription {
        font-size: 1.4rem;
        line-height: 1.7;
        margin-bottom: 3rem;
        max-width: 650px;
    }

    .scrollIndicator {
        bottom: 40px;
        padding: 16px 20px;
        border-radius: 25px;
    }

    .scrollMouse {
        width: 28px;
        height: 45px;
        border-radius: 14px;
    }

    .scrollWheel {
        width: 4px;
        height: 10px;
        top: 8px;
        border-radius: 2px;
    }

    .scrollText {
        font-size: 0.9rem;
        letter-spacing: 1px;
        margin-top: 12px;
    }
}

/* Large Desktop (1200px - 1439px) */
@media (min-width: 1200px) and (max-width: 1439px) {
    .hero {
        padding-top: 80px;
    }

    .heroTitle {
        font-size: 4.2rem;
        line-height: 1.15;
        margin-bottom: 1.8rem;
    }

    .heroDescription {
        font-size: 1.3rem;
        line-height: 1.65;
        margin-bottom: 2.8rem;
        max-width: 620px;
    }

    .scrollIndicator {
        bottom: 35px;
        padding: 15px 18px;
        border-radius: 24px;
    }

    .scrollMouse {
        width: 26px;
        height: 42px;
        border-radius: 13px;
    }

    .scrollWheel {
        width: 3.5px;
        height: 9px;
        top: 7px;
    }

    .scrollText {
        font-size: 0.85rem;
        letter-spacing: 0.8px;
        margin-top: 10px;
    }
}

/* Medium Desktop (992px - 1199px) */
@media (min-width: 992px) and (max-width: 1199px) {
    .hero {
        padding-top: 75px;
    }

    .heroTitle {
        font-size: 3.8rem;
        line-height: 1.2;
        margin-bottom: 1.6rem;
    }

    .heroDescription {
        font-size: 1.25rem;
        line-height: 1.6;
        margin-bottom: 2.5rem;
        max-width: 580px;
    }

    .scrollIndicator {
        bottom: 32px;
        padding: 14px 17px;
        border-radius: 22px;
    }

    .scrollMouse {
        width: 25px;
        height: 40px;
        border-radius: 12px;
    }

    .scrollWheel {
        width: 3px;
        height: 8px;
        top: 6px;
    }

    .scrollText {
        font-size: 0.8rem;
        letter-spacing: 0.6px;
    }
}

/* Small Desktop / Large Tablet (769px - 991px) */
@media (min-width: 769px) and (max-width: 991px) {
    .hero {
        padding-top: 70px;
    }

    .heroTitle {
        font-size: 3.4rem;
        line-height: 1.25;
        margin-bottom: 1.4rem;
    }

    .heroDescription {
        font-size: 1.2rem;
        line-height: 1.55;
        margin-bottom: 2.2rem;
        max-width: 540px;
    }

    .ctaContainer {
        gap: var(--spacing-lg);
    }

    .scrollIndicator {
        bottom: 30px;
        padding: 13px 16px;
        border-radius: 21px;
    }

    .scrollMouse {
        width: 24px;
        height: 38px;
        border-radius: 12px;
    }

    .scrollWheel {
        width: 3px;
        height: 7px;
        top: 6px;
    }

    .scrollText {
        font-size: 0.75rem;
        letter-spacing: 0.4px;
    }
}

/* Mobile Landscape (481px - 768px) */
@media (min-width: 481px) and (max-width: 768px) {
    .hero {
        padding-top: 65px;
    }

    .heroTitle {
        font-size: 2.8rem;
        line-height: 1.3;
        margin-bottom: 1.2rem;
    }

    .heroDescription {
        font-size: 1.1rem;
        line-height: 1.5;
        margin-bottom: 2rem;
        max-width: 480px;
    }

    /* Optimize video for mobile */
    .video {
        object-position: center center;
    }

    .videoOverlay {
        background: linear-gradient(135deg,
                rgba(0, 0, 0, 0.7) 0%,
                rgba(0, 0, 0, 0.4) 50%,
                rgba(0, 0, 0, 0.6) 100%);
    }

    .ctaContainer {
        flex-direction: column;
        align-items: center;
        gap: var(--spacing-md);
    }

    .scrollIndicator {
        bottom: 25px;
        padding: 12px 16px;
        border-radius: 20px;
    }

    .scrollMouse {
        width: 24px;
        height: 40px;
    }

    .scrollWheel {
        height: 8px;
        top: 6px;
    }

    .scrollText {
        font-size: 0.7rem;
    }

    .offerActions {
        flex-direction: column;
    }

    .offerToggle {
        bottom: var(--spacing-lg);
        right: var(--spacing-lg);
        width: 50px;
        height: 50px;
        font-size: var(--font-size-lg);
    }
}

/* Mobile Portrait (320px - 480px) */
@media (min-width: 320px) and (max-width: 480px) {
    .hero {
        padding-top: 60px;
    }

    .heroTitle {
        font-size: 2.4rem;
        line-height: 1.35;
        margin-bottom: 1rem;
    }

    .heroDescription {
        font-size: 1rem;
        line-height: 1.45;
        margin-bottom: 1.8rem;
        max-width: 420px;
    }

    .ctaContainer {
        flex-direction: column;
        align-items: center;
        gap: var(--spacing-sm);
    }

    .scrollIndicator {
        bottom: 20px;
        padding: 10px 14px;
        border-radius: 18px;
    }

    .scrollMouse {
        width: 20px;
        height: 35px;
        border-radius: 10px;
    }

    .scrollWheel {
        width: 3px;
        height: 6px;
        top: 5px;
    }

    .scrollText {
        font-size: 0.65rem;
        letter-spacing: 0.5px;
    }

    .offerContent {
        margin: var(--spacing-sm);
        padding: var(--spacing-xl);
    }

    .offerToggle {
        width: 45px;
        height: 45px;
        bottom: var(--spacing-md);
        right: var(--spacing-md);
    }
}

/* Extra Small Mobile (â‰¤320px) */
@media (max-width: 320px) {
    .hero {
        padding-top: 55px;
    }

    .heroTitle {
        font-size: 2rem;
        line-height: 1.4;
        margin-bottom: 0.8rem;
    }

    .heroDescription {
        font-size: 0.9rem;
        line-height: 1.4;
        margin-bottom: 1.5rem;
        max-width: 280px;
    }

    .ctaContainer {
        flex-direction: column;
        align-items: center;
        gap: 12px;
    }

    .scrollIndicator {
        bottom: 15px;
        padding: 8px 12px;
        border-radius: 16px;
    }

    .scrollMouse {
        width: 18px;
        height: 32px;
        border-radius: 9px;
    }

    .scrollWheel {
        width: 2.5px;
        height: 5px;
        top: 4px;
    }

    .scrollText {
        font-size: 0.6rem;
        letter-spacing: 0.3px;
    }

    .offerToggle {
        width: 40px;
        height: 40px;
        bottom: 12px;
        right: 12px;
        font-size: 0.9rem;
    }
}

/* Reduce motion for accessibility */
@media (prefers-reduced-motion: reduce) {
    .contentWrapper {
        animation: none;
    }

    .scrollIndicator {
        animation: none;
    }

    .scrollWheel {
        animation: none;
    }

    .offerToggle:hover {
        transform: none;
    }
}

src/app/student/my-course/MyCourse.modul.content:
.myCoursePage {
    padding: var(--spacing-xl);
    min-height: 100vh;
}

.loading {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 50vh;
    font-size: var(--font-size-lg);
    color: var(--color-text-secondary);
}

.noCourse {
    text-align: center;
    padding: var(--spacing-3xl);
}

.noCourse h2 {
    font-size: var(--font-size-3xl);
    margin-bottom: var(--spacing-lg);
    color: var(--color-text-primary);
}

.courseHeader {
    margin-bottom: var(--spacing-3xl);
}

.courseTitle {
    font-size: var(--font-size-3xl);
    font-weight: 700;
    margin-bottom: var(--spacing-md);
    color: var(--color-text-primary);
}

.courseDescription {
    font-size: var(--font-size-lg);
    color: var(--color-text-secondary);
    margin-bottom: var(--spacing-xl);
    line-height: 1.6;
}

.progressOverview {
    display: flex;
    align-items: center;
    gap: var(--spacing-lg);
}

.progressBar {
    flex: 1;
    max-width: 300px;
    height: 8px;
    background: var(--color-border);
    border-radius: var(--radius-full);
    overflow: hidden;
}

.progressFill {
    height: 100%;
    background: linear-gradient(90deg, var(--color-text-primary) 0%, var(--color-text-secondary) 100%);
    transition: width var(--transition-normal);
}

.progressText {
    font-weight: 600;
    color: var(--color-text-primary);
}

.courseContent {
    display: grid;
    grid-template-columns: 350px 1fr;
    gap: var(--spacing-2xl);
}

.modulesList {
    background: var(--color-card-bg);
    border-radius: var(--radius-lg);
    border: 1px solid var(--color-card-border);
    padding: var(--spacing-xl);
    height: fit-content;
}

.modulesList h2 {
    font-size: var(--font-size-xl);
    margin-bottom: var(--spacing-lg);
    color: var(--color-text-primary);
}

.moduleItem {
    display: flex;
    align-items: center;
    gap: var(--spacing-md);
    padding: var(--spacing-md);
    border-radius: var(--radius-md);
    cursor: pointer;
    transition: all var(--transition-normal);
    border: 2px solid transparent;
    margin-bottom: var(--spacing-sm);
}

.moduleItem:hover {
    background: var(--color-tertiary-bg);
    border-color: var(--color-border-hover);
}

.moduleItem.active {
    background: var(--color-tertiary-bg);
    border-color: var(--color-text-primary);
}

.moduleItem.completed {
    opacity: 0.7;
}

.moduleNumber {
    width: 32px;
    height: 32px;
    border-radius: var(--radius-full);
    background: var(--color-border);
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    font-size: var(--font-size-sm);
    color: var(--color-text-primary);
    flex-shrink: 0;
}

.moduleItem.active .moduleNumber {
    background: var(--color-text-primary);
    color: var(--color-primary-bg);
}

.moduleContent h3 {
    font-size: var(--font-size-base);
    font-weight: 500;
    margin-bottom: var(--spacing-xs);
    color: var(--color-text-primary);
}

.moduleContent p {
    font-size: var(--font-size-sm);
    color: var(--color-text-muted);
}

.moduleStatus {
    font-size: var(--font-size-lg);
    color: var(--color-text-secondary);
}

.moduleItem.completed .moduleStatus {
    color: #10b981;
}

.videoSection {
    background: var(--color-card-bg);
    border-radius: var(--radius-lg);
    border: 1px solid var(--color-card-border);
    padding: var(--spacing-xl);
}

.videoSection h2 {
    font-size: var(--font-size-2xl);
    margin-bottom: var(--spacing-md);
    color: var(--color-text-primary);
}

.moduleDescription {
    font-size: var(--font-size-base);
    color: var(--color-text-secondary);
    margin-bottom: var(--spacing-xl);
    line-height: 1.6;
}

.textContent {
    text-align: center;
    padding: var(--spacing-2xl);
}

.textContent h3 {
    font-size: var(--font-size-xl);
    margin-bottom: var(--spacing-lg);
    color: var(--color-text-primary);
}

.contentText {
    font-size: var(--font-size-base);
    color: var(--color-text-secondary);
    line-height: 1.7;
    white-space: pre-wrap;
}

.selectModule {
    text-align: center;
    padding: var(--spacing-3xl);
}

.selectModule h3 {
    font-size: var(--font-size-xl);
    margin-bottom: var(--spacing-md);
    color: var(--color-text-primary);
}

@media (max-width: 1024px) {
    .courseContent {
