"use client";

import { useState, useEffect } from "react";
import Layout from "../../components/layout/Layout";
import styles from "./Services.module.css";
import { servicesService } from "@/services/services.service";
import { settingsService } from "@/services/settings.service";
import type { Service } from "@/services/services.service";
import { mockServices as fallbackServices } from "@/services/mockData";
import { useAuth } from "@/hooks/useAuth";

// Use central mock data and derive categories dynamically so UI updates when data changes
const serviceCategoriesFromData = (() => {
  const cats = Array.from(new Set(fallbackServices.map((s) => s.category)));
  const format = (c: string) =>
    c
      .toLowerCase()
      .replace(/[_-]/g, " ")
      .split(" ")
      .map((w) => w.charAt(0).toUpperCase() + w.slice(1))
      .join(" ");

  return [
    { id: "ALL", name: "All Services" },
    ...cats.map((c) => ({ id: c, name: format(c) })),
  ];
})();

export default function ServicesPage() {
  const [selectedCategories, setSelectedCategories] = useState<string[]>([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [services, setServices] = useState<Service[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const { user, isAuthenticated } = useAuth();
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [showPrices, setShowPrices] = useState(true);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const dropdown = document.querySelector('.filter-dropdown');
      if (dropdown && !dropdown.contains(event.target as Node)) {
        setDropdownOpen(false);
      }
    };

    if (dropdownOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [dropdownOpen]);

  // Handle URL parameters for category filtering
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const categoryParam = urlParams.get('category');

    if (categoryParam) {
      // Check if the category exists in our categories
      const categoryExists = serviceCategoriesFromData.some(cat => cat.id === categoryParam);
      if (categoryExists) {
        setSelectedCategories([categoryParam]);
      }
    }
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [servicesData, settings] = await Promise.all([
          servicesService.getAllServices(),
          settingsService.getSettings()
        ]);

        setShowPrices(settings.showPrices);

        if (servicesData && servicesData.length > 0) {
          setServices(servicesData);
        } else {
          console.log("No services found in API, using mock data");
          setServices(fallbackServices);
        }
      } catch (error) {
        console.log("Failed to fetch from API, using mock data");
        setServices(fallbackServices);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleBooking = (serviceId: string) => {
    // Redirect to the static book-now page
    window.location.href = "/book-now";
  };

  const filteredServices = services.filter((service) => {
    const matchesCategory =
      selectedCategories.length === 0 ||
      selectedCategories.includes("ALL") ||
      selectedCategories.includes(service.category);

    const matchesSearch =
      service.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      service.description.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesCategory && matchesSearch;
  });

  if (loading) {
    return (
      <Layout>
        <section className={styles.servicesPage}>
          <div className={styles.container}>
            <div className={styles.loadingContainer}>
              <div className={styles.loader}>Loading...</div>
            </div>
          </div>
        </section>
      </Layout>
    );
  }

  return (
    <Layout>
      <section className={styles.servicesPage}>
        <div className={styles.container}>
          {/* Hero Section */}
          <div className={styles.hero}>
            <h1 className={styles.pageTitle}>Our Services</h1>
            <p className={styles.pageSubtitle}>
              Discover our comprehensive range of premium beauty services
            </p>
          </div>

          {/* Filters */}
          <div className={styles.filters}>
            <div className={styles.searchContainer}>
              <input
                type="text"
                placeholder="Search services..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className={styles.searchInput}
              />
            </div>

            <div className={styles.categoryFilters}>
              {/* Multi-select dropdown trigger */}
              <div
                className={`${styles.filterDropdown} ${
                  dropdownOpen ? styles.open : ""
                } filter-dropdown`}
              >
                <button
                  className={`btn ${styles.filterButton}`}
                  onClick={() => setDropdownOpen((s) => !s)}
                  aria-haspopup="menu"
                  aria-expanded={dropdownOpen}
                >
                  {selectedCategories.length === 0
                    ? "Filter categories"
                    : `${selectedCategories.length} selected`}
                </button>

                {dropdownOpen && (
                  <div className={styles.dropdownMenu} role="menu">
                    <div className={styles.dropdownHeader}>
                      <button
                        className="btn btn-secondary"
                        onClick={() => setSelectedCategories(["ALL"])}
                      >
                        All
                      </button>
                      <button
                        className="btn btn-secondary"
                        onClick={() => setSelectedCategories([])}
                      >
                        Clear
                      </button>
                    </div>

                    <ul className={styles.dropdownList}>
                      {serviceCategoriesFromData.map((c) => (
                        <li key={c.id}>
                          <label>
                            <input
                              type="checkbox"
                              checked={
                                selectedCategories.includes("ALL") ||
                                selectedCategories.includes(c.id)
                              }
                              onChange={(e) => {
                                const checked = e.target.checked;
                                setSelectedCategories((prev) => {
                                  if (checked) {
                                    // add
                                    return Array.from(new Set([...prev, c.id]));
                                  }
                                  // remove
                                  return prev.filter(
                                    (x) => x !== c.id && x !== "ALL"
                                  );
                                });
                              }}
                            />
                            <span className={styles.dropdownLabelText}>
                              {c.name}
                            </span>
                          </label>
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Services Grid */}
          <div className={styles.servicesGrid}>
            {filteredServices.map((service) => (
              <div key={service.id} className={styles.serviceCard}>
                <div className={styles.serviceImage}>
                  <img src={service.image} alt={service.title} />
                  <div className={styles.serviceOverlay}>
                    <button
                      onClick={() => handleBooking(service.id)}
                      className="btn btn-primary"
                      disabled={!isAuthenticated}
                    >
                      {isAuthenticated ? "Book Now" : "Login to Book"}
                    </button>
                  </div>
                </div>

                <div className={styles.serviceContent}>
                  <h3 className={styles.serviceTitle}>{service.title}</h3>
                  <p className={styles.serviceDescription}>
                    {service.description}
                  </p>

                  <div className={styles.serviceDetails}>
                    <div className={styles.serviceMeta}>
                      <span className={styles.duration}>
                        â± {service.duration}
                      </span>
                      {showPrices ? (
                        <span className={styles.price}>${service.price}</span>
                      ) : (
                        <span className={styles.contactPrice}>Contact for pricing</span>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}

            {filteredServices.length === 0 && (
              <div className={styles.noResults}>
                <h3>No services found</h3>
                <p>Try adjusting your search or filter criteria</p>
              </div>
            )}
          </div>

          {/* CTA Section */}
          <div className={styles.ctaSection}>
            <h2 className={styles.ctaTitle}>Ready to Book Your Appointment?</h2>
            <p className={styles.ctaDescription}>
              Our expert stylists are ready to transform your look
            </p>
            <div className={styles.ctaButtons}>
              <a href="/book-now" className="btn btn-primary btn-lg">
                Book Appointment
              </a>
              <a href="/contact" className="btn btn-secondary btn-lg">
                Contact Us
              </a>
            </div>
          </div>
        </div>
      </section>
    </Layout>
  );
}

src/app/student/certificate/page.tsx.content:
"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import StudentLayout from "../../../components/layout/StudentLayout";
import styles from "./Certificate.module.css";

export default function CertificatePage() {
  const [student, setStudent] = useState<any>(null);
  const [course, setCourse] = useState<any>(null);
  const [progress, setProgress] = useState<any>(null);
  const [isLoading, setIsLoading] = useState(true);
  const router = useRouter();

  useEffect(() => {
    const studentSession = localStorage.getItem("student_session");
    if (!studentSession) {
      router.push("/student/login");
      return;
    }
    fetchStudentData();
  }, []);

  const fetchStudentData = async () => {
    try {
      const response = await fetch("/api/student/dashboard");
      const data = await response.json();
      if (response.ok) {
        setStudent(data.student);
        setCourse(data.course);
        setProgress(data.progress);
      }
    } catch (error) {
      console.error("Failed to fetch student data:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const generateCertificate = async () => {
    if (progress?.progressPercent >= 100) {
      alert(
        "Certificate generated! In a real application, this would create a downloadable PDF certificate."
      );
    } else {
      alert(
        "You must complete 100% of the course to generate your certificate."
      );
    }
  };

  if (isLoading) {
    return (
      <StudentLayout>
        <div className={styles.loading}>Loading certificate information...</div>
      </StudentLayout>
    );
  }

  const isEligible = progress?.progressPercent >= 100;

  return (
    <StudentLayout>
      <div className={styles.certificatePage}>
        <div className={styles.header}>
          <h1 className={styles.title}>My Certificate</h1>
          <p className={styles.subtitle}>
            Complete your course to earn your professional certificate
          </p>
        </div>

        <div className={styles.certificateContainer}>
          {isEligible ? (
            <div className={styles.certificateCard}>
              <div className={styles.certificatePreview}>
                <div className={styles.certificateHeader}>
                  <h2>Certificate of Completion</h2>
                  <div className={styles.logo}>The Fringe Academy</div>
                </div>

                <div className={styles.certificateBody}>
                  <p className={styles.certificateText}>
                    This is to certify that
                  </p>
                  <h3 className={styles.studentName}>
                    {student?.name || "Student Name"}
                  </h3>
                  <p className={styles.certificateText}>
                    has successfully completed the course
                  </p>
                  <h4 className={styles.courseName}>{course?.title}</h4>
                  <p className={styles.completionDate}>
                    Completed on {new Date().toLocaleDateString()}
                  </p>
                </div>

                <div className={styles.certificateFooter}>
                  <div className={styles.signature}>
                    <div className={styles.signatureLine}></div>
                    <p>Director, The Fringe Academy</p>
                  </div>
                </div>
              </div>

              <div className={styles.certificateActions}>
                <button
                  onClick={generateCertificate}
                  className="btn btn-primary btn-lg"
                >
                  Download Certificate
                </button>
                <button
                  onClick={() => window.print()}
                  className="btn btn-secondary btn-lg"
                >
                  Print Certificate
                </button>
              </div>
            </div>
          ) : (
            <div className={styles.progressCard}>
              <div className={styles.progressHeader}>
                <h2>Course Progress</h2>
                <p>Complete your course to unlock your certificate</p>
              </div>

              <div className={styles.progressDetails}>
                <div className={styles.progressCircle}>
                  <div
                    className={styles.progressRing}
                    style={{
                      background: `conic-gradient(var(--color-text-primary) ${
                        (progress?.progressPercent || 0) * 3.6
                      }deg, var(--color-border) 0deg)`,
                    }}
                  >
                    <div className={styles.progressInner}>
                      <span className={styles.progressPercent}>
                        {Math.round(progress?.progressPercent || 0)}%
                      </span>
                    </div>
                  </div>
                </div>

                <div className={styles.progressInfo}>
                  <h3>{course?.title}</h3>
                  <p>
                    {progress?.completedModules?.length || 0} of{" "}
                    {course?.modules?.length || 0} modules completed
                  </p>
                  <div className={styles.remainingModules}>
                    <p>
                      {Math.max(
                        0,
                        (course?.modules?.length || 0) -
                          (progress?.completedModules?.length || 0)
                      )}{" "}
                      modules remaining
                    </p>
                  </div>
                </div>
              </div>

              <div className={styles.encouragement}>
                <p>
                  Keep going! You're{" "}
                  {Math.round(progress?.progressPercent || 0)}% of the way
                  there.
                </p>
                <a href="/student/my-course" className="btn btn-primary">
                  Continue Learning
                </a>
              </div>
            </div>
          )}
        </div>
      </div>
    </StudentLayout>
  );
}

