}

.hero {
    text-align: center;
    margin-bottom: 80px;
    padding: 60px 0;
}

.pageTitle {
    font-size: clamp(2.5rem, 6vw, 4rem);
    font-weight: 800;
    color: #ffffff;
    margin-bottom: 24px;
    background: linear-gradient(135deg, #ffffff 0%, #D0B49F 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    line-height: 1.1;
}

.pageSubtitle {
    font-size: clamp(1.1rem, 3vw, 1.3rem);
    color: rgba(255, 255, 255, 0.8);
    max-width: 700px;
    margin: 0 auto;
    line-height: 1.5;
    font-weight: 400;
}

/* Filters / Search */
.filters {
    display: flex;
    gap: 24px;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 60px;
    flex-wrap: wrap;
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    padding: 24px;
    border-radius: 20px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    position: relative;
    z-index: 10;
}

.categoryFilters {
    display: flex;
    gap: 12px;
    align-items: center;
}

.filterDropdown {
    margin-left: auto;
    display: flex;
    align-items: center;
    position: relative;
}



.searchContainer {
    flex: 1;
    max-width: 400px;
}

.searchInput {
    width: 100%;
    padding: 16px 20px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 16px;
    background: rgba(255, 255, 255, 0.08);
    color: #ffffff;
    font-size: 1rem;
    font-weight: 500;
    transition: all 0.3s ease;
    backdrop-filter: blur(10px);
}

.searchInput::placeholder {
    color: rgba(255, 255, 255, 0.5);
}

.searchInput:focus {
    outline: none;
    border-color: #D0B49F;
    background: rgba(255, 255, 255, 0.12);
    box-shadow: 0 0 0 3px rgba(208, 180, 159, 0.2);
    transform: translateY(-2px);
}

/* Dropdown & multi-select parity with Services */
.filterButton {
    background: rgba(255, 255, 255, 0.08);
    color: #ffffff;
    border: 1px solid rgba(255, 255, 255, 0.2);
    padding: 16px 24px;
    border-radius: 16px;
    display: inline-flex;
    align-items: center;
    gap: 12px;
    cursor: pointer;
    font-weight: 600;
    font-size: 1rem;
    transition: all 0.3s ease;
    backdrop-filter: blur(10px);
    min-width: 180px;
    justify-content: space-between;
}

.filterButton:hover {
    background: rgba(255, 255, 255, 0.12);
    border-color: rgba(208, 180, 159, 0.5);
    transform: translateY(-2px);
    box-shadow: 0 8px 24px rgba(208, 180, 159, 0.2);
}

.filterButton::after {
    content: '';
    display: inline-block;
    width: 0;
    height: 0;
    border-left: 6px solid transparent;
    border-right: 6px solid transparent;
    border-top: 6px solid #ffffff;
    transform-origin: center;
    transition: transform 0.3s ease;
}

.filterButton:focus {
    outline: none;
    border-color: #D0B49F;
    box-shadow: 0 0 0 3px rgba(208, 180, 159, 0.3);
}

.filterDropdown.open .filterButton {
    background: rgba(208, 180, 159, 0.15);
    border-color: rgba(208, 180, 159, 0.5);
    box-shadow: 0 8px 24px rgba(208, 180, 159, 0.2);
}

.filterDropdown.open .filterButton::after {
    transform: rotate(180deg);
}

.dropdownMenu {
    position: absolute;
    top: calc(100% + 8px);
    right: 0;
    background: rgba(20, 20, 20, 0.98);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.15);
    border-radius: 12px;
    padding: 16px;
    z-index: 9999;
    min-width: 280px;
    max-width: 320px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);
    opacity: 0;
    visibility: hidden;
    transform: translateY(-10px);
    transition: all 0.2s ease;
}

.filterDropdown.open .dropdownMenu {
    opacity: 1;
    visibility: visible;
    transform: translateY(0);
}

.dropdownHeader {
    display: flex;
    gap: 8px;
    justify-content: space-between;
    margin-bottom: 12px;
    padding-bottom: 12px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.dropdownHeader button {
    padding: 6px 12px;
    border-radius: 8px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    background: rgba(255, 255, 255, 0.05);
    color: #ffffff;
    font-size: 0.85rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.2s ease;
    flex: 1;
}

.dropdownHeader button:hover {
    background: rgba(208, 180, 159, 0.2);
    border-color: rgba(208, 180, 159, 0.4);
}

.dropdownList {
    max-height: 200px;
    overflow-y: auto;
    list-style: none;
    padding: 0;
    margin: 0;
    scrollbar-width: thin;
}

.dropdownList::-webkit-scrollbar {
    width: 4px;
}

.dropdownList::-webkit-scrollbar-track {
    background: rgba(255, 255, 255, 0.05);
}

.dropdownList::-webkit-scrollbar-thumb {
    background: rgba(208, 180, 159, 0.3);
    border-radius: 2px;
}

.dropdownList li {
    padding: 0;
    margin-bottom: 2px;
    opacity: 1;
}

.dropdownList label {
    display: flex;
    align-items: center;
    gap: 10px;
    cursor: pointer;
    color: rgba(255, 255, 255, 0.8);
    padding: 8px 12px;
    border-radius: 8px;
    transition: all 0.2s ease;
    font-weight: 500;
    font-size: 0.9rem;
}

.dropdownList label:hover {
    background: rgba(255, 255, 255, 0.08);
    color: #ffffff;
}

.dropdownList input[type="checkbox"] {
    width: 16px;
    height: 16px;
    appearance: none;
    border: 2px solid rgba(255, 255, 255, 0.3);
    border-radius: 3px;
    background: transparent;
    cursor: pointer;
    transition: all 0.2s ease;
    position: relative;
    flex-shrink: 0;
}

.dropdownList input[type="checkbox"]:checked {
    background: linear-gradient(135deg, #D0B49F 0%, #B8A082 100%);
    border-color: #D0B49F;
}

.dropdownList input[type="checkbox"]:checked::after {
    content: 'âœ“';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: #1a1a1a;
    font-size: 10px;
    font-weight: bold;
}

.dropdownLabelText {
    display: inline-block;
    flex: 1;
}

.dropdownList input[type="checkbox"]:checked + .dropdownLabelText {
    color: #D0B49F;
    font-weight: 600;
}



/* Grid */
.coursesGrid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(380px, 1fr));
    gap: 32px;
    margin-bottom: 80px;
}

/* Card */
.courseCard {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    border-radius: 24px;
    overflow: hidden;
    transition: all 0.4s ease;
    position: relative;
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.2);
}

.courseCard::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, rgba(208, 180, 159, 0.5), transparent);
}

.courseCard:hover {
    transform: translateY(-12px) scale(1.02);
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.4);
    border-color: rgba(208, 180, 159, 0.3);
}

.courseImage {
    position: relative;
    width: 100%;
    height: 280px;
    overflow: hidden;
}

.courseImage img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.6s ease;
}

.courseCard:hover .courseImage img {
    transform: scale(1.15);
}

.courseBadge {
    position: absolute;
    top: 20px;
    right: 20px;
    background: linear-gradient(135deg, #D0B49F 0%, #B8A082 100%);
    color: #1a1a1a;
    padding: 8px 16px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    box-shadow: 0 4px 12px rgba(208, 180, 159, 0.3);
}

.courseContent {
    padding: 32px;
}

.courseTitle {
    font-size: 1.5rem;
    font-weight: 700;
    color: #ffffff;
    margin-bottom: 12px;
    line-height: 1.3;
}

.courseDescription {
    color: rgba(255, 255, 255, 0.7);
    margin-bottom: 24px;
    line-height: 1.6;
    font-size: 1rem;
}

.courseStats {
    display: flex;
    gap: 16px;
    margin-bottom: 24px;
    flex-wrap: wrap;
}

.courseStats span {
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.6);
    font-weight: 500;
    display: flex;
    align-items: center;
    gap: 6px;
}



.courseFooter {
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    padding-top: 20px;
}

.price {
    font-size: 1.3rem;
    font-weight: 700;
    color: #D0B49F;
    background: linear-gradient(135deg, #D0B49F 0%, #E6D3B7 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.courseActions {
    display: flex;
    gap: 12px;
}

/* Modal Styles */
.modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.9);
    backdrop-filter: blur(10px);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 2000;
    padding: 24px;
}

.modalContent {
    background: rgba(20, 20, 20, 0.98);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.15);
    border-radius: 24px;
    max-width: 900px;
    width: 100%;
    max-height: 90vh;
    overflow-y: auto;
    position: relative;
    animation: slideUp 0.3s ease-out;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
}

.closeButton {
    position: absolute;
    top: 20px;
    right: 20px;
    background: rgba(255, 255, 255, 0.1);
    border: none;
    color: #ffffff;
    font-size: 1.5rem;
    cursor: pointer;
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    transition: all 0.3s ease;
    z-index: 1;
    backdrop-filter: blur(10px);
}

.closeButton:hover {
    background: rgba(208, 180, 159, 0.2);
    transform: scale(1.1);
}

.modalHeader {
    display: flex;
    gap: 24px;
    padding: 40px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.modalHeader img {
    width: 140px;
    height: 100px;
    object-fit: cover;
    border-radius: 16px;
    flex-shrink: 0;
}

.modalTitle h2 {
    font-size: 2rem;
    font-weight: 700;
    color: #ffffff;
    margin-bottom: 12px;
    background: linear-gradient(135deg, #ffffff 0%, #D0B49F 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.modalTitle p {
    color: rgba(255, 255, 255, 0.7);
    line-height: 1.6;
    font-size: 1.1rem;
}

.modalBody {
    padding: 40px;
}

.modalSection {
    margin-bottom: 32px;
}

.modalSection h3 {
    font-size: 1.3rem;
    font-weight: 600;
    color: #ffffff;
    margin-bottom: 16px;
}

.modalSection ul {
    list-style: none;
    padding: 0;
    margin: 0;
}

.modalSection li {
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 12px;
    padding-left: 24px;
    position: relative;
    font-size: 1rem;
    line-height: 1.5;
}

.modalSection li::before {
    content: 'âœ“';
    position: absolute;
    left: 0;
    color: #D0B49F;
    font-weight: bold;
    font-size: 1.1rem;
}

.courseDetails {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 16px;
    margin-top: 24px;
    background: rgba(255, 255, 255, 0.05);
    padding: 20px;
    border-radius: 16px;
}

.detailItem {
    color: rgba(255, 255, 255, 0.7);
    font-size: 0.95rem;
    line-height: 1.4;
}

.detailItem strong {
    color: #D0B49F;
    font-weight: 600;
}

.modalFooter {
    padding: 32px 40px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    text-align: center;
}

@keyframes slideUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Loading and Error States */
.loadingContainer,
.errorContainer {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    min-height: 60vh;
    text-align: center;
}

.loader {
    color: #D0B49F;
    font-size: 1.2rem;
    font-weight: 600;
}

.error {
    color: #ff6b6b;
    font-size: 1.1rem;
    margin-bottom: 20px;
}

.retryButton {
    background: linear-gradient(135deg, #D0B49F 0%, #B8A082 100%);
    color: #1a1a1a;
    border: none;
    padding: 12px 24px;
    border-radius: 12px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
}

.retryButton:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 24px rgba(208, 180, 159, 0.3);
}

/* Mobile: make dropdown full width under filters */
@media (max-width: 768px) {
    .filterDropdown {
        width: 100%;
        margin-left: 0;
        position: relative;
    }

    .dropdownMenu {
        position: absolute;
        top: calc(100% + 8px);
        left: 0;
        right: 0;
        width: 100%;
        margin-top: 0;
        border-radius: 12px;
        min-width: auto;
        max-width: none;
        z-index: 9999;
    }

    .filterDropdown:not(.open) .dropdownMenu {
        opacity: 0;
        visibility: hidden;
        transform: translateY(-10px);
    }

    .filterDropdown.open .dropdownMenu {
        opacity: 1;
        visibility: visible;
        transform: translateY(0);
    }
}

/* Mobile Responsiveness */
@media (max-width: 1024px) {
    .coursesGrid {
        grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
        gap: 24px;
    }
}

@media (max-width: 768px) {
    .container {
        padding: 0 20px;
    }

    .hero {
        padding: 40px 0;
        margin-bottom: 60px;
    }

    .filters {
        flex-direction: column;
        gap: 20px;
        padding: 20px;
        align-items: stretch;
    }

    .searchContainer {
        max-width: 100%;
    }

    .filterDropdown {
        width: 100%;
    }

    .filterButton {
        width: 100%;
        justify-content: space-between;
    }

    .coursesGrid {
        grid-template-columns: 1fr;
        gap: 20px;
        margin-bottom: 60px;
    }

    .courseCard {
        max-width: 100%;
    }

    .courseImage {
        height: 240px;
    }

    .courseContent {
        padding: 24px;
    }

    .courseActions {
        flex-direction: column;
        gap: 8px;
    }

    .modalHeader {
        flex-direction: column;
        text-align: center;
        padding: 30px;
    }

    .modalHeader img {
        width: 100%;
        height: 200px;
    }

    .modalBody {
        padding: 30px;
    }

    .modalFooter {
        padding: 24px 30px;
    }
}

@media (max-width: 480px) {
    .container {
        padding: 0 16px;
    }

    .hero {
        padding: 30px 0;
        margin-bottom: 40px;
    }

    .filters {
        padding: 16px;
        border-radius: 16px;
    }

    .searchInput {
        padding: 14px 18px;
        font-size: 0.95rem;
    }

    .filterButton {
        padding: 14px 20px;
        font-size: 0.95rem;
    }

    .coursesGrid {
        gap: 16px;
        margin-bottom: 40px;
    }

    .courseImage {
        height: 200px;
    }

    .courseContent {
        padding: 20px;
    }

    .courseTitle {
        font-size: 1.3rem;
    }

    .courseDescription {
        font-size: 0.95rem;
    }

    .courseStats {
        gap: 12px;
    }

    .courseStats span {
        font-size: 0.85rem;
    }

    .modalHeader {
        padding: 20px;
    }

    .modalBody {
        padding: 20px;
    }

    .modalFooter {
        padding: 20px;
    }

    .courseDetails {
        grid-template-columns: 1fr;
        gap: 12px;
        padding: 16px;
    }
}

src/app/admin/certificates/page.tsx.content:
"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import AdminLayout from "../../../components/layout/AdminLayout";
import LoadingSpinner, { LoadingOverlay } from "../../../components/ui/LoadingSpinner";
import { uploadFile, apiCall, API_CONFIG } from "../../../config/api";
import styles from "./Certificates.module.css";

interface Certificate {
  id: string;
  studentId: string;
  studentName: string;
  studentEmail: string;
  courseName: string;
  completionDate: string;
  status: "issued" | "pending" | "revoked";
  certificateUrl?: string;
  generatedBy?: string;
  generatedAt?: string;
}

interface Student {
  id: string;
  name: string;
  email: string;
  course: string;
  progress: number;
  status: string;
}

export default function CertificatesPage() {
  const [certificates, setCertificates] = useState<Certificate[]>([]);
  const [students, setStudents] = useState<Student[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [showGenerateModal, setShowGenerateModal] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState<Student | null>(null);
  const [filterStatus, setFilterStatus] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const [isUploading, setIsUploading] = useState(false);
  const [certificateFile, setCertificateFile] = useState<File | null>(null);
  const [uploadMessage, setUploadMessage] = useState("");
  const [isDragOver, setIsDragOver] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const router = useRouter();

  useEffect(() => {
    const adminSession = localStorage.getItem("admin_session");
    if (!adminSession) {
      router.push("/admin/login");
      return;
    }
    fetchCertificates();
  }, []);

  const fetchCertificates = async () => {
    try {
      // Use external API endpoints
      const [certsData, studentsData] = await Promise.all([
        apiCall(API_CONFIG.ENDPOINTS.ADMIN.CERTIFICATES.LIST).catch(() => ({ certificates: [] })),
        apiCall(API_CONFIG.ENDPOINTS.ADMIN.STUDENTS.LIST).catch(() => ({ students: [] }))
      ]);

      setCertificates(certsData.certificates || []);
      
      // Filter students who are eligible for certificates (completed courses)
      const eligibleStudents = (studentsData.students || []).filter(
        (student: Student) => student.progress >= 100
      );
      setStudents(eligibleStudents);
      
    } catch (error) {
      console.error("Failed to fetch data:", error);
      // Set some mock data for development
      setStudents([
        {
          id: "1",
          name: "John Doe",
          email: "john@example.com",
          course: "Hair Styling Fundamentals",
          progress: 100,
          status: "completed"
        },
        {
          id: "2", 
          name: "Jane Smith",
          email: "jane@example.com",
          course: "Makeup Artistry",
          progress: 100,
          status: "completed"
        }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleUploadCertificate = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedStudent || !certificateFile) {
      setUploadMessage("Error: Please select a student and choose a certificate file.");
      setTimeout(() => setUploadMessage(""), 5000);
      return;
    }

    setIsUploading(true);
    setUploadProgress(0);
    
    try {
      const formData = new FormData();
      formData.append("certificate", certificateFile);
      formData.append("studentId", selectedStudent.id);
      formData.append("studentName", selectedStudent.name);
      formData.append("courseName", selectedStudent.course);

      const response = await uploadFile(
        API_CONFIG.ENDPOINTS.ADMIN.CERTIFICATES.UPLOAD,
        formData,
        (progress) => setUploadProgress(progress)
      );

      // Success
      fetchCertificates();
      setShowGenerateModal(false);
      setSelectedStudent(null);
      setCertificateFile(null);
      setUploadProgress(0);
      setUploadMessage("Certificate uploaded successfully!");
      setTimeout(() => setUploadMessage(""), 5000);
      
    } catch (error: any) {
      setUploadProgress(0);
      setUploadMessage(`Error: ${error.message || "Failed to upload certificate"}`);
      setTimeout(() => setUploadMessage(""), 5000);
    } finally {
      setIsUploading(false);
    }
  };

  const handleRevokeCertificate = async (certificateId: string) => {
    if (confirm("Are you sure you want to revoke this certificate?")) {
      try {
        const response = await fetch(
          `/api/admin/certificates/${certificateId}/revoke`,
          {
            method: "POST",
          }
        );

        if (response.ok) {
          fetchCertificates();
          alert("Certificate revoked successfully!");
        }
      } catch (error) {
        alert("Error revoking certificate");
      }
    }
  };

  const filteredCertificates = certificates.filter((cert) => {
    const matchesSearch =
      cert.studentName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      cert.courseName.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter =
      filterStatus === "all" || cert.status === filterStatus;
    return matchesSearch && matchesFilter;
  });

  if (isLoading) {
    return (
      <AdminLayout>
        <div className={styles.loading}>Loading certificates...</div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className={styles.certificatesPage}>
        <div className={styles.header}>
          <h1 className={styles.title}>Certificates Management</h1>
          <div className={styles.headerActions}>
            <button
              className="btn btn-primary"
              onClick={() => setShowGenerateModal(true)}
              disabled={students.length === 0}
            >
              <i className="ri-upload-line"></i>
              Upload Certificate
            </button>
          </div>
        </div>

        {/* Upload Message */}
        {uploadMessage && (
          <div className={`${styles.uploadMessage} ${uploadMessage.includes('Error') ? styles.error : styles.success}`}>
            {uploadMessage}
          </div>
        )}

        {/* Filters */}
        <div className={styles.filters}>
          <div className={styles.searchBox}>
            <input
              type="text"
              placeholder="Search certificates..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="input"
            />
          </div>
          <select
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
            className={styles.statusFilter}
          >
            <option value="all">All Status</option>
            <option value="issued">Issued</option>
            <option value="pending">Pending</option>
            <option value="revoked">Revoked</option>
          </select>
        </div>

        {/* Certificates List */}
        <div className={styles.certificatesList}>
          {filteredCertificates.length > 0 ? (
            filteredCertificates.map((cert) => (
              <div key={cert.id} className={`card ${styles.certificateCard}`}>
                <div className={styles.certificateInfo}>
                  <h3 className={styles.studentName}>{cert.studentName}</h3>
                  <p className={styles.courseInfo}>
                    <strong>Course:</strong> {cert.courseName}
                  </p>
                  <p className={styles.completionInfo}>
                    <strong>Completed:</strong>{" "}
                    {new Date(cert.completionDate).toLocaleDateString()}
                  </p>
                  {cert.generatedBy && (
                    <p className={styles.generatedInfo}>
                      <strong>Generated by:</strong> {cert.generatedBy}
                    </p>
                  )}
                  <span className={`${styles.status} ${styles[cert.status]}`}>
                    {cert.status.charAt(0).toUpperCase() + cert.status.slice(1)}
                  </span>
                </div>
                <div className={styles.actions}>
                  {cert.certificateUrl && (
                    <a
                      href={cert.certificateUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="btn btn-secondary btn-sm"
                    >
                      View
                    </a>
                  )}
                  {cert.certificateUrl && (
                    <a
                      href={cert.certificateUrl}
                      download
                      className="btn btn-primary btn-sm"
                    >
                      Download
                    </a>
                  )}
                  {cert.status === "issued" && (
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => handleRevokeCertificate(cert.id)}
                    >
                      Revoke
                    </button>
                  )}
                </div>
              </div>
            ))
          ) : (
            <div className={styles.noCertificates}>
              <p>No certificates found matching your criteria.</p>
            </div>
          )}
        </div>

        {/* Upload Progress Overlay */}
      {isUploading && uploadProgress > 0 && (
        <LoadingOverlay 
          text="Uploading Certificate" 
          progress={uploadProgress}
        />
      )}

      {/* Upload Certificate Modal */}
        {showGenerateModal && (
          <div className={styles.modal}>
            <div className={styles.modalContent}>
              <div className={styles.modalHeader}>
                <h2>Upload Certificate</h2>
                <button
                  onClick={() => {
                    setShowGenerateModal(false);
                    setSelectedStudent(null);
                    setCertificateFile(null);
                    setUploadMessage("");
                  }}
                  className={styles.closeBtn}
                  disabled={isUploading}
                >
                  Ã—
                </button>
              </div>

              <form onSubmit={handleUploadCertificate} className={styles.modalBody}>
                <div className={styles.formGroup}>
                  <label>Select Student</label>
                  <select
                    value={selectedStudent?.id || ""}
                    onChange={(e) => {
                      const student = students.find(
                        (s) => s.id === e.target.value
                      );
                      setSelectedStudent(student || null);
                    }}
                    className="input"
                    required
                  >
                    <option value="">Choose a student...</option>
                    {students.map((student) => (
                      <option key={student.id} value={student.id}>
                        {student.name} - {student.course} ({student.progress}%
                        complete)
                      </option>
                    ))}
                  </select>
                </div>

                <div className={styles.formGroup}>
                  <label>Certificate File</label>
                  <div 
                    className={`${styles.fileUploadArea} ${isDragOver ? styles.dragOver : ''}`}
                    onDragOver={(e) => {
                      e.preventDefault();
                      setIsDragOver(true);
                    }}
                    onDragLeave={(e) => {
                      e.preventDefault();
                      setIsDragOver(false);
                    }}
                    onDrop={(e) => {
                      e.preventDefault();
                      setIsDragOver(false);
                      
                      const files = e.dataTransfer.files;
                      if (files.length > 0) {
                        const file = files[0];
                        
                        // Validate file size (10MB max)
                        if (file.size > 10 * 1024 * 1024) {
                          setUploadMessage("Error: File size must be less than 10MB");
                          setTimeout(() => setUploadMessage(""), 5000);
                          return;
                        }
                        
                        // Validate file type
                        const allowedTypes = ['application/pdf', 'image/jpeg', 'image/jpg', 'image/png'];
                        if (!allowedTypes.includes(file.type)) {
                          setUploadMessage("Error: Please select a valid file type (PDF, JPG, JPEG, PNG)");
                          setTimeout(() => setUploadMessage(""), 5000);
                          return;
                        }
                        
                        setCertificateFile(file);
                      }
                    }}
                  >
                    <input
                      type="file"
                      accept=".pdf,.jpg,.jpeg,.png"
                      onChange={(e) => {
                        const file = e.target.files?.[0];
                        if (file) {
                          // Validate file size (10MB max)
                          if (file.size > 10 * 1024 * 1024) {
                            setUploadMessage("Error: File size must be less than 10MB");
                            setTimeout(() => setUploadMessage(""), 5000);
                            e.target.value = "";
                            return;
                          }
                          
                          // Validate file type
                          const allowedTypes = ['application/pdf', 'image/jpeg', 'image/jpg', 'image/png'];
                          if (!allowedTypes.includes(file.type)) {
                            setUploadMessage("Error: Please select a valid file type (PDF, JPG, JPEG, PNG)");
                            setTimeout(() => setUploadMessage(""), 5000);
                            e.target.value = "";
                            return;
                          }
                          
                          setCertificateFile(file);
                        } else {
                          setCertificateFile(null);
                        }
                      }}
                      required
                      className={styles.fileInput}
                      id="certificate-file"
                    />
                    <label htmlFor="certificate-file" className={styles.fileUploadLabel}>
                      <div className={styles.uploadIcon}>
                        <i className={certificateFile ? "ri-file-check-line" : "ri-upload-cloud-line"}></i>
                      </div>
                      <div className={styles.uploadText}>
                        <span className={styles.uploadTitle}>
                          {certificateFile ? certificateFile.name : "Choose certificate file"}
                        </span>
                        <span className={styles.uploadSubtitle}>
                          {isDragOver ? "Drop file here" : "or drag and drop here"}
                        </span>
                      </div>
                    </label>
                  </div>
                  <small className={styles.fileNote}>
                    Supported formats: PDF, JPG, JPEG, PNG (Max: 10MB)
                  </small>
                </div>

                {selectedStudent && (
                  <div className={styles.studentPreview}>
                    <h4>Student Information</h4>
                    <div className={styles.previewInfo}>
                      <p>
                        <strong>Student:</strong> {selectedStudent.name}
                      </p>
                      <p>
                        <strong>Email:</strong> {selectedStudent.email}
                      </p>
                      <p>
                        <strong>Course:</strong> {selectedStudent.course}
                      </p>
                      <p>
                        <strong>Progress:</strong> {selectedStudent.progress}%
                      </p>
                    </div>
                  </div>
                )}

                {certificateFile && (
                  <div className={styles.filePreview}>
                    <h4>Selected File</h4>
                    <div className={styles.fileInfo}>
                      <p>
                        <strong>File Name:</strong> {certificateFile.name}
                      </p>
                      <p>
                        <strong>File Size:</strong> {(certificateFile.size / 1024 / 1024).toFixed(2)} MB
                      </p>
                      <p>
                        <strong>File Type:</strong> {certificateFile.type}
                      </p>
                    </div>
                  </div>
                )}

                <div className={styles.formActions}>
                  <button
                    type="button"
                    onClick={() => {
                      setShowGenerateModal(false);
                      setSelectedStudent(null);
                      setCertificateFile(null);
                      setUploadMessage("");
                    }}
                    className="btn btn-secondary"
                    disabled={isUploading}
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={!selectedStudent || !certificateFile || isUploading}
                  >
                    {isUploading ? (
                      <LoadingSpinner 
                        size="small" 
                        color="white" 
                        text={uploadProgress > 0 ? `Uploading ${Math.round(uploadProgress)}%` : "Uploading..."}
                      />
                    ) : (
                      "Upload Certificate"
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}


      </div>
    </AdminLayout>
  );
}

src/components/ui/animated-components.ts.content:
"use client";

interface AnimatedSectionProps {
  children: React.ReactNode;
  className?: string;
  animationType?: "fade-up" | "fade-left" | "fade-right" | "scale";
}

export default function AnimatedSection({
  children,
  className = "",
  animationType = "fade-up",
}: AnimatedSectionProps) {
  return (
    <div className={className}>
      {children}
    </div>
  );
}

// Animated Card Component
export function AnimatedCard({
  children,
  className = "",
  interactive = false,
}: {
  children: React.ReactNode;
  className?: string;
  interactive?: boolean;
}) {
  const cardClasses = [
    "card",
    interactive ? "card-interactive" : "",
    "hover-lift",
    className,
  ]
    .filter(Boolean)
    .join(" ");

  return <div className={cardClasses}>{children}</div>;
}

src/components/navigation/FlowingMenu.cs.content:
.menu-wrap {
    width: 30vw;
    height: 100%;
    overflow: hidden;
    background: #D0B49F;
    border-radius: 25px 0 0 25px;
}

.menu {
    display: flex;
    flex-direction: column;
    height: 100%;
    width: 100%;
    margin: 0;
    padding: 0;
}

.menu__item {
    flex: 1;
    position: relative;
    overflow: hidden;
    text-align: center;
    width: 100%;
    background: rgba(255, 255, 255, 0.1);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.menu__item-link {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
    position: relative;
    cursor: pointer;
    text-transform: uppercase;
    text-decoration: none;
    white-space: nowrap;
    font-weight: 600;
    font-family: var(--font-heading);
    color: #000000;
    font-size: 4vh;
    letter-spacing: 0.05em;
    transition: all 0.3s ease;
}

.menu__item-link:hover {
    color: #ffffff;
    transform: scale(1.05);
}

.menu__item-link:focus:not(:focus-visible) {
    color: #000000;
    outline: none;
}

.marquee {
    position: absolute;
    top: 0;
    left: 0;
    overflow: hidden;
    width: 300%;
    height: 100%;
    pointer-events: none;
    background: #ffffff;
    transform: translate3d(0, 101%, 0);
    transition: transform 0.6s ease-expo;
    border-radius: 15px 15px 0 0;
}

.marquee__inner-wrap {
    height: 100%;
    width: 100%;
    display: flex;
    transform: translateX(0);
}

.marquee__inner {
    display: flex;
    align-items: center;
    position: relative;
    height: 100%;
    width: 600%;
    will-change: transform;
    animation: marquee 15s linear infinite;
}

.marquee span {
    color: #000000;
    white-space: nowrap;
    text-transform: uppercase;
    font-weight: 600;
    font-family: var(--font-subheading);
    font-size: 4vh;
    line-height: 1.2;
    padding: 1vh 1vw 0;
    letter-spacing: 0.02em;
}

.marquee__img {
    width: 200px;
    height: 7vh;
    margin: 2em 2vw;
    padding: 1em 0;
    border-radius: 15px;
    background-size: cover;
    background-position: 50% 50%;
}

.menu__item-link:hover+.marquee {
    transform: translate3d(0, 0%, 0);
}

.menu__item:hover {
    background: rgba(255, 255, 255, 0.15);
}

@keyframes marquee {
    from {
        transform: translateX(0);
    }

    to {
        transform: translateX(-50%);
    }
}

/* ==============================================
   RESPONSIVE DESIGN
   ============================================== */

/* Extra Large Desktop (â‰¥1440px) */
@media (min-width: 1440px) {
    .menu__item-link {
        font-size: 4.5vh;
        font-weight: 700;
    }

    .marquee span {
        font-size: 4.2vh;
        font-weight: 500;
        padding: 1.2vh 1.2vw 0;
    }

    .marquee__img {
        width: 220px;
        height: 8vh;
        margin: 2.2em 2.2vw;
    }
}

/* Large Desktop (1200px - 1439px) */
@media (min-width: 1200px) and (max-width: 1439px) {
    .menu__item-link {
        font-size: 4.2vh;
        font-weight: 650;
    }

    .marquee span {
        font-size: 3.8vh;
        font-weight: 450;
        padding: 1.1vh 1.1vw 0;
    }

    .marquee__img {
        width: 210px;
        height: 7.5vh;
        margin: 2.1em 2.1vw;
    }
}

/* Medium Desktop (992px - 1199px) */
@media (min-width: 992px) and (max-width: 1199px) {
    .menu__item-link {
        font-size: 3.8vh;
        font-weight: 600;
    }

    .marquee span {
        font-size: 3.5vh;
        font-weight: 400;
        padding: 1vh 1vw 0;
    }

    .marquee__img {
        width: 190px;
        height: 6.5vh;
        margin: 1.8em 1.8vw;
    }
}

/* Small Desktop / Large Tablet (769px - 991px) */
@media (min-width: 769px) and (max-width: 991px) {
    .menu-wrap {
        width: 35vw;
    }

    .menu__item-link {
        font-size: 3.5vh;
        font-weight: 580;
    }

    .marquee span {
        font-size: 3.2vh;
        font-weight: 380;
        padding: 0.9vh 0.9vw 0;
    }

    .marquee__img {
        width: 170px;
        height: 6vh;
        margin: 1.6em 1.6vw;
    }
}

/* Mobile Landscape (481px - 768px) */
@media (min-width: 481px) and (max-width: 768px) {
    .menu-wrap {
        width: 100vw;
    }

    .menu__item-link {
        font-size: 6vh;
        font-weight: 650;
        letter-spacing: 1px;
    }

    .marquee span {
        font-size: 5.5vh;
        font-weight: 450;
        padding: 1.5vh 2vw 0;
        letter-spacing: 0.5px;
    }

    .marquee__img {
        width: 150px;
        height: 8vh;
        margin: 2em 3vw;
        border-radius: 40px;
    }

    .marquee {
        transition: transform 0.5s ease;
    }
}

/* Mobile Portrait (320px - 480px) */
@media (min-width: 320px) and (max-width: 480px) {
    .menu-wrap {
        width: 100vw;
    }

    .menu__item-link {
        font-size: 5.5vh;
        font-weight: 600;
        letter-spacing: 0.8px;
    }

    .marquee span {
        font-size: 5vh;
        font-weight: 400;
        padding: 1.3vh 2.5vw 0;
        letter-spacing: 0.3px;
    }

    .marquee__img {
        width: 130px;
        height: 7vh;
        margin: 1.8em 4vw;
        border-radius: 35px;
    }

    .marquee {
        transition: transform 0.4s ease;
    }
}

/* Extra Small Mobile (â‰¤320px) */
@media (max-width: 320px) {
    .menu-wrap {
        width: 100vw;
    }

    .menu__item-link {
        font-size: 5vh;
        font-weight: 580;
        letter-spacing: 0.6px;
    }

    .marquee span {
        font-size: 4.5vh;
        font-weight: 380;
        padding: 1.2vh 3vw 0;
        letter-spacing: 0.2px;
    }

    .marquee__img {
        width: 110px;
        height: 6vh;
        margin: 1.6em 5vw;
        border-radius: 30px;
    }

    .marquee {
        transition: transform 0.3s ease;
    }
}

src/components/sections/Hero.tsx.content:
"use client";

import { useState, useRef, useEffect } from "react";
import Link from "next/link";
import { useSmoothScroll } from "../../hooks/useSmoothScroll";
import styles from "./Hero.module.css";

export default function Hero() {
  const [showOffer, setShowOffer] = useState(false);
  const [videoLoaded, setVideoLoaded] = useState(false);
  const videoRef = useRef<HTMLVideoElement>(null);
  const { scrollTo } = useSmoothScroll();

  useEffect(() => {
    const video = videoRef.current;
    if (video) {
      const handleLoadedData = () => {
        console.log("Video loaded successfully");
        setVideoLoaded(true);
      };

      const handleError = (e: Event) => {
        console.error("Video failed to load:", e);
        console.error("Video error details:", video.error);
      };

      const handleCanPlay = () => {
        console.log("Video can start playing");
        setVideoLoaded(true);
      };

      const handleLoadStart = () => {
        console.log("Video loading started");
      };

      video.addEventListener("loadeddata", handleLoadedData);
      video.addEventListener("error", handleError);
      video.addEventListener("canplay", handleCanPlay);
      video.addEventListener("loadstart", handleLoadStart);

      // If video is already loaded
      if (video.readyState >= 2) {
        console.log("Video already loaded, readyState:", video.readyState);
        setVideoLoaded(true);
      }

      // Try to load the video
      video.load();

      return () => {
        video.removeEventListener("loadeddata", handleLoadedData);
        video.removeEventListener("error", handleError);
        video.removeEventListener("canplay", handleCanPlay);
        video.removeEventListener("loadstart", handleLoadStart);
      };
    }
  }, []);

  const toggleOffer = () => {
    setShowOffer(!showOffer);
  };

  const scrollToServices = () => {
    scrollTo("#services", { duration: 1.5 });
  };

  return (
    <section className={styles.hero}>
      {/* Background Video */}
      <div className={styles.videoBackground}>
        <video
          ref={videoRef}
          autoPlay
          muted
          loop
          playsInline
          className={`${styles.video} ${videoLoaded ? styles.videoLoaded : ""}`}
          poster="/video-poster.jpg"
          preload="auto"
          controls={false}
        >
          <source src="/intro.mp4" type="video/mp4" />
          <source src="/intro.webm" type="video/webm" />
          Your browser does not support the video tag.
        </video>
        <div className={styles.videoOverlay}></div>
      </div>

      {/* Hero Content */}
      <div className={`container ${styles.heroContent}`}>
        <div className={styles.contentWrapper}>
          <h1 className={`${styles.heroTitle} font-heading`}>
            <span className="gradient-text-accent">Transform Your Look</span>
            <br />
            <span className="text-primary">Advance Your Career</span>
          </h1>

          <p className={`${styles.heroDescription} font-subheading`}>
            Experience premium salon services and professional beauty education
            at The Fringe. Where style meets expertise, and dreams become
            reality.
          </p>

          <div className={styles.ctaContainer}>
            <Link
              href="/book-now"
              className="btn btn-primary btn-lg font-accent"
            >
              Book Appointment
            </Link>
            <Link
              href="/courses"
              className="btn btn-secondary btn-lg font-accent"
            >
              Explore Courses
            </Link>
          </div>
        </div>
      </div>

      {/* Scroll Down Indicator - At very bottom */}
      <div className={styles.scrollIndicator} onClick={scrollToServices}>
        <div className={styles.scrollMouse}>
          <div className={styles.scrollWheel}></div>
        </div>
        <span className={`${styles.scrollText} font-accent`}>
          Scroll to explore our services
        </span>
      </div>

      {/* Special Offer Popup */}
      {showOffer && (
        <div className={styles.offerModal}>
          <div className={styles.offerContent}>
            <button
              className={styles.closeButton}
              onClick={toggleOffer}
              aria-label="Close offer"
            >
              Ã—
            </button>
            <h3 className={styles.offerTitle}>Special Offer!</h3>
            <p className={styles.offerDescription}>
              Get 20% off your first service or course enrollment. Use code:
              WELCOME20
            </p>
            <div className={styles.offerActions}>
              <Link href="/book-now" className="btn btn-primary">
                Book Now
              </Link>
              <Link href="/courses" className="btn btn-secondary">
                View Courses
              </Link>
            </div>
          </div>
        </div>
      )}

      {/* Offer Toggle Button */}
      <button
        className={styles.offerToggle}
        onClick={toggleOffer}
        aria-label="Toggle special offer"
      >
        ðŸŽ
      </button>
    </section>
  );
}

src/app/admin/services/Services.module.c.content:
.servicesPage {
    padding: var(--spacing-xl);
}

.loading {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 50vh;
    font-size: var(--font-size-lg);
    color: var(--color-text-secondary);
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--spacing-2xl);
}

.title {
    font-size: var(--font-size-3xl);
    font-weight: 700;
    color: var(--color-text-primary);
}

.servicesGrid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
    gap: var(--spacing-xl);
}

.serviceCard {
    padding: var(--spacing-xl);
    transition: all var(--transition-normal);
}

.serviceCard:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-lg);
}

.serviceHeader {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: var(--spacing-lg);
}

.serviceName {
    font-size: var(--font-size-xl);
    font-weight: 600;
    color: var(--color-text-primary);
    margin: 0;
}

.status {
    padding: var(--spacing-xs) var(--spacing-sm);
    border-radius: var(--radius-md);
    font-size: var(--font-size-sm);
    font-weight: 500;
    text-transform: uppercase;
}

.status.active {
    background: rgba(34, 197, 94, 0.2);
    color: #22c55e;
    border: 1px solid #22c55e;
}

.status.inactive {
    background: rgba(156, 163, 175, 0.2);
    color: #9ca3af;
    border: 1px solid #9ca3af;
}

.serviceDescription {
    color: var(--color-text-secondary);
    line-height: 1.6;
    margin-bottom: var(--spacing-lg);
}

.serviceDetails {
    margin-bottom: var(--spacing-lg);
}

.detailItem {
    margin-bottom: var(--spacing-sm);
    color: var(--color-text-secondary);
}

.serviceActions {
    display: flex;
    gap: var(--spacing-sm);
}

/* Modal styles (similar to previous modals) */
.modal {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
}

.modalContent {
    background: var(--color-card-bg);
    border-radius: var(--radius-lg);
    border: 1px solid var(--color-card-border);
    width: 100%;
    max-width: 500px;
    max-height: 90vh;
    overflow-y: auto;
}

.modalHeader {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: var(--spacing-xl);
    border-bottom: 1px solid var(--color-border);
}

.modalHeader h2 {
    font-size: var(--font-size-xl);
    font-weight: 600;
    color: var(--color-text-primary);
    margin: 0;
}

.closeBtn {
    background: none;
    border: none;
    font-size: var(--font-size-2xl);
    color: var(--color-text-secondary);
    cursor: pointer;
    padding: 0;
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
