}

.formGroup .input {
  width: 100%;
  padding: 12px 16px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 8px;
  background: rgba(255, 255, 255, 0.05);
  color: #ffffff;
  font-size: 1rem;
  transition: all 0.3s ease;
}

.formGroup .input:focus {
  outline: none;
  border-color: #D0B49F;
  background: rgba(255, 255, 255, 0.08);
  box-shadow: 0 0 0 3px rgba(208, 180, 159, 0.2);
