  width: 100%;
}

.formActions {
  display: flex;
  gap: 12px;
  margin-top: 20px;
}

.formActions .btn {
  padding: 10px 20px;
  border-radius: 8px;
  font-weight: 600;
  transition: all 0.3s ease;
}

.toggleGroup {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.toggleLabel {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-weight: 500;
  color: #ffffff;
  cursor: pointer;
  padding: 12px 0;
}

.toggle {
  position: relative;
  display: inline-block;
  width: 50px;
  height: 28px;
}

.toggle input {
  opacity: 0;
  width: 0;
  height: 0;
}

.toggleSlider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(255, 255, 255, 0.2);
  transition: 0.4s;
  border-radius: 28px;
}

.toggleSlider:before {
  position: absolute;
  content: "";
  height: 20px;
  width: 20px;
  left: 4px;
  bottom: 4px;
  background-color: white;
  transition: 0.4s;
  border-radius: 50%;
}

.toggle input:checked + .toggleSlider {
  background: linear-gradient(135deg, #D0B49F 0%, #B8A082 100%);
}

.toggle input:checked + .toggleSlider:before {
  transform: translateX(22px);
}

.loading,
.error {
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 400px;
  font-size: 1.1rem;
  color: rgba(255, 255, 255, 0.7);
}

.error {
