src/services/services.service.ts.content:
import { fetchApi } from '@/lib/api';
import { mockServices } from './mockData';

export interface Service {
  id: string;
  title: string;
  description: string;
  price: number;
  duration: string;
  image: string;
  features: string[];
  category: string;
}

export interface ServiceBooking {
  serviceId: string;
  userId: string;
  date: string;
  time: string;
  status: 'pending' | 'confirmed' | 'cancelled';
}

export const servicesService = {
  async getAllServices(): Promise<Service[]> {
    try {
      return await fetchApi<Service[]>('/services');
    } catch (error) {
      console.log('Falling back to mock data for services', error);
      return mockServices;
    }
  },

  async getServicesByCategory(): Promise<{[key: string]: Service[]}> {
    try {
      const services = await this.getAllServices();
      const servicesByCategory: {[key: string]: Service[]} = {};

      services.forEach(service => {
        if (!servicesByCategory[service.category]) {
          servicesByCategory[service.category] = [];
        }
        servicesByCategory[service.category].push(service);
      });

      return servicesByCategory;
    } catch (error) {
      console.log('Error grouping services by category', error);
      return {};
    }
  },

  async getFeaturedServicesByCategory(limit: number = 3): Promise<{[key: string]: Service[]}> {
    try {
      const servicesByCategory = await this.getServicesByCategory();
      const featuredServices: {[key: string]: Service[]} = {};

      Object.keys(servicesByCategory).forEach(category => {
        featuredServices[category] = servicesByCategory[category].slice(0, limit);
      });

      return featuredServices;
    } catch (error) {
      console.log('Error getting featured services by category', error);
      return {};
    }
  },

  async getServiceById(id: string): Promise<Service> {
    try {
      return await fetchApi<Service>(`/services/${id}`);
    } catch (error) {
      console.log('Falling back to mock data for service details', error);
      const service = mockServices.find(s => s.id === id);
      if (!service) throw new Error('Service not found');
      return service;
    }
  },

  async bookService(booking: Omit<ServiceBooking, 'status'>): Promise<ServiceBooking> {
    try {
      return await fetchApi<ServiceBooking>('/services/book', {
        method: 'POST',
        body: JSON.stringify(booking),
      });
    } catch (error) {
      console.log('Simulating booking creation with mock data', error);
      return {
        ...booking,
        status: 'pending'
      };
    }
  },

  async getMyBookings(userId: string): Promise<ServiceBooking[]> {
    try {
      return await fetchApi<ServiceBooking[]>(`/services/bookings/${userId}`);
    } catch (error) {
      console.log('Falling back to mock data for bookings', error);
      return []; // Return empty bookings for mock
    }
  },

  async cancelBooking(bookingId: string): Promise<void> {
    try {
      return await fetchApi<void>(`/services/bookings/${bookingId}/cancel`, {
        method: 'POST',
      });
    } catch (error) {
      console.log('Simulating booking cancellation', error);
    }
  },
};

src/app/services/Services.module.css.content:
.servicesPage {
    padding-top: 100px;
    min-height: 100vh;
    background: linear-gradient(135deg, #0f0f0f 0%, #1a1a1a 50%, #0f0f0f 100%);
    position: relative;
}

.servicesPage::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #000;
    pointer-events: none;
}

.container {
    max-width: 1400px;
    margin: 0 auto;
    padding: 0 24px;
    position: relative;
    z-index: 1;
}

.hero {
    text-align: center;
    margin-bottom: 80px;
    padding: 60px 0;
}

.pageTitle {
    font-size: clamp(2.5rem, 6vw, 4rem);
    font-weight: 800;
    color: #ffffff;
    margin-bottom: 24px;
    background: linear-gradient(135deg, #ffffff 0%, #D0B49F 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    line-height: 1.1;
}

.pageSubtitle {
    font-size: clamp(1.1rem, 3vw, 1.3rem);
    color: rgba(255, 255, 255, 0.8);
    max-width: 700px;
    margin: 0 auto;
    line-height: 1.5;
    font-weight: 400;
}

/* Filters / Search */
.filters {
    display: flex;
    gap: 24px;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 60px;
    flex-wrap: wrap;
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    padding: 24px;
    border-radius: 20px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    position: relative;
    z-index: 10;
}

.categoryFilters {
    display: flex;
    gap: var(--spacing-md);
    flex-wrap: wrap;
}

/* Right-aligned dropdown for filters */
.filterDropdown {
    margin-left: auto;
    display: flex;
    align-items: center;
    position: relative;
}

/* when dropdown is open rotate the caret and slightly elevate the trigger */
.filterDropdown.open .filterButton {
    background: rgba(208, 180, 159, 0.15);
    border-color: rgba(208, 180, 159, 0.5);
    box-shadow: 0 8px 24px rgba(208, 180, 159, 0.2);
}

.filterDropdown.open .filterButton::after {
    transform: rotate(180deg);
}

/* extra spacing so search and category buttons sit at opposite ends */
.filters {
    display: flex;
    gap: var(--spacing-md);
    justify-content: space-between;
    /* push items to ends */
    margin-bottom: var(--spacing-3xl);
    flex-wrap: wrap;
}

.filterSelect {
    background: var(--color-card-bg);
    color: var(--color-text-primary);
    border: 1px solid var(--color-border);
    padding: var(--spacing-sm) var(--spacing-md);
    border-radius: var(--radius-md);
    font-size: var(--font-size-sm);
}

/* Multi-select dropdown styles */
.filterButton {
    background: rgba(255, 255, 255, 0.08);
    color: #ffffff;
    border: 1px solid rgba(255, 255, 255, 0.2);
    padding: 16px 24px;
    border-radius: 16px;
    display: inline-flex;
    align-items: center;
    gap: 12px;
    cursor: pointer;
    font-weight: 600;
    font-size: 1rem;
    transition: all 0.3s ease;
    backdrop-filter: blur(10px);
    min-width: 180px;
    justify-content: space-between;
}

.filterButton:hover {
    background: rgba(255, 255, 255, 0.12);
    border-color: rgba(208, 180, 159, 0.5);
    transform: translateY(-2px);
    box-shadow: 0 8px 24px rgba(208, 180, 159, 0.2);
}

.filterButton::after {
    content: '';
    display: inline-block;
    width: 0;
    height: 0;
    border-left: 6px solid transparent;
    border-right: 6px solid transparent;
    border-top: 6px solid #ffffff;
    transform-origin: center;
    transition: transform 0.3s ease;
}

.filterButton:focus {
    outline: none;
    border-color: #D0B49F;
    box-shadow: 0 0 0 3px rgba(208, 180, 159, 0.3);
}

.dropdownMenu {
    position: absolute;
    top: calc(100% + 8px);
    right: 0;
    background: rgba(20, 20, 20, 0.98);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.15);
    border-radius: 12px;
    padding: 16px;
    z-index: 9999;
    min-width: 280px;
    max-width: 320px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);
    opacity: 0;
    visibility: hidden;
    transform: translateY(-10px);
    transition: all 0.2s ease;
}

.filterDropdown.open .dropdownMenu {
    opacity: 1;
    visibility: visible;
    transform: translateY(0);
}

.dropdownHeader {
    display: flex;
    gap: 8px;
    justify-content: space-between;
    margin-bottom: 12px;
    padding-bottom: 12px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.dropdownHeader button {
    padding: 6px 12px;
    border-radius: 8px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    background: rgba(255, 255, 255, 0.05);
    color: #ffffff;
    font-size: 0.85rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.2s ease;
    flex: 1;
}

.dropdownHeader button:hover {
    background: rgba(208, 180, 159, 0.2);
    border-color: rgba(208, 180, 159, 0.4);
}

.dropdownList {
    max-height: 200px;
    overflow-y: auto;
    list-style: none;
    padding: 0;
    margin: 0;
    scrollbar-width: thin;
}

.dropdownList::-webkit-scrollbar {
    width: 4px;
}

.dropdownList::-webkit-scrollbar-track {
    background: rgba(255, 255, 255, 0.05);
}

.dropdownList::-webkit-scrollbar-thumb {
    background: rgba(208, 180, 159, 0.3);
    border-radius: 2px;
}

.dropdownList li {
    padding: 0;
    margin-bottom: 2px;
}

.dropdownList label {
    display: flex;
    align-items: center;
    gap: 10px;
    cursor: pointer;
    color: rgba(255, 255, 255, 0.8);
    padding: 8px 12px;
    border-radius: 6px;
    transition: all 0.2s ease;
    font-weight: 500;
    font-size: 0.9rem;
}

.dropdownList label:hover {
    background: rgba(255, 255, 255, 0.08);
    color: #ffffff;
}

.dropdownList input[type="checkbox"] {
    width: 16px;
    height: 16px;
    appearance: none;
    border: 2px solid rgba(255, 255, 255, 0.3);
    border-radius: 3px;
    background: transparent;
    cursor: pointer;
    transition: all 0.2s ease;
    position: relative;
    flex-shrink: 0;
}

.dropdownList input[type="checkbox"]:checked {
    background: linear-gradient(135deg, #D0B49F 0%, #B8A082 100%);
    border-color: #D0B49F;
}

.dropdownList input[type="checkbox"]:checked::after {
    content: 'âœ“';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: #1a1a1a;
    font-size: 10px;
    font-weight: bold;
}

.dropdownLabelText {
    display: inline-block;
    flex: 1;
}

/* checked option visuals */
.dropdownList input[type="checkbox"]:checked+.dropdownLabelText {
    color: #D0B49F;
    font-weight: 600;
}

.dropdownList li {
    opacity: 1;
}



@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(10px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* larger tap targets on mobile */
@media (max-width: 480px) {
    .dropdownList label {
        padding: var(--spacing-sm) 0;
    }

    .dropdownMenu {
        min-width: 100%;
        padding: var(--spacing-md);
    }
}

/* Mobile: make dropdown full width under filters */
@media (max-width: 768px) {
    .filterDropdown {
        width: 100%;
        margin-left: 0;
        position: relative;
    }

    .dropdownMenu {
        position: absolute;
        top: calc(100% + 8px);
        left: 0;
        right: 0;
        width: 100%;
        margin-top: 0;
        border-radius: 12px;
        min-width: auto;
        max-width: none;
        z-index: 9999;
    }

    .filterDropdown:not(.open) .dropdownMenu {
        opacity: 0;
        visibility: hidden;
        transform: translateY(-10px);
    }

    .filterDropdown.open .dropdownMenu {
        opacity: 1;
        visibility: visible;
        transform: translateY(0);
    }
}

@keyframes slideDown {
    from {
        opacity: 0;
        transform: translateY(-10px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.categoryButton {
    padding: var(--spacing-sm) var(--spacing-lg);
    background-color: transparent;
    border: 1px solid var(--color-border);
    border-radius: var(--radius-md);
    color: var(--color-text-secondary);
    cursor: pointer;
    transition: var(--transition-normal);
    font-size: var(--font-size-sm);
}

.categoryButton:hover {
    transform: translateY(-2px);
    border-color: var(--color-border-hover);
    color: var(--color-text-primary);
}

.categoryButton.active {
    background-color: var(--color-text-primary);
    color: var(--color-primary-bg);
    border-color: var(--color-text-primary);
}

.searchContainer {
    flex: 1;
    max-width: 400px;
}

.searchInput {
    width: 100%;
    padding: 16px 20px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 16px;
    background: rgba(255, 255, 255, 0.08);
    color: #ffffff;
    font-size: 1rem;
    font-weight: 500;
    transition: all 0.3s ease;
    backdrop-filter: blur(10px);
}

.searchInput::placeholder {
    color: rgba(255, 255, 255, 0.5);
}

.searchInput:focus {
    outline: none;
    border-color: #D0B49F;
    background: rgba(255, 255, 255, 0.12);
    box-shadow: 0 0 0 3px rgba(208, 180, 159, 0.2);
    transform: translateY(-2px);
}

/* Grid */
.servicesGrid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(380px, 1fr));
    gap: 32px;
    margin-bottom: 80px;
}

/* Card */
.serviceCard {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    border-radius: 24px;
    overflow: hidden;
    transition: all 0.4s ease;
    position: relative;
    border: 3px solid rgb(255, 255, 255);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.2);
}

.serviceCard::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, rgba(208, 180, 159, 0.5), transparent);
}

.serviceCard:hover {
    transform: translateY(-12px) scale(1.02);
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.4);
}

.serviceImage {
    position: relative;
    width: 100%;
    height: 280px;
    overflow: hidden;
}

.serviceImage img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.6s ease;
}

.serviceOverlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(0, 0, 0, 0.7) 0%, rgba(0, 0, 0, 0.4) 100%);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
