    padding: 16px 32px;
    border-radius: 50px;
    font-weight: 700;
    font-size: 1.1rem;
    cursor: pointer;
    transition: all 0.3s ease;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.serviceOverlay button:hover {
    transform: translateY(-3px) scale(1.05);
    transition: all 0.3s ease;
}

.serviceCard:hover .serviceOverlay {
    opacity: 1;
}

.serviceCard:hover .serviceImage img {
    transform: scale(1.15);
}

.serviceContent {
    padding: 32px;
}

.serviceTitle {
    font-size: 1.5rem;
    font-weight: 700;
    color: #ffffff;
    margin-bottom: 12px;
    line-height: 1.3;
}

.serviceDescription {
    color: rgba(255, 255, 255, 0.7);
    margin-bottom: 24px;
    line-height: 1.6;
    font-size: 1rem;
}

.serviceDetails {
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    padding-top: 20px;
}

.serviceMeta {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.serviceFeatures ul li {
    list-style-type: none;
    font-size: 1rem;
    color: #e6e6e6;
    padding-left: 10px;
}

.duration {
    color: rgba(255, 255, 255, 0.6);
    font-size: 0.9rem;
    font-weight: 500;
    display: flex;
    align-items: center;
    gap: 6px;
}

.price {
    font-size: 1.3rem;
    font-weight: 700;
    color: #D0B49F;
    background: linear-gradient(135deg, #D0B49F 0%, #E6D3B7 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.contactPrice {
    font-size: 1rem;
    font-weight: 600;
    color: rgba(255, 255, 255, 0.8);
    font-style: italic;
}

.noResults {
    text-align: center;
    padding: var(--spacing-3xl);
    color: var(--color-text-secondary);
}

.ctaSection {
    text-align: center;
    padding: 60px 40px;
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    border-radius: 24px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    position: relative;
    overflow: hidden;
}

.ctaSection::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, rgba(208, 180, 159, 0.5), transparent);
}

.ctaTitle {
    font-size: clamp(2rem, 5vw, 3rem);
    font-weight: 800;
    color: #ffffff;
    margin-bottom: 16px;
    background: linear-gradient(135deg, #ffffff 0%, #D0B49F 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.ctaDescription {
    font-size: 1.2rem;
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 40px;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
}

.ctaButtons {
    display: flex;
    gap: 20px;
    justify-content: center;
    flex-wrap: wrap;
}

.ctaButtons a {
    padding: 18px 36px;
    border-radius: 50px;
    font-weight: 700;
    font-size: 1.1rem;
    text-decoration: none;
    transition: all 0.3s ease;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.ctaButtons a:first-child {
    background: linear-gradient(135deg, #D0B49F 0%, #B8A082 100%);
    color: #1a1a1a;
    box-shadow: 0 8px 24px rgba(208, 180, 159, 0.3);
}

.ctaButtons a:first-child:hover {
    transform: translateY(-3px) scale(1.05);
    box-shadow: 0 12px 32px rgba(208, 180, 159, 0.5);
}

.ctaButtons a:last-child {
    background: transparent;
    color: #ffffff;
    border: 2px solid rgba(255, 255, 255, 0.3);
}

.ctaButtons a:last-child:hover {
    background: rgba(255, 255, 255, 0.1);
    border-color: rgba(255, 255, 255, 0.5);
    transform: translateY(-3px);
}

/* Mobile Responsiveness */
@media (max-width: 1024px) {
    .servicesGrid {
        grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
        gap: 24px;
    }
}

@media (max-width: 768px) {
    .container {
        padding: 0 20px;
    }

    .hero {
        padding: 40px 0;
        margin-bottom: 60px;
    }

    .filters {
        flex-direction: column;
        gap: 20px;
        padding: 20px;
        align-items: stretch;
    }

    .searchContainer {
        max-width: 100%;
    }

    .filterDropdown {
        width: 100%;
    }

    .filterButton {
        width: 100%;
        justify-content: space-between;
    }

    .servicesGrid {
        grid-template-columns: 1fr;
        gap: 20px;
        margin-bottom: 60px;
    }

    .serviceCard {
        max-width: 100%;
    }

    .serviceImage {
        height: 240px;
    }

    .serviceContent {
        padding: 24px;
    }

    .ctaSection {
        padding: 40px 24px;
    }

    .ctaButtons {
        flex-direction: column;
        align-items: center;
        gap: 16px;
    }

    .ctaButtons a {
        width: 100%;
        max-width: 300px;
        text-align: center;
    }
}

@media (max-width: 480px) {
    .container {
        padding: 0 16px;
    }

    .hero {
        padding: 30px 0;
        margin-bottom: 40px;
    }

    .filters {
        padding: 16px;
        border-radius: 16px;
    }

    .searchInput {
        padding: 14px 18px;
        font-size: 0.95rem;
    }

    .filterButton {
        padding: 14px 20px;
        font-size: 0.95rem;
    }

    .servicesGrid {
        gap: 16px;
        margin-bottom: 40px;
    }

    .serviceImage {
        height: 200px;
    }

    .serviceContent {
        padding: 20px;
    }

    .serviceTitle {
        font-size: 1.3rem;
    }

    .serviceDescription {
        font-size: 0.95rem;
    }

    .ctaSection {
        padding: 32px 20px;
        border-radius: 20px;
    }
}

common/types.ts.content:
// Shared types used across all applications

export interface User {
  id: string;
  email: string;
  name: string;
  role: UserRole;
  createdAt: Date;
  updatedAt: Date;
}

export interface Student {
  id: string;
  email: string;
  name: string;
  phone?: string;
  enrollmentDate: Date;
  status: StudentStatus;
  assignedCourseId?: string;
  certificateUrl?: string;
}

export interface Course {
  id: string;
  title: string;
  description: string;
  category: CourseType;
  isPublic: boolean;
  duration: string;
  price?: number;
  thumbnail?: string;
  status: CourseStatus;
  createdAt: Date;
  updatedAt: Date;
}

export interface Service {
  id: string;
  title: string;
  description: string;
  category: ServiceCategory;
  price?: number;
  duration?: string;
  image?: string;
  isActive: boolean;
  createdAt: Date;
  updatedAt: Date;
}

// Enums
export enum UserRole {
  ADMIN = 'ADMIN',
  SUPER_ADMIN = 'SUPER_ADMIN',
  STUDENT = 'STUDENT'
}

export enum StudentStatus {
  ACTIVE = 'ACTIVE',
  INACTIVE = 'INACTIVE',
  GRADUATED = 'GRADUATED',
  SUSPENDED = 'SUSPENDED'
}

export enum CourseType {
  PUBLIC = 'PUBLIC',
  ACADEMY = 'ACADEMY',
  PROFESSIONAL = 'PROFESSIONAL',
  MASTERCLASS = 'MASTERCLASS'
}

export enum CourseStatus {
  ACTIVE = 'ACTIVE',
  INACTIVE = 'INACTIVE',
  DRAFT = 'DRAFT',
  ARCHIVED = 'ARCHIVED'
}

export enum ServiceCategory {
  HAIR_STYLING = 'HAIR_STYLING',
  HAIR_COLORING = 'HAIR_COLORING',
  MAKEUP = 'MAKEUP',
  NAIL_ART = 'NAIL_ART',
  SKINCARE = 'SKINCARE',
  SPECIAL_OCCASION = 'SPECIAL_OCCASION'
}

// Authentication
export interface LoginRequest {
  email: string;
  password: string;
}

export interface LoginResponse {
  success: boolean;
  token?: string;
  user?: {
    email: string;
    role: UserRole;
  };
  error?: string;
}

