src/app/api/admin/certificates/upload/ro.content:
import { NextRequest, NextResponse } from 'next/server';
import { writeFile, mkdir } from 'fs/promises';
import { join } from 'path';
import { existsSync } from 'fs';

export async function POST(request: NextRequest) {
  try {
    const formData = await request.formData();
    const file = formData.get('certificate') as File;
    const studentId = formData.get('studentId') as string;
    const studentName = formData.get('studentName') as string;
    const courseName = formData.get('courseName') as string;

    if (!file || !studentId || !studentName || !courseName) {
      return NextResponse.json(
        { error: 'Missing required fields' },
        { status: 400 }
      );
    }

    // Validate file type
    const allowedTypes = ['application/pdf', 'image/jpeg', 'image/jpg', 'image/png'];
    if (!allowedTypes.includes(file.type)) {
      return NextResponse.json(
        { error: 'Invalid file type. Only PDF, JPG, JPEG, PNG are allowed.' },
        { status: 400 }
      );
    }

    // Validate file size (10MB max)
    if (file.size > 10 * 1024 * 1024) {
      return NextResponse.json(
        { error: 'File size too large. Maximum 10MB allowed.' },
        { status: 400 }
      );
    }

    // Create uploads directory if it doesn't exist
    const uploadsDir = join(process.cwd(), 'public', 'uploads', 'certificates');
    if (!existsSync(uploadsDir)) {
      await mkdir(uploadsDir, { recursive: true });
    }

    // Generate unique filename
    const timestamp = Date.now();
    const fileExtension = file.name.split('.').pop();
    const fileName = `${studentId}_${timestamp}.${fileExtension}`;
    const filePath = join(uploadsDir, fileName);

    // Convert file to buffer and save
    const bytes = await file.arrayBuffer();
    const buffer = Buffer.from(bytes);
    await writeFile(filePath, buffer);

    // Create certificate record (in a real app, this would go to a database)
    const certificateRecord = {
      id: `cert_${timestamp}`,
      studentId,
      studentName,
      studentEmail: `${studentName.toLowerCase().replace(' ', '.')}@example.com`, // Mock email
      courseName,
      completionDate: new Date().toISOString(),
      status: 'issued' as const,
      certificateUrl: `/uploads/certificates/${fileName}`,
      generatedBy: 'Admin User', // In real app, get from session
      generatedAt: new Date().toISOString(),
    };

    // In a real application, you would save this to your database
    // For now, we'll just return success
    console.log('Certificate uploaded:', certificateRecord);

    return NextResponse.json({
      success: true,
      message: 'Certificate uploaded successfully',
      certificate: certificateRecord,
    });

  } catch (error) {
    console.error('Error uploading certificate:', error);
    return NextResponse.json(
      { error: 'Failed to upload certificate' },
      { status: 500 }
    );
  }
}

src/components/ui/VideoPlayer.module.css.content:
.videoPlayer {
    width: 100%;
    max-width: 800px;
    margin: 0 auto;
}

.videoContainer {
    position: relative;
    background-color: var(--color-primary-bg);
    border-radius: var(--radius-lg);
    overflow: hidden;
    border: 1px solid var(--color-border);
}

.video {
    width: 100%;
    height: auto;
    min-height: 400px;
    display: block;
    cursor: pointer;
}

.controls {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    background: linear-gradient(transparent, rgba(0, 0, 0, 0.8));
    padding: var(--spacing-lg);
    display: flex;
    align-items: center;
    gap: var(--spacing-md);
    opacity: 0;
    transition: opacity var(--transition-normal);
}

.videoContainer:hover .controls {
    opacity: 1;
}

.playButton {
    background: none;
    border: none;
    color: var(--color-text-primary);
    font-size: var(--font-size-xl);
    cursor: pointer;
    padding: var(--spacing-sm);
    border-radius: var(--radius-md);
    transition: all var(--transition-normal);
    flex-shrink: 0;
}

.playButton:hover {
    background-color: rgba(255, 255, 255, 0.1);
    transform: scale(1.1);
}

.progressContainer {
    flex: 1;
    margin: 0 var(--spacing-md);
}

.progressSlider {
    width: 100%;
    height: 6px;
    background: rgba(255, 255, 255, 0.3);
    border-radius: 3px;
    outline: none;
    cursor: pointer;
    appearance: none;
}

.progressSlider::-webkit-slider-thumb {
    appearance: none;
    width: 16px;
    height: 16px;
    background: var(--color-text-primary);
    border-radius: 50%;
    cursor: pointer;
    transition: transform var(--transition-normal);
}

.progressSlider::-webkit-slider-thumb:hover {
    transform: scale(1.2);
}

.progressSlider::-moz-range-thumb {
    width: 16px;
    height: 16px;
    background: var(--color-text-primary);
    border-radius: 50%;
    border: none;
    cursor: pointer;
    transition: transform var(--transition-normal);
}

.progressSlider::-moz-range-thumb:hover {
    transform: scale(1.2);
}

.timeDisplay {
    color: var(--color-text-primary);
    font-size: var(--font-size-sm);
    font-family: monospace;
    display: flex;
    align-items: center;
    gap: var(--spacing-xs);
    flex-shrink: 0;
}

/* Mobile Responsive */
@media (max-width: 768px) {
    .video {
        min-height: 250px;
    }

    .controls {
        padding: var(--spacing-md);
        gap: var(--spacing-sm);
    }

    .playButton {
        font-size: var(--font-size-lg);
    }

    .timeDisplay {
        font-size: var(--font-size-xs);
    }
}

@media (max-width: 480px) {
    .video {
        min-height: 200px;
    }

    .controls {
        opacity: 1;
